INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(140, '7-дней-6-ночей', '7-дней-6-ночей', '7-дней-6-ночей', '17', NULL, '7 дней / 6 ночей', '7 дней / 6 ночей', '7 дней / 6 ночей', '', '', '', '', '<p>The first gaming day of the 2017/18 season of the Champions League in the Tbilisi Olympic Palace - 29 October - started the first league of silver league and Super League.</p>\r\n\r\n<p>The team of Caucasus International University met with Imedi TV team in the Super League I round. With the merit of the goalkeepers of these teams, the game was quite spectacular. At the end of the meeting, Davit Parajanishvili, the goalkeeper of the KSU team was named as the best player.<br />\r\nWe would like to remind you that the CSU team will play in the Champions League league games. The coach of the team is Ilia Modebadze.</p>\r\n\r\n<p>The closest match of the business league is scheduled with Ivane Javakhishvili Tbilisi State University in December.</p>\r\n', '<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 1. Прибытие &ndash; Тбилиси&nbsp;&nbsp; </span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 2. Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 3. Тбилиси -&nbsp; Мцхета &ndash; Уплисцихе -&nbsp; Тбилиси&nbsp; </span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 4. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 5. Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 6. Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 7. Отъезд &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Детальная программа тура:</span></span></b><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"><img alt=\"\" src=\"/uploads/images/turebi/7%20day%206%20night/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C%20pat.jpg\" style=\"float: right; width: 300px; height: 189px; margin: 12px;\" /></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 1. Прибытие &ndash; Тбилиси&nbsp;&nbsp; </span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Встреча гостей в аэропорту и трансфер в отель выбранной категории.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Размещение в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Ночевка в Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 2. Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Экскурсия в Кахетию .</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Продолжительность тура : 8 &ndash; 9 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Кахетия</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают </span></span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Кр</span></span></span><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"><img alt=\"\" src=\"/uploads/images/turebi/7%20day%206%20night/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8%20pat.jpg\" style=\"width: 300px; height: 189px; float: right; margin: 12px;\" /></span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">епость Господина ), где расположен дворец царя Ираклия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">, насладиться зеленью речных долин и склонами гор Большого Кавказа.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье п</span></span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">оэта, общественного деятеля и князя Кахетии </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова.&nbsp; У Вас будет возможность посетить дом &ndash; </span></span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">музей князя и&nbsp; прогуляться по саду грузинской аристократии </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в., которая имела утонченный европейский вкус.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"><span style=\"color:red\">( дегустация вин )</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ.<b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">&nbsp;День 3.Тбилиси -&nbsp; Мцхета &ndash; Уплисцихе -&nbsp; Тбилиси&nbsp;</span></span></span></b></span><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"><img alt=\"\" src=\"/uploads/images/turebi/%E2%80%93%20%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" /></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">&nbsp;Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">&nbsp;Экскурсия в город Мцхета и в пещерный город Уплисцихе.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">&nbsp;Продолжительность тура : 6 &ndash; 7 ч.</span></span></span></b></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Еще один выдающийся памятник&nbsp; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Затем направитесь в пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 4. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i>НА ПУТИ К ВЕЛИКАНУ</i></b>&raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"><img alt=\"\" src=\"/uploads/images/turebi/7%20day%206%20night/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%20pat.jpg\" style=\"float: left; width: 300px; height: 189px; margin: 12px;\" />&nbsp; Продолжительность тура : 8 &ndash; 9 ч.</span></span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">&nbsp;Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ).&nbsp; &nbsp;Здесь&nbsp;проходит главный Кавказский хребет. Впереди Вас ждет живописная&nbsp; &nbsp;природа и ландшафт.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">&nbsp;Военно &ndash; грузинская дорога</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> &ndash; это одна из самых красивых дорог на Кавказе,&nbsp; &nbsp;соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже&nbsp; &nbsp;очень давно ставший популярным у путешественников и туристов. По этой&nbsp; &nbsp;дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот! </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной <img alt=\"\" src=\"/uploads/images/turebi/7%20day%206%20night/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B8%20pat.jpg\" style=\"width: 300px; height: 189px; float: right; margin: 12px;\" />достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери,&nbsp; 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XIV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i>.</span></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 5. Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Завтрак в отеле. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Пешая экскурсия по Тбилиси. &laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Продолжительность тура : 3 &ndash; 5 ч<i>.</i></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь,</span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span cambria=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000- летию рождества Христова и 1500-летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 6. Тбилиси&nbsp;&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Свободный день.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">День 7. Отъезд &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\">Трансфер в аэропорт</span></span></span></span></p>\r\n', '/uploads/images/turebi/111.jpg', '/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C1.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8.png,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpeg,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0.jpg,/uploads/images/turebi/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B8.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B82.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B8.jpg,/uploads/images/turebi/Uplistsikhe1.jpg,/uploads/images/turebi/upliscixe.jpg,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0223.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C.jpg', '-5%', 1, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(141, '4-დღე-3-ღამე', '4-days-3-nights', '4-дня-3-ночи', '20', NULL, '4 დღე  / 3 ღამე', '4 days / 3 nights', '4 дня / 3 ночи', '', '', '', '<p>გთავაზობთ ერთდღიან დაუვიწყარ ტურს მცხეთაში.ტურის ფარგლებში თქვენ შეძლებთ ნახოთ:</p>\r\n\r\n<p>1.ჯვრის მონასტერი.</p>\r\n\r\n<p>2.სვეტიცხოველი.</p>\r\n\r\n<p>3.სამთავროს დედათა მონასტერი.</p>\r\n\r\n<p>4.შიომღვიმის მონასტერი.</p>\r\n\r\n<p>5.შთამბეჭდავი დღის ბოლოს ვახშამი მცხეთის სალობიეში.</p>\r\n', '<p>Offers a one-day unforgettable tour in Mtskheta. Within the tour you will be able to see:</p>\r\n\r\n<p>1. Zvari Monastery.</p>\r\n\r\n<p>2.The Svetitskhoveli.</p>\r\n\r\n<p>3.Master Monastery of the Ministry.</p>\r\n\r\n<p>4.The cemetery monastery.</p>\r\n\r\n<p>At the end of the impressive day at dinner in Mtskheta Salbia.</p>\r\n', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси &ndash; Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Сигнахи &ndash; Бодбийский монастырь &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4</b>. Отъезд &ndash; Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси.<span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span>XIIIвек ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.<img alt=\"\" src=\"/uploads/images/turebi/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 122px; float: right;\" /></p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b>XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000- летию рождества Христова и 1500-летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси &ndash; Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в бывшую столицу Грузии &ndash; Мцхета.<b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8F%20%D0%94%D0%B6%D0%B2%D0%B0%D1%80%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 122px; float: left;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Сигнахи &ndash; Бодбийский монастырь &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ОБИТЕЛЬ ПРЕКРАСНОГО</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 5 &ndash; 7 ч.</b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Маршрут</span></span></b><b>:<i>Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</i></b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец <img alt=\"\" src=\"/uploads/images/turebi/signagi45r3.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ.</p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Отъезд &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%20pat2.jpg', '/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8.png,/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B82.jpg,/uploads/images/mts1.jpg,/uploads/images/mts.jpg,/uploads/images/turebi/bodbis%20monasteri2.jpg,/uploads/images/turebi/mcxeta6556.jpg,/uploads/images/turebi/tbilisi654433.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C%201.jpg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8.jpg,/uploads/images/turebi/signagi4325.jpg,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%2016.jpg', '', 1, 1, 1, 1),
(142, '5-дней-4-ночи-1', '5-дней-4-ночи-1', '5-дней-4-ночи-1', '14', NULL, '5 дней / 4 ночи', '5 дней / 4 ночи', '5 дней / 4 ночи', '', '', '', '<p><strong>ტურის ფარგლებში ნახავთ:</strong></p>\r\n\r\n<p>✔ჟინვალის წყალსაცავი​</p>\r\n\r\n<p>✔ანანურის სამონასტრო კომპლექსი</p>\r\n\r\n<p>✔​გუდაურის გადმოსახედი​​​</p>\r\n\r\n<p>✔ვეძა: მჟავე წყლები​</p>\r\n\r\n<p>✔სოფელი სნო, ​​სნოს ციხე</p>\r\n\r\n<p>✔ გერგეთის სამება</p>\r\n\r\n<p>✔ დარიალის ხეობა</p>\r\n', '<p><strong>Within the tour you will find:</strong></p>\r\n\r\n<p>✔ Water Reservoir</p>\r\n\r\n<p>✔anuri monastery complex</p>\r\n\r\n<p>✔ Gudauri&#39;s perspective</p>\r\n\r\n<p>✔Very: Sour Waters</p>\r\n\r\n<p>✔Soopo Sno, Sno Fortress</p>\r\n\r\n<p>✔ Gergeti Trinity</p>\r\n\r\n<p>✔ Darial Valley</p>\r\n', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 1.&nbsp;Прибытие &ndash; Тбилиси ( ужин в национальном ресторане с шоу &ndash; программой )</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 2. Тбилиси &ndash; Мцхета - Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 3.&nbsp;Тбилиси &ndash; крепость Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси </span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 4.&nbsp;Тбилиси &ndash; Болниси - Дманиси &ndash; Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 5.&nbsp;Отъезд &ndash; Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 1. Прибытие &ndash; Тбилиси </span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Встреча гостей&nbsp; / трансфер в отель выбранной категории.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Размещение в отеле / апартаментах.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ужин в национальном ресторане.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночевка в Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 2. Тбилиси / Мцхета &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле / апартаментах.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i>&laquo; По двум столицам &raquo;</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь,</span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.&nbsp;<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%90%D0%BD%D1%87%D0%B8%D1%81%D1%85%D0%B0%D1%82%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 240px; float: right;\" /></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"><span style=\"color:#002060\">( Дегустация вин / чачи / обед за дополнительную плату )</span></span></span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 3. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле / апартаментах.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i>НА ПУТИ К ВЕЛИКАНУ</i></b>&raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Военно &ndash; грузинская дорога</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот! </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"><img alt=\"\" src=\"/uploads/images/turebi/ananuri%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице включен в стоимость тура )</i>.</span></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 4. Тбилиси -<i> </i>Дманиси &ndash; Болниси &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 5 - 7 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Невозможно представить&nbsp; Средневековую Грузию без города &ndash; крепости<b> Дманиси</b>. Здесь в те далекие времена проходила бойкая торговля самым разнообразным товаром. Здесь проживало много ремесленников, которые на заказ могли изготовить красивые кувшины или сшить прочные сапоги из кожи. Здесь можно было легко обменять один товар на другой. Дманиси был перевалочным пунктом на Великом Шелковом Пути.Но не только торговлей занимался Дманиси. Эта была еще и очень хорошо укрепленная крепость, которая помогала защищать остальную территорию Грузии. Общая территория крепости когда &ndash; то составляла 25 Га. Во время раскопок на территории города &ndash; крепости Дманиси нашли очень интересные артефакты и человеческие кости, которые теперь ставят под вопрос саму теорию эволюции человочества. Именно здесь 1,85 млн. лет назад человек прямоходящий ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Homoerectus</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Homogeorgicus</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> ) уже умел создавать характерные для него инструменты. &laquo; Первых европейцев &raquo; назвали <b>Зезва и Мзия</b>. Их восстановленные образы и археологические находки Вы сможете увидеть в музее, который находится в археологической зоне дманисского городища. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">На территории городища есть два интересных религиозно &ndash; исторических памятника: остатки крепостных сооружений и храм <b>Дманисский Сион </b>( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">С нашим гидом Вы посетите древнюю базилику <b>Болнисский Сион</b> ( 478 &ndash; 493 гг. ), которая до наших дней дошла практически без изменений. Болнисский Сион особенно интересен надписями </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> века на стенах храма. Эти&nbsp; надписи являются древнейшими образцами грузинской письменности.&nbsp;<img alt=\"\" src=\"/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B823.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" /></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Стены базилики хранят тысячи молитв, и храм до сих пор продолжает служить людям. Посещение таких мест обогащает людей не только новыми знаниями и впечатлениями, но и дарует духовную пищу.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Пьянящий воздух, разлитый вокруг; дикая природа, нетронутая цивилизацией, заворожит и превратит мир , в котором все равны. Ведь мы &ndash; дети природы, ее неотъемлемая часть... </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Возвращение в Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночевка в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 5.Отъезд &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле / апартаментах.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Трансфер в аэропорт. </span></span></span></span></p>\r\n', '/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%20pat.jpg', '/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B845.jpg,/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B8.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B821.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B8%20242.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%BB%D0%BD%D0%B8%D1%81%D0%B8%20%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B8%2021.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%BB%D0%BD%D0%B8%D1%81%D0%B8.jpg,/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B823.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%20%203.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20324.jpg,/uploads/images/turebi/tbilisi456543.jpg,/uploads/images/turebi/tbilisi321.jpg,/uploads/images/turebi/tbilisi56543.jpg,/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B83.jpg', '', 0, 0, 0, 1),
(143, 'ტური-თუშეთში', 'Tour-in-Tusheti', 'Тур-в-Тушети', '11', NULL, 'ტური თუშეთში', 'Tour in Tusheti', 'Тур в Тушети', '', '', '', '<p>მსურველების რაოდენობა: 7-14-21... პერსონა<br />\r\n​ტურის ხანგრძლივობა: 3 დღე - 2 ღამე.&nbsp;<br />\r\n&nbsp;<br />\r\nშეკრების ადგილი:</p>\r\n\r\n<p>თბილისი: რუსთაველის მოედანი ( ველოსიპედის წინ ) &nbsp;6:00 საათი</p>\r\n\r\n<p>მარშრუტი:</p>\r\n\r\n<p>1)დღე</p>\r\n\r\n<p>ალავერდის ტაძრი<br />\r\nაბანოს უღელტეხილი (ზღვის დონიდან 2950 მ.)<br />\r\nომალო (საოჯახო სასტუმროში დასვენება)<br />\r\nშენაქო (ზღვის დონიდან 2008 მ.)<br />\r\nდიკლო<br />\r\nომალო (დაბრუნება სასტუმროში)<br />\r\n2)დღე</p>\r\n\r\n<p>ძველი ომალო (კესელოს კოშკები)<br />\r\nდართლო (თუშური ტრადიციული არქიტექტურის საუკეთესო ნიმუში)<br />\r\nკვავლო<br />\r\nფარსმა<br />\r\nგირევი<br />\r\nომალო (დაბრუნება სასტუმროში)<br />\r\n3)დღე</p>\r\n\r\n<p>ბოჭორნა (ევროპაში ყველაზე მაღლა მდებარე დასახლებული პუნქტი. 2345 მ. )<br />\r\n​​​დოჭუ (გადმოცემის თანახმად,სწორედ ამ სოფელში ამბავდნენ ბავშვებს ჯაჭვით, რათა კლდიდან არ გადაჩეხილიყვნენ)<br />\r\nჯვარბოსელი<br />\r\nგამომგზავრება თბილისის მიმართულებით</p>\r\n', '<p>Number of applicants: 7-14-21 ... Person<br />\r\nDuration of the tour: 3 days - 2 nights.<br />\r\n&nbsp;<br />\r\nPlace of gathering:</p>\r\n\r\n<p>Tbilisi: Rustaveli square (before bicycle) 6:00 am</p>\r\n\r\n<p>Route:</p>\r\n\r\n<p>1 day</p>\r\n\r\n<p>Alaverdi Temple<br />\r\nBath Pass (2950 m above sea level)<br />\r\nOmalo (Family Guest)<br />\r\nShenako (sea level 2008 m.)<br />\r\nDicklo<br />\r\nOmalo (back to hotel)<br />\r\n2) day</p>\r\n\r\n<p>Old Omalo (Keselo&#39;s towers)<br />\r\nDartlo (the best example of Tushetian architecture)<br />\r\nKvavlo<br />\r\nFarsma<br />\r\nGrave<br />\r\nOmalo (back to hotel)<br />\r\n3) Day</p>\r\n\r\n<p>Bochorana (the highest point in Europe, 2345 m)<br />\r\nDochu (according to the legend that in this village the children were told to chain so that they could not get out of the rock)<br />\r\nThe graviton<br />\r\nDeparture to Tbilisi</p>\r\n', '<p><textarea autocapitalize=\"off\" autocomplete=\"off\" autocorrect=\"off\" dir=\"ltr\" disabled=\"disabled\" id=\"source-is\" name=\"text-is\" spellcheck=\"false\" wrap=\"SOFT\"></textarea></p>\r\n', '/uploads/images/TUSH2P.png', '/uploads/images/TUSH3.png,/uploads/images/TUSH.jpg,/uploads/images/TUSH1.jpg', '2017-11-14 16:38:00', 0, 1, 1, 0);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(144, 'ტური-სამეგრელოში', 'Tour-in-Samegrelo', 'Двухдневный-тур-в-Самегрело', '12', NULL, 'ტური სამეგრელოში', 'Tour in Samegrelo ', 'Двухдневный тур в Самегрело ', '', '', '', '<p><strong>ორ დღიანი ტური&nbsp;სამეგრელოში...გთავაზობთ სანახაობათა ჩამონათვალს:</strong></p>\r\n\r\n<p>1) დადიანების სასახლე:&nbsp;მუზეუმში გამოფენილია ამ მხარის არქეოლოგიური (&nbsp;პალეოლითის, ნეოლითისა და ბრინჯაოს ხანის ნივთები) და ეთნოგრაფიული (სასოფლო-სამეურნეო იარაღი, საყოფაცხოვრებო ნივთები,&nbsp;ტანსაცმელი, საცხოვრებელ ნაგებობათა - &quot;ფაცხა&quot;, &quot;ჯარგვალი&quot;, &quot;ოდა&quot; - და სხვა ნიმუშები) მასალა. წარმოდგენილია ფეოდალური ხანის&nbsp;საქართველოს&nbsp;ისტორიის ამსახველი ექსპონანტები. სამეგრელოს უკანასკნელი მთავრის&nbsp;დავით დადიანის&nbsp;და მისი ოჯახის წევრთა პირადი ნივთები.</p>\r\n\r\n<p>2) მარტვილის კანიონი:&nbsp;<strong>ღირსშესანიშნაობა:</strong>&nbsp;ბუნების ძეგლი წარმოადგენს მდინარე აბაშის მიერ აბდათის კირქვული ქანებით აგებულ ანტიკლინში, კანიონისებურად გაჭრილ ხეობას. კანიონის სიგრძე 2400 მ-ია, ჩაჭრის სიღრმე 20-30 მ, ხოლო სიგანე 5-10 მ.<br />\r\nკანიონში ორ ადგილას არის შენახული კირქვის ბუნებრივი ხიდები, რაც იმის მანიშნებელია, რომ აქ ოდესღაც კარსტული მღვიმე ჩამოიშალა. გარდა ამისა, კანიონის შუა წელში წარმოიშობა 12-15 მ სიმაღლის ვარდნის მცირე ჩანჩქერები. ერთი მონაკვეთი გამოირჩევა განსაკუთრებული სილამაზით. ეს არის ოფუცხოლე, რომელიც ცნობილია &ldquo;დადიანების საბანაოს&rdquo; სახელით. აქ ჩასასვლეად ბილიკი კირქვის დიდი კვადრებისგან აშენებული კიბეს ჩაუყვება. გადმოცემის თანახმად, სწორედ ამ კიბით დადიოდნენ გიორგი ჭყონდიდელი და დავით აღმაშენებელი.<br />\r\nმიმდებარე კირქვოვან კლდეებზე განვითარებულია კოლხური ტყისთვის დამახასიათებელი ფლორა, თავისი უნიკალური წარმომადგენლებით, აქ გვხვდება: დათვის თხილი, კოლხური ბზა, უთხოვარი და სხვა. მრავლადაა სხვადასხვა სახეობის ხავსი, გვიმრანაირები.</p>\r\n\r\n<p>3) ოკაცეს კანიონი: კანიონი მდებარეობს მდინარე&nbsp;&nbsp;ოკაცეს ხეობაში. მდინარის სიგრძე 14 კილომეტრია, ხოლო კანიონის სიგანე ზოგან 3&ndash;6 მეტრი, ზოგან 15&ndash;20 მეტრია, სიღრმე კი 20-დან 100 მეტრამდე მერყეობს. კანიონს ამშვენებს რამდენიმე წყალუხვი ჩანჩქერი. ერთ-ერთი ტბის ,ოსხაპოს&rdquo; სიგრძე 60 მეტრია.&nbsp;&nbsp;მდინარე&nbsp;ოკაცეზე რამდენიმე ბუნებრივი ქვახიდია გადებული, ერთი მათგანია ,ბოგა&rdquo;. კანიონს გააჩნია მღვიმეები, საიდანაც გამოედინება კარსტული წყლები.</p>\r\n\r\n<p>ტური უნდა შედგებოდეს 7 ან 8 ადამიანისგან.. ან ფასის შეთანხმებით რაოდენობას მნიშვნელიბა არ აქვს..</p>\r\n\r\n<p>ტური განხორციელდება თქვენი ან ჩვენს მიერ დაგეგმილ დღეს.</p>\r\n', '<p>Two days tour in Samegrelo ... offers a list of sights:</p>\r\n\r\n<p>1) Dadiani Palace: The museum displays archaeological (items of Paleolithic, Neolith and Bronze Age) and ethnographic (agricultural weapons, household items, clothes, housing, &quot;jackal&quot;, &quot;oda&quot; and other samples ) Material. Exhibitors depicting Georgian history of feudal age are presented. Personal belongings of David Dadiani and his family members of Samegrelo&#39;s last president.</p>\r\n\r\n<p>2) Martvili Canyon: Sightseeing: Nature&#39;s monument is the gorge of the canyon in the Anklikon, constructed by Abbati&#39;s limestone rocks by the river Abasha. The length of the canyon is 2400 m, the depth of the depth is 20-30 m, and the width is 5-10 m.<br />\r\nThe natural bridges of limestone are stored in canyon in two places, which indicates that the karst cave was collapsed here. In addition, in the middle of the canyon there are 12-15 m high drops of small waterfalls. One section is distinguished by its special beauty. This is an office that is known by the name of &quot;Dadiani Sabana&quot;. The trail goes to the staircase constructed from the big quadrilateral limestone. Giorgi Chkondideli and David Aghmashenebeli walked through this staircase according to the legend.<br />\r\nOn the adjoining limestone rocks developed the flora of the Colchic Forest, with its unique representatives, it can be found here: bear hazelnuts, kolchic buss, yew and others. There are many varieties of moss and firms.</p>\r\n\r\n<p>3) Octus Canyon: The canyon is located in the valley of the River Oketa. The length of the river is 14 km, and the canyon width is 3-6 meters in some, 15-20 meters in some places, and the depth range from 20 to 100 meters. The canyon is decorated with some waterfalls. One of the lakes, Osobpo &quot;is about 60 meters long. There are several natural rocks on the river Okada, one of them is a bogey &quot;. The canyon has caves, from which the karst waters are poured out.</p>\r\n', '<p>Двухдневный тур в Самегрело ... предлагает список достопримечательностей:</p>\r\n\r\n<p>1) Дворец Дадиани: в музее представлены археологические (элементы палеолита, неолита и бронзы) и этнографические (сельскохозяйственное оружие, предметы домашнего обихода, одежда, жилье, &laquo;шакал&raquo;, &laquo;ода&raquo; и другие образцы ) Материал. Представлены экспонаты, изображающие грузинскую историю феодального возраста. Личные вещи Давида Дадиани и членов его семьи последнего президента Самегрело.</p>\r\n\r\n<p>2) Каньон Мартвили: Достопримечательности: памятник природы - это ущелье каньона в Анкликоне, построенное известняковыми скалами Аббати у реки Абаша. Длина каньона составляет 2400 м, глубина глубины 20-30 м, ширина - 5-10 м.<br />\r\nЕстественные мосты известняка хранятся в каньоне в двух местах, что указывает на то, что здесь была разрушена карстовая пещера. Кроме того, в середине каньона есть 12-15 м высоты капель маленьких водопадов. Один раздел отличается особой красотой. Это офис, который известен под именем &laquo;Дадиани Сабана&raquo;. Тропа идет к лестнице, построенной из большого четырехугольного известняка. Гиорги Чкондидели и Дэвид Агмашенебели прошли эту лестницу в соответствии с легендой.<br />\r\nНа прилегающих известняковых породах развилась флора Колхидского леса с ее уникальными представителями, которую можно найти здесь: медвежьи лещинные орехи, колхича, тис и другие. Существует много разновидностей мхов и фирм.</p>\r\n\r\n<p>3) Каньон Октав: Каньон расположен в долине реки Окета. Длина реки составляет 14 км, а ширина каньона составляет 3-6 метров, в некоторых - 15-20 метров, а глубина - от 20 до 100 метров. Каньон украшен водопадами. Один из озер, Osobpo &quot;составляет около 60 метров в длину. На реке Окада есть несколько природных скал, один из которых - пугало &raquo;. В каньоне есть пещеры, из которых выливаются карстовые воды.</p>\r\n', '/uploads/images/news/martvili1111.jpg', '/uploads/images/news/news-2/1.jpg,/uploads/images/news/news-2/10.jpg,/uploads/images/news/news-2/2.jpg,/uploads/images/news/news-2/3.jpg,/uploads/images/news/news-2/4.jpg,/uploads/images/news/news-2/5.jpeg,/uploads/images/news/news-2/6.jpg,/uploads/images/news/news-2/7.jpg,/uploads/images/news/news-2/8.jpg,/uploads/images/news/news-2/9.jpg', '2017-11-14 16:41:00', 0, 1, 1, 0),
(145, 'ტური-სვანეთში', 'Tour-in-Svaneti', 'Тур-в-Сванети', '12', NULL, 'ტური სვანეთში', 'Tour in Svaneti', 'Тур в Сванети', '', '', '', '<p><strong>მარშუტი:<br />\r\n1)დღე</strong></p>\r\n\r\n<ul>\r\n	<li><strong>ენგურის წყალსაცავი</strong></li>\r\n	<li><strong>მესტიაში საოჯახო სასტუმროში განთავსება</strong></li>\r\n	<li><strong>მოვილოცავთ მესტიის საკათედრო ტაძარს</strong></li>\r\n	<li><strong>მესტიის ეთნოგრაფიული მუზეუმი</strong></li>\r\n	<li><strong>მ. ხერგიანის და მარგიანების სახლ-მუზეუმი</strong><br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>2)დღე</strong></p>\r\n\r\n<ul>\r\n	<li><strong>სიყვარულის კოშკი</strong></li>\r\n	<li><strong>კვირიკეს და ივლიტას ტაძარი (სოფ. კალა)</strong></li>\r\n	<li><strong>უშგულის ეთნოგრაფიული მუზეუმი</strong></li>\r\n	<li><strong>ლამარია (ღვთისმშობლის სახელობის ეკლესია)</strong><br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>3)დღე</strong></p>\r\n\r\n<ul>\r\n	<li><strong>კურორტი ჰაწვალი (ვისრიალებთ და გადავიღებთ ულამაზეს სურათებს მესტიის და თეთნულდის ფონზე)</strong></li>\r\n	<li><strong>სოფ.ლატალში&nbsp;მოვილოცავთ&nbsp;მაცხოვრის და იონა წინასწარმეტყველის სახელობის უძველეს ტაძრებს</strong></li>\r\n	<li><strong>დადიანების სასახლე (ზუგდიდი)</strong></li>\r\n	<li><strong>გამომგზავრება თბილისში</strong></li>\r\n</ul>\r\n', '<p>Routes:<br />\r\n1) day</p>\r\n\r\n<p>Enguri reservoir<br />\r\nLocated in Mestia in a family hotel<br />\r\nWe will welcome Mestia Cathedral<br />\r\nMestia Ethnographic Museum<br />\r\nM. Khergiani and Margi House Museum<br />\r\n&nbsp;<br />\r\n2) day</p>\r\n\r\n<p>Love tower<br />\r\nKvirike and Ivlita Temple (Village Kala)<br />\r\nUshguli Ethnographic Museum<br />\r\nLamaria (Church of the Virgin Mary)<br />\r\n&nbsp;<br />\r\n3) Day</p>\r\n\r\n<p>Resort Hatsvali (we will shoot and take pictures of the beautiful pictures of Mestia and Tetnuldi)<br />\r\nIn the village of Latal we will celebrate the ancient temples of the Savior and Iona the prophet<br />\r\nDadiani Palace (Zugdidi)<br />\r\nDeparture to Tbilisi</p>\r\n', '<p>МАРШРУТЫ:<br />\r\n1) День</p>\r\n\r\n<p>Энгурийское водохранилище<br />\r\nРасположенный в Местиа в семейном отеле<br />\r\nМы будем приветствовать собор Местиа<br />\r\nЭтнографический музей Местии<br />\r\nМ. Дом-музей Хергиани и Марги<br />\r\n&nbsp;<br />\r\n2) день</p>\r\n\r\n<p>Любовь башня<br />\r\nХрам Квирике и Ивлита (деревня Кала)<br />\r\nЭштографический музей Ушгули<br />\r\nЛамария (Церковь Девы Марии)<br />\r\n&nbsp;<br />\r\n3) День</p>\r\n\r\n<p>Resort Hatsvali (мы будем снимать и фотографировать красивые фотографии Местии и Тетнулди)<br />\r\nВ деревне Латаль мы отпразднуем древние храмы Спасителя и Ионы, пророка<br />\r\nДворец Дадиани (Зугдиди)<br />\r\nВылет в Тбилиси</p>\r\n', '/uploads/images/svaP.png', '/uploads/images/SVAN.jpg,/uploads/images/sva.png,/uploads/images/SVA2.jpg,/uploads/images/SVAN11.jpg', '2018-05-20 20:07:00', 0, 1, 1, 0),
(146, 'ღვინის-ტური-კახეთში', 'Wine-Tour-in-Kakheti', 'Винный-тур-в-Кахети', '13,12', NULL, 'ღვინის ტური კახეთში ', 'Wine Tour in Kakheti', 'Винный тур в Кахети', '', '', '', '<p>ტურის დეტალური აღწერა:</p>\r\n\r\n<p>პირველი დღე: თბილისი - სიღნაღი - გურჯაანი - თელავი<br />\r\nსანახავი ადგილები: ბოდბის მონასტერი, სიღნაღი სიყვარულის ქალაქი, გურჯაანის ყოვლადწმინდა, საოჯახო მარანი ჩუმლაყში &quot;სიმონაჲ ზავოტი&quot;, თელავი<br />\r\n09:00 გასვლა თბილისიდან კახეთის მიმართულებით<br />\r\n11:00 ბოდბის მონასტრი.&nbsp;<br />\r\n11:30 სიღნაღი ერთი საათი თავისუფალი დრო<br />\r\n14:30 გურჯაანის ყოვლადწმინდა<br />\r\n15:00 - 19:00 კახური ქეიფი &quot;სიმონაჲ ზავოტზე&quot;<br />\r\n20:00 დაბინავება თელავის საოჯახო სასტუმროში</p>\r\n\r\n<p>მეორე დღე: თელავი - ყვარელი - გვირაბი - თბილისი<br />\r\nსანახავი ადგილები: ალავერდი, გრემი, ილიას ტბა, ნეკრესი, გვირაბი.<br />\r\n09:00 საუზმე სასტუმროში<br />\r\n10:00 ალავერდის სამონასტრო კომპლექსი სადაც დავათვალიერებთ მონასტრის უძველეს ღვინის მარანს<br />\r\n12:00 გრემის მონასტერი<br />\r\n13:00 - 14:00 თავისუფალი დრო ილიას ტბაზე<br />\r\n15:00 ნერკესის მონასტერი&nbsp;<br />\r\n16:00 მეღვინეობა ხარება - ღვინის მარანი გვირაბში. ღვინის დეგუსტაცია.<br />\r\n20:00 დაბრუნება თბილისში დაუვიწყარი შთაბეჭდილებებით&nbsp;</p>\r\n', '<p>Detailed description of the tour:</p>\r\n\r\n<p>First day: Tbilisi - Sighnaghi - Gurjaani - Telavi<br />\r\nPlaces to Visit: Bodbe Monastery, Sighnaghi City of Love, Gurjaani Most Guest, Family Marani Chumlaki &quot;Simona Zavoti&quot;, Telavi<br />\r\n09:00 Departure from Tbilisi to Kakheti<br />\r\n11:00 Bodbe Monastery.<br />\r\n11:30 Sighnaghi one hour free time<br />\r\n14:30 Gurjaani is very clean<br />\r\n15:00 - 19:00 Kakhetian Caf&eacute; &quot;Simona Zavote&quot;<br />\r\n20:00 Stay in Telavi Family Hotel</p>\r\n\r\n<p>Second Day: Telavi - Kvareli - Tunnel - Tbilisi<br />\r\nAttractions: Alaverdi, Gremi, Ilia Lake, Nekresi, Tunnel.<br />\r\n09:00 Breakfast at the hotel<br />\r\n10:00 Alaverdi monastery complex where we can see the monastery of ancient wine cellar<br />\r\n12:00 Grammy Monastery<br />\r\n13:00 - 14:00 Free time on Lake Ilia<br />\r\n15:00 Nerke&#39;s monastery<br />\r\n16:00 Winery Khareba - Wine Cellar in the Tunnel. Wine tasting.<br />\r\n20:00 Return to Tbilisi with unforgettable impressions</p>\r\n', '<p>Подробное описание тура:</p>\r\n\r\n<p>Первый день: Тбилиси - Сигнаги - Гурджаани - Телави<br />\r\nМеста для посещения: Монастырь Бодбе, Город Сигнахи, Любовь Гурджаани, Семья Марани Чумлаки &laquo;Симона Завоти&raquo;, Телави<br />\r\n09:00 Вылет из Тбилиси в Кахети<br />\r\n11:00 Монастырь Бодбе.<br />\r\n11:30 Сигнаги один час свободного времени<br />\r\n14:30 Гурджаани очень чистый<br />\r\n15:00 - 19:00 Кафе &laquo;Кахетиан&raquo; &laquo;Симона Завоте&raquo;<br />\r\n20:00 Пребывание в семейном отеле &laquo;Телави&raquo;</p>\r\n\r\n<p>Второй день: Телави - Кварели - Туннель - Тбилиси<br />\r\nДостопримечательности: Алаверди, Греми, озеро Илья, Некреси, Туннель.<br />\r\n09:00 Завтрак в отеле<br />\r\n10:00 Алавердиский монастырский комплекс, где мы можем увидеть монастырь древнего винного погреба<br />\r\n12:00 Монастырь Грэмми<br />\r\n13:00 - 14:00 Свободное время на озере Илья<br />\r\n15:00 Монастырь Нерке<br />\r\n16:00 Винный завод Хареба - Винный погреб в тоннеле. Дегустация вин.<br />\r\n20:00 Возвращение в Тбилиси с незабываемыми впечатлениями</p>\r\n', '/uploads/images/news/wine290.295.jpg', '/uploads/images/news/wine2.jpg,/uploads/images/news/wine-garden.jpg,/uploads/images/news/wine.jpg', '-10%', 0, 1, 1, 0),
(147, '6-дней-5-ночей', '6-дней-5-ночей', '6-дней-5-ночей', '11', NULL, '6 дней / 5 ночей', '6 дней / 5 ночей', '6 дней / 5 ночей', '', '', '', '', '', '<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 1.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 2. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Тбилиси ( пешая обзорная экскурсия ) &ndash; Мцхета - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 3. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Тбилиси &ndash; Телави &ndash; Цинандали ( + дегустация вина ) -&nbsp; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 4. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Тбилиси </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 5. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Тбилиси &ndash; пещерный город Уплисцихе - Боржоми &ndash; Тбилиси </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 6.&nbsp; </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Отъезд.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 1. Прибытие &ndash; Тбилиси&nbsp;&nbsp; </span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Встреча гостей в аэропорту и трансфер в отель выбранной категории.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Размещение в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночевка в Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 2. Тбилиси / Мцхета &ndash; Тбилиси&nbsp; &nbsp;<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B84.jpg\" style=\"width: 300px; height: 146px; float: right; margin: 12px;\" /></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">10:00&nbsp; Встреча с гидом в лобби отеля.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i>&laquo; По двум столицам &raquo;</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 6 &ndash; 7 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь,</span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.&nbsp;</span></span></span></span><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 3. Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси&nbsp;&nbsp;<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpg\" style=\"width: 300px; height: 146px; float: right; margin: 12px;\" /></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-style: italic; font-size: 14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Экскурсия в Кахетию .</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Кахетия</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">, насладиться зеленью речных долин и склонами гор Большого Кавказа.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в., которая имела утонченный европейский вкус.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"><span style=\"color:red\">( дегустация вин на винзаводе &laquo; Корпорация Киндзмараули &raquo; )</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ.<b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;День 4.Тбилиси&nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B81.jpg\" style=\"width: 300px; height: 146px; float: left; margin: 12px;\" /></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Свободный день.</span></span></span></b></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:16px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 5. Тбилиси &ndash; пещерный город Уплисцихе - Боржоми &ndash; Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Экскурсия в пещерный город Уплисцихе и город Боржоми.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Город Гори</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> расположен в 89 км от Тбилиси на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.<b><i><span style=\"color:#002060\">( посещение музея по желаниюза дополнительную плату )</span></i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы посетите курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 6. Отъезд - Тбилиси </span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B8%20pat.jpg', '/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B84.jpg,/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0.jpg,/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg,/uploads/images/turebi/6%20days%205%20night/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B8.jpg,/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B81.jpg,/uploads/images/turebi/6%20days%205%20night/%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg,/uploads/images/turebi/6%20days%205%20night/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C.jpg,/uploads/images/turebi/6%20days%205%20night/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B81.jpg,/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B82.jpg,/uploads/images/turebi/6%20days%205%20night/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpg,/uploads/images/turebi/6%20days%205%20night/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C%201.jpg,/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%201.jpg,/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8.png', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(148, '5-дней-4-ночи', '5-дней-4-ночи', '5-дней-4-ночи', '14', NULL, '5 дней / 4 ночи', '5 дней / 4 ночи', '5 дней / 4 ночи', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 1.&nbsp;&nbsp; Прибытие &ndash; Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 2. &nbsp;Тбилиси &ndash; Мцхета - Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 3.&nbsp;&nbsp;Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Сигнахи &ndash; Бодбийский монастырь&nbsp; &ndash; Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 4.&nbsp; Тбилиси &ndash; крепость Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 5.&nbsp; Отъезд &ndash; Тбилиси</span></span></span></span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 1. Прибытие &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Встреча гостей в аэропорту / на ж.д. &nbsp;и трансфер в отель выбранной категории.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Размещение в отеле / апартаментах.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Пешая экскурсия по Тбилиси.<span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 2,5 &ndash; 3 ч<i>.</i></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь,</span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;День 2. Тбилиси &ndash; Мцхета &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;Завтрак в отеле / апартаментах.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;Экскурсия в бывшую столицу Грузии &ndash; Мцхета.<b><i><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ&nbsp; &nbsp;ГОСУДАРСТВЕННОСТИ &raquo;</span></i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;Продолжительность тура : 4 &ndash; 5 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская</span></span></span></span><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0223.jpg\" style=\"width: 300px; height: 146px; float: left; margin: 12px;\" /></span></span></span></b></span><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 3. Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле / апартаментах.&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Экскурсия в Кахетию .</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 8 &ndash; 10 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Кахетия</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзаж</span></span></span></span><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">а и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">, насладиться зеленью речных долин и склонами гор Большого Кавказа.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России</span></span></span></span><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" /></span></span></span></b></span><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в., которая имела утонченный европейский вкус.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ. <b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"><span style=\"color:#002060\">( Дегустация вин / чачи / обед за дополнительную плату )</span></span></span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 4. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси&nbsp;&nbsp;<img alt=\"\" src=\"/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20324.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" /></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле / апартаментах.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i>НА ПУТИ К ВЕЛИКАНУ</i></b>&raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Военно &ndash; грузинская дорога</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот! </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, </span></span></span><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">XIV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\"> в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице включен в стоимость тура )</i>.</span></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">День 5. Отъезд &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Завтрак в отеле / апартаментах.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span cambria=\"\" style=\"font-family:\">Трансфер в аэропорт. </span></span></span></span></p>\r\n', '/uploads/images/turebi/ananuri%20pat.jpg', '/uploads/images/turebi/yazbegi3.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C1.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B8.jpg,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpeg,/uploads/images/turebi/6%20days%205%20night/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B8.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%2012.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B82.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B843.jpg,/uploads/images/turebi/yazbegi12.jpg,/uploads/images/turebi/bodbis%20monasteri.jpg,/uploads/images/turebi/mcxeta4534.jpg,/uploads/images/turebi/tbilisi56543.jpg', '-10 %', 0, 0, 0, 1),
(149, '4-дня-3-ночи', '4-дня-3-ночи', '4-дня-3-ночи-1', '20', NULL, '4 дня / 3 ночи', '4 дня / 3 ночи', '4 дня / 3 ночи', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси &ndash; Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash;Ананури &ndash; Уплисцихе &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4</b>. Отъезд &ndash; Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 1. Прибытие &ndash; Тбилиси</b></span></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 2.</b> Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">&laquo; По двум столицам &raquo;</span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 150px; float: left;\" />Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span>XIIIвек ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b>XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте <img alt=\"\" src=\"/uploads/images/turebi/sveticxoveli7.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"color:#002060\">&nbsp;( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 3.</b> Тбилиси &ndash;Ананури &ndash; Уплисцихе &ndash; Тбилиси</span></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; Грузинской дороге и пещерный город Уплисцихе.</p>\r\n\r\n<p><b>Продолжительность тура : 6 &ndash; 8 ч.</b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Путешествие продолжим в пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник <img alt=\"\" src=\"/uploads/images/turebi/upliscixe%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: right;\" />грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 4. Отъезд &ndash; Тбилиси</b></span></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/upliscixe%20pat.jpg', '/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8.jpg,/uploads/images/turebi/upliscixe(1).jpg,/uploads/images/turebi/Uplistsikhe1.jpg,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0223.jpg,/uploads/images/turebi/tbilisi456543.jpg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B823334.jpg,/uploads/images/turebi/abanot.jpg,/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/mcxeta6556.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B822.png', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(150, '5-дней-4-ночи-1-2', '5-дней-4-ночи-1-2', '5-дней-4-ночи-1-2', '14', NULL, '5 дней / 4 ночи', '5 дней / 4 ночи', '5 дней / 4 ночи', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Сигнахи &ndash; Бодбийский монастырь &ndash; Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Отъезд &ndash; Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси.<span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span>XIIIвек ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 200px; float: left;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b>XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000- летию рождества Христова и 1500-летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в бывшую столицу Грузии &ndash; Мцхета.<b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/sveticxoveli7.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Сигнахи &ndash; Бодбийский монастырь &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ОБИТЕЛЬ ПРЕКРАСНОГО</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 5 &ndash; 7 ч.</b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Маршрут</span></span></b><b>:<i>Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</i></b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там фун<img alt=\"\" src=\"/uploads/images/turebi/signagi4325.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 200px;\" />кционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ.</p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Отъезд &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/tbilisi54%20pat.jpg', '/uploads/images/turebi/tbilisi5464.jpg,/uploads/images/turebi/mcxeta4534.jpg,/uploads/images/turebi/bodbis%20monasteri.jpg,/uploads/images/turebi/bodbis%20monasteri2.jpg,/uploads/images/turebi/signagi4325.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/tbilisi543.jpg,/uploads/images/turebi/sveticxoveli7.jpg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8.jpg,/uploads/images/turebi/abanotubani12.jpg,/uploads/images/turebi/narikala.jpg,/uploads/images/turebi/tbilisi56543.jpg,/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B82.jpg', '- 5%', 0, 0, 0, 1),
(151, '05-дней-04-ночей', '05-дней-04-ночей', '05-дней-04-ночей', '14', NULL, '05 дней / 04 ночей', '05 дней / 04 ночей', '05 дней / 04 ночей', '', '', '', '', '', '<p style=\"text-align: center;\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1.</b> Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси / Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</p>\r\n\r\n<p><b>День 5.</b> Отъезд &ndash; Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1.Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2.Тбилиси / Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">&laquo; По двум столицам &raquo;</span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span>XIIIвек ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: right;\" />памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b>XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"color:#002060\">&nbsp;( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3.Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию .</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</p>\r\n\r\n<p>Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/signagi4325.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ.<b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"color:#002060\">( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4.Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ПУТИ К ВЕЛИКАНУ</span></span></i></b>&raquo;.</p>\r\n\r\n<p><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, <img alt=\"\" src=\"/uploads/images/turebi/%D0%96%D0%B8%D0%BD%D0%B2%D0%B0%D0%BB%D0%B8%20pat.jpg\" style=\"margin: 12px; width: 300px; height: 201px; float: right;\" />чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i>.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5.Отъезд &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20pat.jpg', '/uploads/images/turebi/gudauri3.jpg,/uploads/images/turebi/gudauri4.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B8.jpg,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpeg,/uploads/images/turebi/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B81.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/tbilisi654.jpg,/uploads/images/turebi/ananuri232.jpg,/uploads/images/turebi/yazbegi3.jpg,/uploads/images/turebi/yazbegi4.jpg', '- 10%', 0, 0, 0, 1),
(152, '05-дней-04-ночей-1', '05-дней-04-ночей-1', '05-дней-04-ночей-1', '14', NULL, '05 дней / 04 ночей', '05 дней / 04 ночей', '05 дней / 04 ночей', '', '', '', '', '', '<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси / Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Ананури &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Боржоми &ndash; Ахалцихе &ndash; Вардзия &ndash; Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Отъезд - Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси / Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">&laquo; По двум столицам &raquo;</span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span>XIIIвек ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/narikala.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b>XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"color:#002060\">&nbsp;( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Ананури &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; Грузинской дороге и пещерный город Уплисцихе.</p>\r\n\r\n<p><b>Продолжительность тура : 6 &ndash; 8 ч.</b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%92%D0%BE%D0%B5%D0%BD%D0%BD%D0%BE%20%E2%80%93%20%D0%B3%D1%80%D1%83%D0%B7%D0%B8%D0%BD%D1%81%D0%BA%D0%B0%D1%8F%20%D0%B4%D0%BE%D1%80%D0%BE%D0%B3%D0%B0.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 200px;\" />Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p><b>Город Гори</b> расположен в 89 км от Тбилиси на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</p>\r\n\r\n<p>Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/stalin.jpg\" style=\"margin: 12px; width: 300px; height: 195px; float: left;\" />Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.<b><i><span style=\"color:#002060\">( посещение музея по желанию )</span></i></b></p>\r\n\r\n<p>В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Боржоми &ndash; Ахалцихе &ndash; Вардзия &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в Самцхе &ndash; Джавахети.<span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>НАСЛЕДИЕ ЗОЛОТОГО ВЕКА</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 8 &ndash; 10 ч.</b></p>\r\n\r\n<p>С древних времен <b>Джавахети</b> был одним из эриставств ( княжеств ) Картлинского царства. Географическое расположение Месхети создавало выгодные условия для культурного и экономического развития страны. Оно способствовало установить связь между старыми культурами известных на территории Грузии и с центрами цивилизаций Ближнего Востока.</p>\r\n\r\n<p>Период царствования Царицы Тамар ( XII &ndash; XIII вв. ) считался Золотым веком Грузии. В этот период начались большие строительства. Среди них самым удивительным считается Вардзия &ndash; наш последний пункт по этому маршруту.</p>\r\n\r\n<p>По дороге к высеченному городу в скале, Вы посетите курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</p>\r\n\r\n<p>Из Боржоми Вы направитесь в город <b>Ахалцихе</b> ( старое название &ndash; Ломсия ), где еще в IX веке находился город &ndash; дворец османских завоевателей, так называемый<b> Рабат</b> ( с арабского &laquo; укрепленное место &raquo; ). Рабат &ndash; это один большой город в городе. В комплексе кроме исторических крепостей, башен, церквей, келий и мечети существуют и современные учреждения разного типа.</p>\r\n\r\n<p>Редко где Вы сможете встретить столько красоты, столько достоинства и величия. Редко какая крепость может похвастаться своей необычной историей и своими царскими корнями. Вся атмосфера крепости дышит великими событиями, которые происходили здесь столько веков назад.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/vardzia%20pat1.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />Продолжая путешествие, Вы посетите высеченный в скале комплекс<b> Вардзия</b> &ndash; один из важнейших и впечатляющих исторических памятников Грузии. Вардзия &ndash; памятник&nbsp; XII века, предназначенный для военной крепости и грандиозный укрепленный монастырь.Комплекс Вардзия простирается на 500 м и состоит из 13 этажей, который связывают друг с другом сложная система тоннелей.</p>\r\n\r\n<p>Наш гид повествует Вам историю строительства этого великолепного памятника, который сохранил следы многих трагических событий. Вы окунетесь во времена царствования&nbsp; великой царицы Тамары, узнаете много исторических фактов и легенд, связанных с ней и на мгновенье представите мир ее глазами...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Отъезд - Тбилиси </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p><span lang=\"RU\" style=\"font-size:11.0pt\"><span style=\"line-height:115%\"><span calibri=\"\" style=\"font-family:\">Трансфер в аэропорт</span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/uflists%20pat.jpg', '/uploads/images/turebi/vardzia26.jpg,/uploads/images/turebi/ananuri232.jpg,/uploads/images/turebi/mcxeta4534.jpg,/uploads/images/turebi/gori21.png,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0223.jpg,/uploads/images/turebi/Uplistsikhe1.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B84.jpg,/uploads/images/turebi/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B513.jpg,/uploads/images/turebi/Borjomi1.jpg,/uploads/images/turebi/Borjomi12.jpg,/uploads/images/turebi/%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8F%20%D0%94%D0%B6%D0%B2%D0%B0%D1%80%D0%B8.jpg,/uploads/images/turebi/%D0%A3%D0%BF%D0%BB%D0%B8%D1%81%D1%86%D0%B8%D1%85%D0%B5.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(153, '05-дней-04-ночей-1-2', '05-дней-04-ночей-1-2', '05-дней-04-ночей-1-2', '14', NULL, '05 дней / 04 ночей ', '05 дней / 04 ночей ', '05 дней / 04 ночей ', '', '', '', '', '', '<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси &ndash; Мцхета &ndash; Уплисцихе - Кутаиси</p>\r\n\r\n<p><b>День 3.</b> Кутаиси - Батуми</p>\r\n\r\n<p><b>День 4.</b> Батуми</p>\r\n\r\n<p><b>День 5</b>. Отъезд &ndash; Батуми</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси.<span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span>XIIIвек ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i><span style=\"color:#7030a0\">( подъем на канатке с парка Рике к Нарикале )</span></i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b>XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2.</b> Тбилиси &ndash; Мцхета &ndash; Уплисцихе - Кутаиси</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в бывшую столицу Грузии &ndash; Мцхета и путешествие в пещерный город Уплисцихе.</p>\r\n\r\n<p><b>Продолжительность тура:&nbsp; 8 &ndash; 9 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%20pat2.jpg\" style=\"margin: 12px; float: right; width: 290px; height: 295px;\" />Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Путешествие продолжите в пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>Переезд в г. Кутаиси.</p>\r\n\r\n<p>Ночь в отеле&nbsp; Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3.</b> Кутаиси - Батуми</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по окрестностям Кутаиси.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>Затем Вы посетите выдающий<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20pat.jpg\" style=\"margin: 12px; float: left; width: 290px; height: 295px;\" />ся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p>У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке.</p>\r\n\r\n<p>Переезд в Батуми.</p>\r\n\r\n<p>Ночь в отеле Батуми.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4.</b> Батуми</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Обзорная экскурсия по Батуми.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность экскурсии : 2 ч.</span></span></b></p>\r\n\r\n<p><b>Батуми</b> &ndash; один из древнейших городов Грузии, который согласно историческим источникам, был основан еще до нашей эры. Богатое историческое прошлое в значительной степени обусловило архитектурное разнообразие города.</p>\r\n\r\n<p>В сегодняшнем Батуми, помимо старых, исторических строений, есть и много новых, сделанных в современном стиле: <b>Алфавитная башня</b>, <b>Дом Юстиции</b>, рестораны и гостиницы необычных форм и др.</p>\r\n\r\n<p>В самом&nbsp; сердце старого Батуми находятся <b>площадь Европы </b>и &laquo; <b>Пьяцца</b> &raquo;, окруженные центральными улицами и диковинными зданиями.</p>\r\n\r\n<p><b>Батумский приморский парк</b> занимает территорию длиной в 7 км.&nbsp; Бульвар украшают бунгало, кафе &ndash; лаунджи, рестораны, детские аттракционы, скамейки современного дизайна, скульптуры и новые фонтаны. Аллеи приморского парка отличаются необычным сочетанием <img alt=\"\" src=\"/uploads/images/turebi/batumi543.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />вечнозеленых растений, магнолий и пальм. Бульвар всегда был источником многих творческих начинаний. И сегодня он является своеобразной гаванью для оригинальных творческих идей и современных скульптурных композиций: &laquo; <b>Я, ты и Батуми</b> &raquo;, &laquo; <b>Нино и Али</b> &raquo;, &laquo; <b>Женщина с велосипедом</b> &raquo;, &laquo; <b>Статуи влюбленных</b> &raquo;, или признанные символом города , скульптуры &laquo; <b>Дельфины</b> &raquo; - делают Батумский бульвар более притягательным и эффектным.</p>\r\n\r\n<p>Ночевка в отеле Батуми.</p>\r\n\r\n<p><b>День 5</b>. Отъезд &ndash; Батуми</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт Батуми.</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/Batum%20pat.jpg', '/uploads/images/turebi/batumi312.jpg,/uploads/images/turebi/Batumi1.jpg,/uploads/images/turebi/batumi4321.jpg,/uploads/images/turebi/Kutaisi.JPG,/uploads/images/turebi/tbilisi321.jpg,/uploads/images/turebi/%D0%A3%D0%BF%D0%BB%D0%B8%D1%81%D1%86%D0%B8%D1%85%D0%B5.jpg,/uploads/images/turebi/batumi543.jpg,/uploads/images/turebi/tbilisi654433.jpg,/uploads/images/turebi/upliscixe34.jpg,/uploads/images/turebi/abanotubani12.jpg,/uploads/images/turebi/batumi65.jpg', '', 0, 0, 0, 1),
(154, '7-дней-6-ночей-1', '7-дней-6-ночей-1', '7-дней-6-ночей-1', '17', NULL, '7 дней / 6 ночей', '7 дней / 6 ночей', '7 дней / 6 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Гори &ndash; Уплисцихе - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси&nbsp; - Боржоми &ndash; Ахалцихе &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7.&nbsp; </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Отъезд.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1. Прибытие </span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Встреча гостей в аэропорту и трансфер в отель выбранной категории.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Размещение в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2. Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Пешая экскурсия по Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура:&nbsp; 3 &ndash; 5 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь,</span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню <img alt=\"\" src=\"/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B82.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3. Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия во Внешнюю Кахетию.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 5 &ndash; 7 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Кахетия</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/signagi45r3.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В городе находится музей Нико Пиросмани, где выставлены 16 его работ<b><i>. ( посещение музея за дополнительную плату )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4. Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Поездка в бывшую столицу Грузии &ndash; Мцхета и путешествие по Военно &ndash; Грузинской дороге.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура:&nbsp; 7 &ndash; 8 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), выстроенный на месте первой в<img alt=\"\" src=\"/uploads/images/turebi/sveticxoveli7.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" /> Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Еще один выдающийся памятник&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Путешествие продолжим по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Военно &ndash; грузинская дорога</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот! </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Возвращение в Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5. Гудаури &ndash; Гори &ndash; Уплисцихе - Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 7 &ndash; 8 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">По программе город<b> Гори</b> расположенный на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/uflists%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: right;\" />В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6. Тбилиси -&nbsp; Боржоми &ndash; Ахалцихе &ndash; Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Далее Вы направитесь в курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B51.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Из Боржоми Вы направитесь в город <b>Ахалцихе</b> ( старое название &ndash; Ломсия ), где еще в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке находился город &ndash; дворец османских завоевателей, так называемый<b> Рабат</b> ( с арабского &laquo; укрепленное место &raquo; ). Рабат &ndash; это один большой город в городе. В комплексе кроме исторических крепостей, башен, церквей, келий и мечети существуют и современные учреждения разного типа.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Редко где Вы сможете встретить столько красоты, столько достоинства и величия. Редко какая крепость может похвастаться своей необычной историей и своими царскими корнями. Вся атмосфера крепости дышит великими событиями, которые происходили здесь столько веков назад.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Возвращение в Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7.&nbsp; Отъезд.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер в аэропорт.</span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B5%20pat.jpg', '/uploads/images/turebi/tbilisi5464.jpg,/uploads/images/turebi/Borjomi987.jpg,/uploads/images/turebi/upliscixe(1).jpg,/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/uflistsikhe5422.jpg,/uploads/images/turebi/tbilisi56543.jpg,/uploads/images/turebi/tbilisi456543.jpg,/uploads/images/turebi/bodbis%20monasteri.jpg,/uploads/images/turebi/bodbis%20monasteri2.jpg,/uploads/images/turebi/signagi4325.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/narikala.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(155, '7-дней-6-ночей-1-2', '7-дней-6-ночей-1-2', '7-дней-6-ночей-1-2', '17', NULL, '7 дней / 6 ночей', '7 дней / 6 ночей', '7 дней / 6 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Сигнахи - Бодбе &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Ананури &ndash; Уплисцихе - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7.&nbsp; </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Отъезд.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1. Прибытие </span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Встреча гостей в аэропорту и трансфер в отель выбранной категории.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Размещение в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2. Тбилиси</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Пешая экскурсия по Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура:&nbsp; 3 &ndash; 5 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь,</span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B82.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Поездка в бывшую столицу Грузии &ndash; Мцхета.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура:&nbsp; 4 &ndash; 5 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%20pat2.jpg\" style=\"margin: 12px; float: right; width: 290px; height: 295px;\" />на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Еще один выдающийся памятник&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Сигнахи - Бодбе &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия во Внешнюю Кахетию.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 5 &ndash; 7 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Кахетия</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В городе находится музей Нико Пиросмани, где выставлены 16 его работ<b><i>. ( посещение музея за дополнительную плату )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Ананури &ndash; Уплисцихе - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Путешествие по Военно &ndash; Грузинской дороге и в пещерный город&nbsp; Уплисцихе.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура:&nbsp; 7 &ndash; 8 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Путешествие продолжим по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%92%D0%BE%D0%B5%D0%BD%D0%BD%D0%BE%20%E2%80%93%20%D0%B3%D1%80%D1%83%D0%B7%D0%B8%D0%BD%D1%81%D0%BA%D0%B0%D1%8F%20%D0%B4%D0%BE%D1%80%D0%BE%D0%B3%D0%B0.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 200px;\" />Военно &ndash; грузинская дорога</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот! </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Путешествие продолжите в пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры. </span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Свободный отдых.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7.&nbsp; </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Отъезд.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер в аэропорт Тбилиси.</span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B8%20pat.jpg', '/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%20%203.jpg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B824.jpeg,/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B82.jpg,/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B83.jpg,/uploads/images/turebi/ananuri232.jpg,/uploads/images/turebi/uflistsikhe5422.jpg,/uploads/images/turebi/upliscixe(1).jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B822.png,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8212.jpg,/uploads/images/turebi/signagi45r3.jpg', '', 0, 0, 0, 1),
(156, '7-дней-6-ночей-1-2-3', '7-дней-6-ночей-1-2-3', '7-дней-6-ночей-1-2-3', '17', NULL, '7 дней / 6 ночей', '7 дней / 6 ночей', '7 дней / 6 ночей', '', '', '', '', '', '<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси / Мцхета&nbsp;</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи&nbsp; &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash;- Ананури &ndash; Гудаури &ndash; Казбеги - Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Тбилиси - Кутаиси &ndash; Гелатский монастырский комплекс&ndash; Пещера Прометея &ndash; Батуми</p>\r\n\r\n<p><b>День 6. </b>Батуми</p>\r\n\r\n<p><b>День 7</b>. Отъезд&nbsp; - Батуми</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. .</b> <b>Тбилиси / Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">&laquo; По двум столицам &raquo;</span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8%20pat.jpg\" style=\"margin: 12px; float: left; width: 290px; height: 295px;\" />Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i><span style=\"color:#7030a0\">( подъем на канатке с парка Рике к Нарикале )</span></i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте <img alt=\"\" src=\"/uploads/images/turebi/sveticxoveli7.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи&nbsp; &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию &laquo; <b><i>&nbsp;</i></b>&raquo;</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B81.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ. <b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"color:#7030a0\">Дегустация вин на винном заводе &laquo; </span><span style=\"color:#7030a0\">KTW</span></b><b><span lang=\"RU\" style=\"color:#7030a0\"> &raquo;.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ПУТИ К ВЕЛИКАНУ</span></span></i></b> &raquo;.</p>\r\n\r\n<p><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную<img alt=\"\" src=\"/uploads/images/turebi/Jinvali.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" /> красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице &nbsp;)</i>.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5</b>. <b>Тбилиси &ndash; Кутаиси &ndash; Гелатский монастырь &ndash;Пещера Прометея &ndash; Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Переезд в город Кутаиси.</p>\r\n\r\n<p><b>Продолжительность тура : 8 &ndash; 10 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>Затем Вы посетите выдающийся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F1.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке <b><i><span style=\"color:red\">( за дополнительную плату ).</span></i></b></p>\r\n\r\n<p>Переезд в город Батуми.</p>\r\n\r\n<p>Ночевка в отеле Батуми.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6.</b> <b>Батуми &nbsp;<i>( свободный отдых )</i></b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><i>Свободный день.</i></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 7.</b> <b>Отъезд</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт Батуми.</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20par6.jpg', '/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C.jpg,/uploads/images/turebi/yazbegi3.jpg,/uploads/images/turebi/yazbegi5.jpg,/uploads/images/turebi/abanotubani12.jpg,/uploads/images/turebi/abanot.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B81.jpg,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F2.jpg,/uploads/images/turebi/batumi32.jpg,/uploads/images/turebi/batumi98.jpg,/uploads/images/turebi/batumi312.jpg,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F1.jpg,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F.jpg,/uploads/images/turebi/yazbegi12.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B845.jpg,/uploads/images/turebi/gudauri3.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20324.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(157, '7-дней-6-ночей-1-2-3-4', '7-дней-6-ночей-1-2-3-4', '7-дней-6-ночей-1-2-3-4', '17', NULL, '7 дней / 6 ночей', '7 дней / 6 ночей', '7 дней / 6 ночей', '', '', '', '', '', '<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси&nbsp;</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash;- Бодбийский монастырь &ndash; Сигнахи - Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Тбилиси - Кутаиси &ndash; Гелатский монастырский комплекс&ndash; Пещера Прометея &ndash; Батуми</p>\r\n\r\n<p><b>День 6. </b>Батуми</p>\r\n\r\n<p><b>День 7</b>. Отъезд&nbsp; - Батуми</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси&nbsp; </b></p>\r\n\r\n<p>Пешая экскурсия по Тбилиси. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i><span style=\"color:#002060\">( подъем на канатке с парка Рике к Нарикале )</span></i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/abanot%20pat.jpg\" style=\"margin: 12px; float: left; width: 290px; height: 295px;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Мцхета &ndash; Тбилиси </b></p>\r\n\r\n<p>Экскурсия в бывшую столицу Грузии &ndash; Мцхета. <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%20pat2.jpg\" style=\"margin: 12px; float: right; width: 290px; height: 295px;\" />Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4.</b> <b>Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>Экскурсия во Внешнюю Кахетию.</p>\r\n\r\n<p><b>Продолжительность тура : 5 &ndash; 7 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ<b><i>. <span style=\"color:red\">( посещение музея за дополнительную плату )</span></i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"color:#7030a0\">Дегустация вин на винном заводе &laquo; </span><span style=\"color:#7030a0\">KTW</span></b><b><span lang=\"RU\" style=\"color:#7030a0\"> &raquo;.</span></b></p>\r\n\r\n<p>Ночь в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5</b>. <b>Тбилиси &ndash; Кутаиси &ndash; Гелатский монастырь &ndash;Пещера Прометея &ndash; Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Переезд в город Кутаиси.</p>\r\n\r\n<p><b>Продолжительность тура : 8 &ndash; 10 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>Затем Вы посетите выдающийся па<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20pat.jpg\" style=\"margin: 12px; float: left; width: 290px; height: 295px;\" />мятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p>У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке <b><i><span style=\"color:red\">( за дополнительную плату ).</span></i></b></p>\r\n\r\n<p>Переезд в город Батуми.</p>\r\n\r\n<p>Ночевка в отеле Батуми.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6.</b> <b>Батуми &nbsp;<i>( свободный отдых )</i></b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><i>Свободный день.</i></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 7.</b> <b>Отъезд</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт Батуми.</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/batumi%20pat(1).jpg', '/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C(1).jpg,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F.jpg,/uploads/images/turebi/bodbis%20monasteri.jpg,/uploads/images/turebi/Batumi1.jpg,/uploads/images/turebi/tbilisi321.jpg,/uploads/images/turebi/batumi344.png,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F1.jpg,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F2.jpg,/uploads/images/turebi/mcxeta34.jpg,/uploads/images/turebi/tbilisi86.jpg,/uploads/images/turebi/batumi65.jpg,/uploads/images/turebi/batumi543.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8212.jpg,/uploads/images/turebi/Bagrati_Kutaisi.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B84.jpg', '', 0, 0, 0, 1),
(158, '8-дней-7-ночей', '8-дней-7-ночей', '8-дней-7-ночей', '18', NULL, '8 дней / 7 ночей', '8 дней / 7 ночей', '8 дней / 7 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p align=\"center\" style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси </span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси </span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5.&nbsp; </span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Тбилиси &ndash; Бодбе - Сигнахи &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Ананури &ndash; Уплисцихе - Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Боржоми &ndash; Ахалцихе &ndash; Рабат - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Отъезд</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; прибытие&nbsp; &nbsp;</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Прибытие в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси</span></span></span></span></b></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Вас встретит водитель компании и отвезет в гостиницу. Заезд в гостиницу в 12:00.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси&nbsp; &nbsp;</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Пешая экскурсия по Тбилиси.&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Вас ждет экскурсия с гидом по Тбилиси - столице Грузии.</span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">В этот день запланировано посещение православного кафедрального собора</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sameba.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">Цминда Самеба</span></span></span></span></b></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, являющегося сегодня одним из самых больших православных соборов мира. Во время <img alt=\"\" src=\"/uploads/images/turebi/narikala.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />экскурсии вы посетите</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/metekhi.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">церковь Метехи</span></span></span></span></b></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">и памятник легендарному основателю Тбилиси царю Вахтангу Горгасали, подниметесь по канатной дороге на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/narikala.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">крепость Нарикала</span></span></span></span></b></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, откуда вам откроется захватывающая дух панорама города. Спустившись к району серных бань, в которых любили бывать Пушкин и Дюма, и, пройдя мимо единственной мечети в Тбилиси, вы окажитесь среди уютных кафе и сувенирных лавок уголка Шарден, любимого места горожан и гостей столицы. Узкие улочки, винные погреба и картинные галереи хранят дух Старого Тбилиси и ведут к</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sioni.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">храму Сиони</span></span></span></span></b></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, названного в честь Сионской горы,</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/anchiskhati.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">базилике Анчисхати</span></span></span></span></b></a><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> VI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> века и уникальной часовой башне</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rezo-gabriadze-theatre.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">Театра Кукол Резо Габриадзе</span></span></span></span></b></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">Праздничный ужин в ресторане.</span></span></span></span></b></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси&nbsp;&nbsp;</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">Свободный день.</span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия в бывшую столицу Грузии &ndash; Мцхета.<b><i><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 4 &ndash; 5 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%20pat2.jpg\" style=\"margin: 12px; float: left; width: 290px; height: 295px;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Путешествие начнем </span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">в город любви</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/sighnaghi.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"text-decoration:none\">Сигнахи</span></span></span></span></span></b></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">,</span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">который в средние века был одним из важнейших<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%91%D0%BE%D0%B4%D0%B1%D0%B5%20pat.jpg\" style=\"margin: 12px; float: right; width: 290px; height: 295px;\" /> торговых пунктов на Великом Шелковом Пути, окруженным крепостной стеной с 23 башнями. Прогулка по городу, возвращение в Тбилиси. По пути остановка в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/bodbe.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"text-decoration:none\">монастырском комплексе Бодбе</span></span></span></span></span></b></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, где, согласно легенде, похоронена Св. Нино, привнёсшая христиантсов в Грузию в четвертом веке. По указу Императора Александра </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">III</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> с 1889 года Бодбе функционирует как женский монастырь. Прибытие в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><b>. Тбилиси &ndash; Ананури &ndash;Уплисцихе &ndash; Тбилиси&nbsp; &nbsp;</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия по Военно &ndash; грузинской дороге и пещерный город. </span></span></span>&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Военно &ndash; грузинская дорога</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот! </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Затем<b> </b><span style=\"background:white\"><span style=\"color:#4f5d6e\">вы отправитесь в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori/uplistsikhe.htm\"><b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"text-decoration:none\">Уплисцихе</span></span></span></span></span></b></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; город, вырубленный в скалах в первой половине первого тысячелетия до нашей эры. На протяжении многих веков Уплисцихе был точкой пересечения множества торговых маршрутов на Великом Шелковом пути: в самый пик расцвета в городе проживало более 20 000 человек, он был центром языческой культуры в регионе.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><b>Тбилиси &ndash; &nbsp;Боржоми - Ахалцихе &ndash; Рабат &ndash; Тбилиси&nbsp;&nbsp;</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">По дороге к Рабату, Вы посетите курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с досто<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B5%20pat.jpg\" style=\"margin: 12px; float: left; width: 290px; height: 295px;\" />примечательностями этого курорта.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Из Боржоми Вы направитесь в город <b>Ахалцихе</b> ( старое название &ndash; Ломсия ), где еще в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке находился город &ndash; дворец османских завоевателей, так называемый<b> Рабат</b> ( с арабского &laquo; укрепленное место &raquo; ). Рабат &ndash; это один большой город в городе. В комплексе кроме исторических крепостей, башен, церквей, келий и мечети существуют и современные учреждения разного типа.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Редко где Вы сможете встретить столько красоты, столько достоинства и величия. Редко какая крепость может похвастаться своей необычной историей и своими царскими корнями. Вся атмосфера крепости дышит великими событиями, которые происходили здесь столько веков назад.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Возвращение в Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><b>&nbsp;Отъезд&nbsp;&nbsp;</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансферв а/порт.&nbsp; </span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/Borjomi%20pat43.jpg', '/uploads/images/turebi/ananuri232.jpg,/uploads/images/turebi/Borjomi12.jpg,/uploads/images/turebi/mcxeta6556.jpg,/uploads/images/turebi/bodbis%20monasteri2.jpg,/uploads/images/turebi/Borjomi987.jpg,/uploads/images/turebi/tbilisi321.jpg,/uploads/images/turebi/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B513.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/upliscixe.jpg,/uploads/images/turebi/uflistsikhe5422.jpg,/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/Borjomi1.jpg,/uploads/images/turebi/upliscixe34.jpg', '', 0, 1, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(159, '10-дней-9-ночей', '10-дней-9-ночей', '10-дней-9-ночей', '16', NULL, '10 дней / 9 ночей', '10 дней / 9 ночей', '10 дней / 9 ночей', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Кварели &ndash; Цинандали &ndash; Тбилиси</span></span> </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5.&nbsp; </span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Тбилиси &ndash; Дманиси &ndash; Болниси &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Батуми</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батуми &ndash; Гонио - Махунцети ( Горная Аджария ) &ndash; Батуми</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\"> Батуми</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\"> Батуми</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Отъезд</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; прибытие</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Пешая экскурсия по Тбилиси.&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"><img alt=\"\" src=\"/uploads/images/turebi/narikala.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />Прибытие в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Тбилиси</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Вас встретит водитель компании и отвезет в гостиницу. Заезд в гостиницу в 12:00. Вас ждет экскурсия с гидом по Тбилиси - столице Грузии.</span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">В этот день запланировано посещение православного кафедрального собора</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sameba.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Цминда Самеба</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, являющегося сегодня одним из самых больших православных соборов мира. Во время экскурсии вы посетите</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/metekhi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">церковь Метехи</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">и памятник легендарному основателю Тбилиси царю Вахтангу Горгасали, подниметесь по канатной дороге на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/narikala.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">крепость Нарикала</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, откуда вам откроется захватывающая дух панорама города. Спустившись к району серных бань, в которых любили бывать Пушкин и Дюма, и, пройдя мимо единственной мечети в Тбилиси, вы окажитесь среди уютных кафе и сувенирных лавок уголка Шарден, любимого места горожан и гостей столицы. Узкие улочки, винные погреба и картинные галереи хранят дух Старого Тбилиси и ведут к</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sioni.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">храму Сиони</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, названного в честь Сионской горы,</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/anchiskhati.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">базилике Анчисхати</span></span></span></span></span></a><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> VI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> века и уникальной часовой башне</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rezo-gabriadze-theatre.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Театра Кукол Резо Габриадзе</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 8 &ndash; 9 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Утром выезд из Тбилиси в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Мцхету</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, один из самых древних городов, культурный и религиозный центр Грузии. В настоящее время город объявлен объектом Всемирного наследия ЮНЕСКО и является живым музеем со многочисленными архитектурными и историческими памятниками. Посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/dzhavari.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">церкви Джвари</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(храм &laquo;Святого Креста&raquo;), построенного в </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">VI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> в. Находясь на высоком холме, мы увидим слияние двух величественных рек Арагви и Мтквари. Посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/svetitskhoveli.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">собора Светицховели</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(</span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">XI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> в).</span></span></span></span></span><br />\r\n<span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Продолжаем исследовать провинцию Хеви с посещения архитектурного комплекса</span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/ananuri-fortress.htm\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Ананури</span></span></span></span></a><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, который раньше был резиденцией герцогов в регионе Аргвети. Комплекс церквей и башен, относящиеся </span></span></span></span><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">XVI</span></span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">-</span></span></span></span><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">XVII</span></span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> вв, красиво возвышается над водохранилищем Жинвали. Далее едем по Военно-грузинской дороге, проходящей через самую высокую точку 2395 м над уровнем моря. По пути сделаем остановку у минерального источника, бегущего вниз от вершины. Прибытие в Казбеги, небольшой город с видом на гору Казбек (или Мкинвари) 5033 м., седьмой по высоте пик в Кавказских горах. Поднимитесь на внедорожнике к Троицкой церкви в Гергети, расположенной на высоте 2170 м под горой Казбеги. Возвращение в</span></span></span></span> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Тбилиси. Ночь в гостинице.</span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Кварели &ndash; Цинандали &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 7 &ndash; 8 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Утром выезд из Тбилиси в регион Кахетии. Кахетия расположена в восточной части Грузии и известна как колыбель грузинского вина. Здесь в долине Алазань выращены уникальные виды виноградов, такие как Киндзмараули, Манави, Саперави. Посещение женского монастыря</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/bodbe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Бодбе</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(19 в), место захоронения Св. Нино, которая принесла христианство в Грузию в 4 в. Далее направляемся в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/signakhi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Сигнахи</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, небольшой город, расположенный на вершине холма, откуда можно увидеть прекрасный вид реки Алазани и горный хребет Кавказа. Дегустация вин в винном погребе &ldquo;Хареба&rdquo;. Первоначально построенный как военный тоннель, теперь это один из самых больших винных погребов в Грузии с протяженностью 7.7 км. Посещение резиденции одной из самых любимых и известных грузинских семей &ndash;</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/tsinandali.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">принцы Чавчавадзе</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Возвращение в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 6 &ndash; 7 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Утром выезд из Тбилиси в город</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Гори</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori/stalin.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">музея Иосифа Сталина</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, где можно увидеть дом, в котором он родился, и <img alt=\"\" src=\"/uploads/images/turebi/stalin.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 195px;\" />его частный бронированный вагон поезда. Далее едем в Уплисцихе (&ldquo;Крепость Владыки&rdquo;), высеченный из камня город, относящийся к эллинистическому периоду.</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori/uplistsikhe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Уплисцихе</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">расположен на левом берегу реки Мтквари и известен уникальной комбинацией различных стилей вырезанных в скале культур и сосуществования языческой и христианской архитектур. Прибытие в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Дманиси &ndash; Болниси &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 6 -8 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">С утра выезд из Тбилиси. Посещение городка</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/dmanisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Дманиси</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. В 9 веке Дманиси был центром арабского государства Думанисский эмират и важным торговым центром на Великом Шелковом пути. При раскопках в конце прошлого &ndash; начале нынешнего века, на территории города был обнаружен череп, предположительно принадлежащий первому человеку в Европе (1,6 млн. лет). Далее посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/rustavi/bolnisi-sion.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Болнисского Сиона</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, построенного в 478-493 гг. Это самая старая церковь в Грузии, сохранившаяся до наших дней. Собор также известен древними надписями, упоминающими имена епископа Давида Болнисского, а также двух сасанидских царей &ndash; Пероза </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">I</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> и Кавадха </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">I</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Эти надписи &ndash; один из древнейших исторических документов, подтверждающих существование грузинского языка в то время. Прибытие в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Батуми</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Переезд в город Батуми.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Обзорная экскурсия по Батуми.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность экскурсии : 2 ч.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батуми &ndash; один из древнейших городов Грузии, который согласно историческим источникам, был основан еще до нашей эры. Богатое историческое прошлое в значительной степени обусловило архитектурное разнообразие города.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В сегодняшнем Батуми, помимо старых, исторических строений, есть и много новых, сделанных в современном стиле: Алфавитная башня, Дом Юстиции, рестораны и гостиницы необычных форм и др.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В самом&nbsp; сердце старого Батуми находятся площадь Европы и &laquo; Пьяцца &raquo;, окруженные центральными улицами и диковинными зданиями.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батумский приморский парк занимает территорию длиной в 7 км.&nbsp; Бульвар украшают бунгало, кафе &ndash; лаунджи, рестораны, детские аттракционы, скамейки современного дизайна, скульптуры и новые фонтаны. Аллеи приморского парка отличаются необычным сочетанием вечнозеленых растений, магнолий и пальм. Бульвар всегда был источником многих творческих начинаний. И сегодня он является своеобразной гаванью для оригинальных творческих идей и современных скульптурных композиций: &laquo; Я, ты и Батуми &raquo;, &laquo; Нино и Али &raquo;, &laquo; Женщина с велосипедом &raquo;, &laquo; Статуи влюбленных &raquo;, или признанные символом города , скульптуры &laquo; Дельфины &raquo; - делают Батумский бульвар более притягательным и эффектным.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в отеле Батуми.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b><b> </b><b> </b><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батуми &ndash; Гонио - Махунцети ( Горная Аджария ) &ndash; Батуми</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия в горную Аджарию.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 4 - 6 ч.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%93%D0%BE%D0%BD%D0%B8%D0%BE2.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />В 12 км к югу от Батуми находится крепость Гонио &ndash; Апсарос, а в ее окрестностях &ndash; одно из древнейших поселений Грузии. В прошлом крепость Гонио &ndash; Апсарос имела уникальное стратегическое значение: она защищала подходы к ущельям рек Чорохи и Ачарисцкали, которые связывали внутренние регионы Юго &ndash; Западной Грузии с Черноморским побережьем. Крепость Гонио была опорной цитаделью на Черноморском побережье сначала Византийской, а затем и Османской империй. Соответственно, крепость включает и несколько культурных пластов : римский&nbsp; (</span></span></span><span style=\"line-height:115%\"><span arial=\"\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> - </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. до н. э. ), византийский ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ) и османский ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. )</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Стратегическое расположение Аджарии на перекресте торговых путей ( Северный Кавказ- Малая Азия, Турция &ndash; Самцхе &ndash; Артануджи и так далее ) обусловило необходимость поддержания в надлежащем состоянии существующих дорог и мостов. Сохранившиеся в Аджарии древние арочные каменные мосты являются важнейшими памятниками грузинской материальной культуры. В результате последних исследований выявлено около 25 сохранившихся в Аджарии арочных мостов, строительство которых, согласно устным сказаниям, связано с именем царицы Тамар.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В селе Махунцети расположен мост&ndash;один из самых крупных арочных мостов Аджарии. Он построен из скальных камней вулканического происхождения, соединенных между собой известковым раствором. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">По дороге Вы увидете самую длинную реку Аджарии &ndash; Аджарисцкали и&nbsp; ее слияние с Чорохи, а также горный водопад Махунцети с кристально чистой водой.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в отеле Батуми.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Батуми</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Батуми<img alt=\"\" src=\"/uploads/images/turebi/batumi4321.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" /></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><b><span lang=\"RU\"><span style=\"line-height:115%\">Отъезд</span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер в аэропорт Батуми.</span></span></span></span></p>\r\n', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Кварели &ndash; Цинандали &ndash; Тбилиси</span></span> </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5.&nbsp; </span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Тбилиси &ndash; Дманиси &ndash; Болниси &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Батуми</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батуми &ndash; Гонио - Махунцети ( Горная Аджария ) &ndash; Батуми</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\"> Батуми</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\"> Батуми</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Отъезд</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; прибытие</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Пешая экскурсия по Тбилиси.&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"><img alt=\"\" src=\"/uploads/images/turebi/narikala.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />Прибытие в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Тбилиси</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Вас встретит водитель компании и отвезет в гостиницу. Заезд в гостиницу в 12:00. Вас ждет экскурсия с гидом по Тбилиси - столице Грузии.</span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">В этот день запланировано посещение православного кафедрального собора</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sameba.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Цминда Самеба</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, являющегося сегодня одним из самых больших православных соборов мира. Во время экскурсии вы посетите</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/metekhi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">церковь Метехи</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">и памятник легендарному основателю Тбилиси царю Вахтангу Горгасали, подниметесь по канатной дороге на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/narikala.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">крепость Нарикала</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, откуда вам откроется захватывающая дух панорама города. Спустившись к району серных бань, в которых любили бывать Пушкин и Дюма, и, пройдя мимо единственной мечети в Тбилиси, вы окажитесь среди уютных кафе и сувенирных лавок уголка Шарден, любимого места горожан и гостей столицы. Узкие улочки, винные погреба и картинные галереи хранят дух Старого Тбилиси и ведут к</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sioni.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">храму Сиони</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, названного в честь Сионской горы,</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/anchiskhati.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">базилике Анчисхати</span></span></span></span></span></a><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> VI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> века и уникальной часовой башне</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rezo-gabriadze-theatre.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Театра Кукол Резо Габриадзе</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 8 &ndash; 9 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Утром выезд из Тбилиси в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Мцхету</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, один из самых древних городов, культурный и религиозный центр Грузии. В настоящее время город объявлен объектом Всемирного наследия ЮНЕСКО и является живым музеем со многочисленными архитектурными и историческими памятниками. Посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/dzhavari.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">церкви Джвари</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(храм &laquo;Святого Креста&raquo;), построенного в </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">VI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> в. Находясь на высоком холме, мы увидим слияние двух величественных рек Арагви и Мтквари. Посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/svetitskhoveli.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">собора Светицховели</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(</span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">XI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> в).</span></span></span></span></span><br />\r\n<span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Продолжаем исследовать провинцию Хеви с посещения архитектурного комплекса</span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/ananuri-fortress.htm\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Ананури</span></span></span></span></a><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, который раньше был резиденцией герцогов в регионе Аргвети. Комплекс церквей и башен, относящиеся </span></span></span></span><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">XVI</span></span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">-</span></span></span></span><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">XVII</span></span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> вв, красиво возвышается над водохранилищем Жинвали. Далее едем по Военно-грузинской дороге, проходящей через самую высокую точку 2395 м над уровнем моря. По пути сделаем остановку у минерального источника, бегущего вниз от вершины. Прибытие в Казбеги, небольшой город с видом на гору Казбек (или Мкинвари) 5033 м., седьмой по высоте пик в Кавказских горах. Поднимитесь на внедорожнике к Троицкой церкви в Гергети, расположенной на высоте 2170 м под горой Казбеги. Возвращение в</span></span></span></span> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Тбилиси. Ночь в гостинице.</span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Кварели &ndash; Цинандали &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 7 &ndash; 8 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Утром выезд из Тбилиси в регион Кахетии. Кахетия расположена в восточной части Грузии и известна как колыбель грузинского вина. Здесь в долине Алазань выращены уникальные виды виноградов, такие как Киндзмараули, Манави, Саперави. Посещение женского монастыря</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/bodbe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Бодбе</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(19 в), место захоронения Св. Нино, которая принесла христианство в Грузию в 4 в. Далее направляемся в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/signakhi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Сигнахи</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, небольшой город, расположенный на вершине холма, откуда можно увидеть прекрасный вид реки Алазани и горный хребет Кавказа. Дегустация вин в винном погребе &ldquo;Хареба&rdquo;. Первоначально построенный как военный тоннель, теперь это один из самых больших винных погребов в Грузии с протяженностью 7.7 км. Посещение резиденции одной из самых любимых и известных грузинских семей &ndash;</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/tsinandali.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">принцы Чавчавадзе</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Возвращение в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 6 &ndash; 7 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Утром выезд из Тбилиси в город</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Гори</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori/stalin.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">музея Иосифа Сталина</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, где можно увидеть дом, в котором он родился, и <img alt=\"\" src=\"/uploads/images/turebi/stalin.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 195px;\" />его частный бронированный вагон поезда. Далее едем в Уплисцихе (&ldquo;Крепость Владыки&rdquo;), высеченный из камня город, относящийся к эллинистическому периоду.</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori/uplistsikhe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Уплисцихе</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">расположен на левом берегу реки Мтквари и известен уникальной комбинацией различных стилей вырезанных в скале культур и сосуществования языческой и христианской архитектур. Прибытие в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Дманиси &ndash; Болниси &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 6 -8 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">С утра выезд из Тбилиси. Посещение городка</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/dmanisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Дманиси</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. В 9 веке Дманиси был центром арабского государства Думанисский эмират и важным торговым центром на Великом Шелковом пути. При раскопках в конце прошлого &ndash; начале нынешнего века, на территории города был обнаружен череп, предположительно принадлежащий первому человеку в Европе (1,6 млн. лет). Далее посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/rustavi/bolnisi-sion.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Болнисского Сиона</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, построенного в 478-493 гг. Это самая старая церковь в Грузии, сохранившаяся до наших дней. Собор также известен древними надписями, упоминающими имена епископа Давида Болнисского, а также двух сасанидских царей &ndash; Пероза </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">I</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> и Кавадха </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">I</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Эти надписи &ndash; один из древнейших исторических документов, подтверждающих существование грузинского языка в то время. Прибытие в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Батуми</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Переезд в город Батуми.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Обзорная экскурсия по Батуми.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность экскурсии : 2 ч.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батуми &ndash; один из древнейших городов Грузии, который согласно историческим источникам, был основан еще до нашей эры. Богатое историческое прошлое в значительной степени обусловило архитектурное разнообразие города.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В сегодняшнем Батуми, помимо старых, исторических строений, есть и много новых, сделанных в современном стиле: Алфавитная башня, Дом Юстиции, рестораны и гостиницы необычных форм и др.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В самом&nbsp; сердце старого Батуми находятся площадь Европы и &laquo; Пьяцца &raquo;, окруженные центральными улицами и диковинными зданиями.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батумский приморский парк занимает территорию длиной в 7 км.&nbsp; Бульвар украшают бунгало, кафе &ndash; лаунджи, рестораны, детские аттракционы, скамейки современного дизайна, скульптуры и новые фонтаны. Аллеи приморского парка отличаются необычным сочетанием вечнозеленых растений, магнолий и пальм. Бульвар всегда был источником многих творческих начинаний. И сегодня он является своеобразной гаванью для оригинальных творческих идей и современных скульптурных композиций: &laquo; Я, ты и Батуми &raquo;, &laquo; Нино и Али &raquo;, &laquo; Женщина с велосипедом &raquo;, &laquo; Статуи влюбленных &raquo;, или признанные символом города , скульптуры &laquo; Дельфины &raquo; - делают Батумский бульвар более притягательным и эффектным.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в отеле Батуми.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b><b> </b><b> </b><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батуми &ndash; Гонио - Махунцети ( Горная Аджария ) &ndash; Батуми</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия в горную Аджарию.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 4 - 6 ч.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%93%D0%BE%D0%BD%D0%B8%D0%BE2.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />В 12 км к югу от Батуми находится крепость Гонио &ndash; Апсарос, а в ее окрестностях &ndash; одно из древнейших поселений Грузии. В прошлом крепость Гонио &ndash; Апсарос имела уникальное стратегическое значение: она защищала подходы к ущельям рек Чорохи и Ачарисцкали, которые связывали внутренние регионы Юго &ndash; Западной Грузии с Черноморским побережьем. Крепость Гонио была опорной цитаделью на Черноморском побережье сначала Византийской, а затем и Османской империй. Соответственно, крепость включает и несколько культурных пластов : римский&nbsp; (</span></span></span><span style=\"line-height:115%\"><span arial=\"\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> - </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. до н. э. ), византийский ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ) и османский ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. )</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Стратегическое расположение Аджарии на перекресте торговых путей ( Северный Кавказ- Малая Азия, Турция &ndash; Самцхе &ndash; Артануджи и так далее ) обусловило необходимость поддержания в надлежащем состоянии существующих дорог и мостов. Сохранившиеся в Аджарии древние арочные каменные мосты являются важнейшими памятниками грузинской материальной культуры. В результате последних исследований выявлено около 25 сохранившихся в Аджарии арочных мостов, строительство которых, согласно устным сказаниям, связано с именем царицы Тамар.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В селе Махунцети расположен мост&ndash;один из самых крупных арочных мостов Аджарии. Он построен из скальных камней вулканического происхождения, соединенных между собой известковым раствором. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">По дороге Вы увидете самую длинную реку Аджарии &ndash; Аджарисцкали и&nbsp; ее слияние с Чорохи, а также горный водопад Махунцети с кристально чистой водой.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в отеле Батуми.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Батуми</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Батуми<img alt=\"\" src=\"/uploads/images/turebi/batumi4321.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" /></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><b><span lang=\"RU\"><span style=\"line-height:115%\">Отъезд</span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер в аэропорт Батуми.</span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%93%D0%BE%D0%BD%D0%B8%D0%BE%20pat.jpg', '/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B8.jpg,/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B834.jpeg,/uploads/images/turebi/batumi312.jpg,/uploads/images/turebi/batumi543.jpg,/uploads/images/turebi/yazbegi5.jpg,/uploads/images/turebi/yazbegi3.jpg,/uploads/images/turebi/%D0%93%D0%BE%D0%BD%D0%B8%D0%BE3.jpg,/uploads/images/turebi/%D0%93%D0%BE%D0%BD%D0%B8%D0%BE2.jpg,/uploads/images/turebi/%D0%93%D0%BE%D0%BD%D0%B8%D0%BE.jpg,/uploads/images/turebi/batumi32.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B81.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B82.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20324.jpg,/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B83.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B8.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(160, '10-дней-9-ночей-1', '10-дней-9-ночей-1', '10-дней-9-ночей-1', '16', NULL, '10 дней / 9 ночей', '10 дней / 9 ночей', '10 дней / 9 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Мцхета &ndash; Уплисцихе&nbsp; - Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. <strong><span style=\"background:white\"><span arial=\"\"><span style=\"color:#4f5d6e\">Тбилиси &ndash; Ананури - Казбеги &ndash; Тбилиси</span></span></span></strong></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5.&nbsp; </span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Батуми ( переезд&nbsp; на поезде )</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Батуми </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\"> Батуми</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\"> Батуми</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Отъезд</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Прибытие</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Прибытие в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Тбилиси</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Вас встретит водитель и отвезет в гостиницу. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">После завтрака вы посетите</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/metekhi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">церковь Метехи</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">и памятник легендарному основателю Тбилиси царю Вахтангу Горгасали. <img alt=\"\" src=\"/uploads/images/turebi/abanotubani12.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />Затем подниметесь по канатной дороге на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/narikala.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">крепость Нарикала</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, откуда вам откроется захватывающая дух панорама Тбилиси. Крепость и ее окрестности считались важнейшим пунктом торговли и местом отдыха на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/silkroad/index.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Великом Шелковом пути</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Спустившись к району</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sulfuric-baths.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">серных бань</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, и пройдя мимо единственной мечети в Тбилиси, вы окажитесь среди уютных кафе и сувенирных лавок района Шарден. Узкие улочки с винными погребами и картинными галереями хранят дух Старого Тбилиси. Они приведут вас к</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sioni.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">храму Сиони</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(назван в честь Сионской горы), &ldquo;Царскому караван-сараю&rdquo; (1672 г.),</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/anchiskhati.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">базилике Анчисхати</span></span></span></span></span></span></a><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> VI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> века и уникальной часовой башне</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rezo-gabriadze-theatre.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Театра Кукол Резо Габриадзе</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Пройдя по пешеходному</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/bridge-of-peace.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Мосту Мира</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">на левый берег Куры, вы побываете в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rike-park.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">парке причудливых скульптур Рике</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Мцхета &ndash; Уплисцихе&nbsp; - Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">После завтрака вы отправитесь в древнюю столицу Грузии &ndash; город</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Мцхета</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. По пути поднимитесь к</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/dzhavari.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">монастырю Джвари</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, откуда открывается сказочный вид на сам древний город и слияние двух рек, Арагви и Мтквари (Кура). Далее вас ждёт посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/svetitskhoveli.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">собора Светицховели</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">в Мцхете. Тур продолжится дегустацией домашнего вина и обеда в традиционном грузинском домике в селе Чардахи. После обеда вы отправитесь в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori/uplistsikhe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Уплисцихе</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; город, вырубленный в скалах в первой половине первого тысячелетия до нашей эры. На протяжении многих веков Уплисцихе был точкой пересечения множества торговых маршрутов на Великом Шелковом пути: в самый пик расцвета в городе проживало более 20 000 человек, он был центром языческой культуры в регионе.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span><strong><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси &ndash; Ананури - Казбеги &ndash; Тбилиси</span></span></span></span></strong></span></p>\r\n\r\n<p style=\"margin-top:0in; margin-right:0in; margin-bottom:5.75pt; margin-left:0in\"><span style=\"font-size:14px;\"><span style=\"background:white\"><span lang=\"RU\"><span arial=\"\"><span style=\"color:#4f5d6e\">В 09:00 утра наш гид и водитель встретят вас в гостинице. Наше путешествие пройдет по Военно-Грузинской дороге в сторону границы с Россией. Это<img alt=\"\" src=\"/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" /> старейшая и важнейшая транспортная артерия уже несколько столетий соединяет Россию и страны Закавказья. По пути мы посетим</span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/ananuri-fortress.htm\"><span lang=\"RU\"><span arial=\"\"><span style=\"color:#0091cc\">архитектурный комплекс Ананури</span></span></span></a> <span lang=\"RU\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; бывшую резиденцию князей Арагветских. Церкви и средневековые башни Ананури величественно возвышаются над озером Жинвали, обнимаемым склонами Кавказских гор.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:0in; margin-right:0in; margin-bottom:5.75pt; margin-left:0in\"><span style=\"font-size:14px;\"><span style=\"background:white\"><span lang=\"RU\"><span arial=\"\"><span style=\"color:#4f5d6e\">Мы продолжаем тур остановкой на месте слияния двух горных рек у села Пасанаури. Особенность этого места заключается в том, что каждая из двух рек имеет свой собственный цвет и при слиянии воды двух рек еще долгое время текут параллельно друг другу не смешиваясь. Наш путь продолжится через Крестовый перевал с остановкой на панорамной смотровой площадке советского периода &quot;Дружба Народов&quot;, откуда открывается живописный вид на глубокое ущелье Кавказского хребта.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:0in; margin-right:0in; margin-bottom:5.75pt; margin-left:0in\"><span style=\"font-size:14px;\"><span style=\"background:white\"><span lang=\"RU\"><span arial=\"\"><span style=\"color:#4f5d6e\">По прибытии в</span></span></span> <a href=\"https://www.advantour.com/rus/georgia/stepantsminda.htm\"><span lang=\"RU\"><span arial=\"\"><span style=\"color:#0091cc\">Казбеги</span></span></span></a> <span lang=\"RU\"><span arial=\"\"><span style=\"color:#4f5d6e\">мы сменим машину на внедорожник и поднимемся к</span></span></span> <a href=\"https://www.advantour.com/rus/georgia/stepantsminda/gergeti-trinity-church.htm\"><span lang=\"RU\"><span arial=\"\"><span style=\"color:#0091cc\">Троицкой церкви Гергети</span></span></span></a><span lang=\"RU\"><span arial=\"\"><span style=\"color:#4f5d6e\">, расположенной на высоте 2170 метров над уровнем моря. В хорошую погоду здесь можно насладиться завораживающим видом знаменитой горы Казбек. Спуск и возвращение в Тбилиси по Военно-Грузинской дороге.</span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Путешествие начнем </span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">в город любви</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/sighnaghi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Сигнахи</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">,</span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">который в средние века был одним из важнейших торговых пунктов на Великом Шелковом Пути, окруженным крепостной стеной с 23 башнями. Прогулка по городу, возвращение в Тбилиси. По пути остановка в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/bodbe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">монастырском комплексе Бодбе</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, где, согласно легенде, похоронена Св. Нино, привнёсшая христиантсов в Грузию в четвертом веке. По указу Императора Александра </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">III</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> с 1889 года Бодбе функционирует как женский монастырь. Прибытие в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Батуми ( переезд&nbsp; на поезде )</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Прибытие в Батуми. Вас встретит водитель и отвезет в гостиницу.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Размещение в отеле.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Отдых.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Батуми<img alt=\"\" src=\"/uploads/images/turebi/batumi312.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" /></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Завтрак в отеле.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Пешая обзорная экскурсия по Батуми.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 2 ч.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Батуми</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Батуми</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Отъезд</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер на ж /д&nbsp; вокзал.&nbsp; </span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/batumi%20pat6.png', '/uploads/images/turebi/Batumi1.jpg,/uploads/images/turebi/batumi4321.jpg,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B065.jpg,/uploads/images/turebi/Uplistsikhe1.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B845.jpg,/uploads/images/turebi/yazbegi12.jpg,/uploads/images/turebi/yazbegi3.jpg,/uploads/images/turebi/yazbegi4.jpg,/uploads/images/turebi/batumi32.jpg,/uploads/images/turebi/batumi65.jpg,/uploads/images/turebi/batumi98.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B84.jpg,/uploads/images/turebi/tbilisi456543.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(161, '10-дней-9-ночей-1-2', '10-дней-9-ночей-1-2', '10-дней-9-ночей-1-2', '16', NULL, '10 дней / 9 ночей', '10 дней / 9 ночей', '10 дней / 9 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА&nbsp; </span></span></span></b><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">( </span></span></i></b><b><i><span style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span style=\"font-family:\" sylfaen=\"\">I</span></span></span></i></b><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span style=\"font-family:\" sylfaen=\"\"> вариант </span></span></span></i></b><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">)</span></span></i></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Кварели &ndash; Цинандали &ndash; Тбилиси</span></span> </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. <strong><span style=\"background:white\"><span arial=\"\"><span style=\"font-weight:normal\">Тбилиси &ndash; Ананури &ndash; Уплисцихе &ndash; Тбилиси</span></span></span></strong></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Тбилиси &ndash; Дманиси &ndash; Болниси &ndash; Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Тбилиси &ndash; Боржоми &ndash; Ахалцихе &ndash; Вардзия _ Тбилиси</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>&nbsp;</b><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">Тбилиси</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Отъезд</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Прибытие в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Тбилиси</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Вас встретит водитель и отвезет в гостиницу. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8%20pat.jpg\" style=\"margin: 12px; float: left; width: 290px; height: 295px;\" />После завтрака вы посетите</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/metekhi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">церковь Метехи</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">и памятник легендарному основателю Тбилиси царю Вахтангу Горгасали. Затем подниметесь по канатной дороге на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/narikala.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">крепость Нарикала</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, откуда вам откроется захватывающая дух панорама Тбилиси. Крепость и ее окрестности считались важнейшим пунктом торговли и местом отдыха на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/silkroad/index.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Великом Шелковом пути</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Спустившись к району</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sulfuric-baths.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">серных бань</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, и пройдя мимо единственной мечети в Тбилиси, вы окажитесь среди уютных кафе и сувенирных лавок района Шарден. Узкие улочки с винными погребами и картинными галереями хранят дух Старого Тбилиси. Они приведут вас к</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sioni.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">храму Сиони</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(назван в честь Сионской горы), &ldquo;Царскому караван-сараю&rdquo; (1672 г.),</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/anchiskhati.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">базилике Анчисхати</span></span></span></span></span></span></a><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> VI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> века и уникальной часовой башне</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rezo-gabriadze-theatre.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Театра Кукол Резо Габриадзе</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Пройдя по пешеходному</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/bridge-of-peace.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Мосту Мира</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">на левый берег Куры, вы побываете в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rike-park.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">парке причудливых скульптур Рике</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">После завтрака выезд в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Мцхета</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, один из древнейших городов Грузии. С </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">III</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> в.н.э. Мцхета являлась столицей Восточного Грузинского царства Иверия, а сегодня это культурный и духовный центр страны. Посещение живописного</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/dzhavari.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">монастыря Джвари</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, воспетого М.Ю.Лермонтовым в поэме &laquo;Мцыри&raquo;. С холма, на котором расположен монастырь, открываются захватывающие дух виды на слияние двух рек Арагвы и Куры и</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/svetitskhoveli.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">храм Светицховели</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, религиозный центр города, где хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Возвращение в Тбилиси.</span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Кварели &ndash; Цинандали &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 7 &ndash; 8 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Утром выезд из Тбилиси в регион Кахетии. Кахетия расположена в восточной части Грузии и известна как колыбель грузинского вина. Здесь в долине Алазань выращены уникальные виды виноградов, такие как Киндзмараули, Манави, Саперави. <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 146px;\" />Посещение женского монастыря</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/bodbe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Бодбе</span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(19 в), место захоронения Св. Нино, которая принесла христианство в Грузию в 4 в. Далее направляемся в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/signakhi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Сигнахи</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, небольшой город, расположенный на вершине холма, откуда можно увидеть прекрасный вид реки Алазани и горный хребет Кавказа. Дегустация вин в винном погребе &ldquo;Хареба&rdquo;. Первоначально построенный как военный тоннель, теперь это один из самых больших винных погребов в Грузии с протяженностью 7.7 км. Посещение резиденции одной из самых любимых и известных грузинских семей &ndash;</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/telavi/tsinandali.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">принцы Чавчавадзе</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Возвращение в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. <strong><span style=\"background:white\"><span arial=\"\">Тбилиси &ndash; Ананури &ndash; Уплисцихе &ndash; Тбилиси</span></span></strong></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:0in; margin-right:0in; margin-bottom:5.75pt; margin-left:0in\"><span style=\"font-size:14px;\"><span style=\"background:white\"><span lang=\"RU\"><span arial=\"\"><span style=\"color:#4f5d6e\">В 09:00 утра наш гид и водитель встретят вас в гостинице. Наше путешествие пройдет по Военно-Грузинской дороге в сторону границы с Россией. Это старейшая и важнейшая транспортная артерия уже несколько столетий соединяет Россию и страны Закавказья. По пути мы посетим</span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/ananuri-fortress.htm\"><span lang=\"RU\"><span arial=\"\"><span style=\"color:#0091cc\">архитектурный комплекс Ананури</span></span></span></a> <span lang=\"RU\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; бывшую резиденцию князей Арагветских. Церкви и средневековые башни Ананури величественно возвышаются над озером Жинвали, обнимаемым склонами Кавказских гор.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">После обеда вы отправитесь в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori/uplistsikhe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Уплисцихе</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; город, вырубленный в скалах в первой половине первого тысячелетия до нашей эры. На протяжении многих веков Уплисцихе был точкой пересечения множества торговых маршрутов на Великом Шелковом пути: в самый пик расцвета в городе проживало более 20 000 человек, он был центром языческой культуры в регионе.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6. </span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;<b>Тбилиси</b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Тбилиси &ndash; Дманиси &ndash; Болниси &ndash; Тбилиси</span></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Продолжительность тура : 6 -8 ч.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%91%D0%BE%D0%BB%D0%BD%D0%B8%D1%81%D0%B8.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />С утра выезд из Тбилиси. Посещение городка</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/dmanisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Дманиси</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. В 9 веке Дманиси был центром арабского государства Думанисский эмират и важным торговым центром на Великом Шелковом пути. При раскопках в конце прошлого &ndash; начале нынешнего века, на территории города был обнаружен череп, предположительно принадлежащий первому человеку в Европе (1,6 млн. лет). Далее посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/rustavi/bolnisi-sion.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\">Болнисского Сиона</span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, построенного в 478-493 гг. Это самая старая церковь в Грузии, сохранившаяся до наших дней. Собор также известен древними надписями, упоминающими имена епископа Давида Болнисского, а также двух сасанидских царей &ndash; Пероза </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">I</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> и Кавадха </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">I</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Эти надписи &ndash; один из древнейших исторических документов, подтверждающих существование грузинского языка в то время. Прибытие в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: <span style=\"color:#333333\">Тбилиси &ndash; Боржоми &ndash; Ахалцихе &ndash; Вардзия _ Тбилиси</span></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Выезд в сторону города Ахалцихе. По пути остановка в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/borjomi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Боржоми</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; одном из самых известных мест на территории Грузии. Прогулка по Боржомскому Национальному Парку.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/akhaltsikhe/rabat-fortress.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">крепости Рабат</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">в Ахалцихе. В стенах крепости расположены церковь, мечеть, синагога, небольшой парк, <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/vardzia%20pat1.jpg\" style=\"margin: 12px; float: right; width: 290px; height: 295px;\" />исторический музей, различные магазины, гостиница и ЗАГС. Остановка у</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/vardzia/khartsvisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">крепости Хертвиси</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, которая уже в 10 веке была известна как одна из стратегических цитаделей на торговых маршрутах Шелкового пути. Далее вы посетите</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/akhalkalaki/vardzia.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Вардзию</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; уникальный пещерный город, в котором до наших дней сохранился древний монастырь и настенные росписи </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">XII</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> века. Возвращение вТбилиси.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: </span></span></span><span style=\"line-height:115%\"><span arial=\"\">Отъезд</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер в а/порт.&nbsp; </span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/abanot%20pat.jpg', '/uploads/images/turebi/tbilisi86.jpg,/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B834.jpeg,/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B8.jpg,/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B823.jpg,/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B84.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%BB%D0%BD%D0%B8%D1%81%D0%B8%20%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B8%2021.jpg,/uploads/images/turebi/abanotubani12.jpg,/uploads/images/turebi/abanot.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%2012.jpg,/uploads/images/turebi/vardzia26.jpg,/uploads/images/turebi/tbilisi543.jpg,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpeg,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0223.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(162, '10-дней-9-ночей-1-2-3', '10-дней-9-ночей-1-2-3', '10-дней-9-ночей-1-2-3', '16', NULL, '10 дней /9 ночей', '10 дней /9 ночей', '10 дней /9 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА&nbsp; </span></span></span></b><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">( </span></span></i></b><b><i><span style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span style=\"font-family:\" sylfaen=\"\">II</span></span></span></i></b><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span style=\"font-family:\" sylfaen=\"\"> вариант </span></span></span></i></b><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">)</span></span></i></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. <b>Прибытие &ndash; Тбилиси</b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <b>Тбилиси</b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <b><span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></span></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <b><span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси </span></span></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. <b><span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Тбилиси</span></span></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6. <span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Уплисцихе - Боржоми &ndash; Ахалцихе</span></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Ахалцихе &ndash; Рабат &ndash; Вардзия&nbsp; &ndash; Тбилиси</span></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: <span style=\"color:#333333\">Тбилиси </span></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>&nbsp;</b><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Отъезд</span></span></span></span></b></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Прибытие в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Тбилиси</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Вас встретит водитель и отвезет в гостиницу. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"><img alt=\"\" src=\"/uploads/images/turebi/abanotubani12.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />После завтрака вы посетите</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/metekhi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">церковь Метехи</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">и памятник легендарному основателю Тбилиси царю Вахтангу Горгасали. Затем подниметесь по канатной дороге на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/narikala.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">крепость Нарикала</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, откуда вам откроется захватывающая дух панорама Тбилиси. Крепость и ее окрестности считались важнейшим пунктом торговли и местом отдыха на</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/silkroad/index.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Великом Шелковом пути</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Спустившись к району</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sulfuric-baths.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">серных бань</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, и пройдя мимо единственной мечети в Тбилиси, вы окажитесь среди уютных кафе и сувенирных лавок района Шарден. Узкие улочки с винными погребами и картинными галереями хранят дух Старого Тбилиси. Они приведут вас к</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/sioni.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">храму Сиони</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">(назван в честь Сионской горы), &ldquo;Царскому караван-сараю&rdquo; (1672 г.),</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/anchiskhati.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">базилике Анчисхати</span></span></span></span></span></span></a><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> VI</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> века и уникальной часовой башне</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rezo-gabriadze-theatre.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Театра Кукол Резо Габриадзе</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Пройдя по пешеходному</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/bridge-of-peace.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Мосту Мира</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">на левый берег Куры, вы побываете в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/tbilisi/rike-park.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">парке причудливых скульптур Рике</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси &ndash; Мцхета &ndash; Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">После завтрака выезд в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Мцхета</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, один из древнейших городов Грузии. С </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">III</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> в.н.э. Мцхета являлась столицей Восточного Грузинского царства Иверия, а сегодня это культурный и духовный центр страны. Посещение живописного</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/dzhavari.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">монастыря Джвари</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, воспетого М.Ю.Лермонтовым в поэме &laquo;Мцыри&raquo;. С холма, на котором расположен монастырь, открываются захватывающие дух виды на слияние двух рек Арагвы и Куры и</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/mtskheta/svetitskhoveli.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">храм Светицховели</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, религиозный центр города, где хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Возвращение в Тбилиси.</span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <b><span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси</span></span></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> <b><span style=\"background:whitesmoke\"><span style=\"color:#333333\">Тбилиси &ndash; Бодбе &ndash; Сигнахи &ndash; Тбилиси</span></span></b> </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Путешествие начнем </span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">в город любви</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/sighnaghi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Сигнахи</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">,</span></span></span></span></span> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">который в средние века был одним из важнейших торговых пунктов на Великом Шелковом Пути, окруженным крепостной стеной с 23 башнями. Прогулка по городу, возвращение в Тбилиси. По пути остановка в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/bodbe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">монастырском комплексе Бодбе</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, где, согласно легенде, похоронена Св. Нино, привнёсшая христиантсов в Грузию в четвертом веке. По указу Императора Александра </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">III</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> с 1889 года Бодбе функционирует как женский монастырь. Прибытие в Тбилиси. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 6</span></span><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: <span style=\"color:#333333\">Тбилиси &ndash; Уплисцихе - Боржоми &ndash; Ахалцихе</span></span></span></span></span></b><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/uflists%20pat.jpg\" style=\"margin: 12px; float: right; width: 290px; height: 295px;\" /></span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">После завтрака вы отправитесь в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/gori/uplistsikhe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Уплисцихе</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; город, вырубленный в скалах в первой половине первого тысячелетия до нашей эры. На протяжении многих веков Уплисцихе был точкой пересечения множества торговых маршрутов на Великом Шелковом пути: в самый пик расцвета в городе проживало более 20 000 человек, он был центром языческой культуры в регионе </span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">.По пути остановка в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/borjomi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Боржоми</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; одном из самых известных мест на территории Грузии. Прогулка по Боржомскому Национальному Парку. Прибытие в</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/akhaltsikhe.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Ахалцихе</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. Ночь в гостинице.</span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 7</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">:</span></span></span></span></b> <b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Ахалцихе &ndash; Рабат &ndash; Вардзия&nbsp; &ndash; Тбилиси</span></span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Утром посещение</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/akhaltsikhe/rabat-fortress.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">крепости Рабат</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">в Ахалцихе. В стенах крепости расположены церковь, мечеть, синагога, небольшой парк, исторический музей, различные магазины, гостиница и ЗАГС. Остановка у</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/vardzia/khartsvisi.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">крепости Хертвиси</span></span></span></span></span></span></a><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">, которая уже в 10 веке была известна как одна из стратегических цитаделей на торговых маршрутах Шелкового пути. Далее вы посетите</span></span></span></span></span> <a href=\"https://www.advantour.com/rus/georgia/akhalkalaki/vardzia.htm\"><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#0091cc\"><span style=\"text-decoration:none\">Вардзию</span></span></span></span></span></span></a> <span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">&ndash; уникальный пещерный город, в котором до наших дней сохранился древний монастырь и настенные росписи </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">XII</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\"> века. </span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Возвращение в</span></span></span></span><span lang=\"RU\"><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">Тбилиси</span></span></span></span></span><span style=\"background:white\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#4f5d6e\">. </span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 8</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\"><span style=\"color:#333333\">Свободный отдых.</span></span></span></span></span></i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 9</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Тбилиси</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%BD%D0%B0%D1%86%D0%B8%D0%BE%D0%BD%D0%B0%D0%BB%D1%8C%D0%BD%D0%BE%D0%B3%D0%BE%20%D0%BC%D1%83%D0%B7%D0%B5%D1%8F..jpg\" style=\"margin: 12px; float: right; width: 300px; height: 199px;\" />Посещение национального музея.&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Подъем на Святую гору Мама Давити.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Прогулка по парку.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ужин в национальном ресторане.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 10</span></span></span></b><b><span lang=\"RU\"><span style=\"background:whitesmoke\"><span style=\"line-height:115%\"><span arial=\"\">: Отъезд</span></span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер в а/порт.&nbsp; </span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%91%D0%BE%D0%B4%D0%B1%D0%B5%20pat.jpg', '/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8212.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B84.jpg,/uploads/images/turebi/abanot.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B822.png,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B824.jpeg,/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B82.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/bodbis%20monasteri2.jpg,/uploads/images/turebi/bodbis%20monasteri.jpg,/uploads/images/turebi/upliscixe(1).jpg,/uploads/images/turebi/%D0%A3%D0%BF%D0%BB%D0%B8%D1%81%D1%86%D0%B8%D1%85%D0%B5.jpg,/uploads/images/turebi/vardzia23.jpg,/uploads/images/turebi/vardzia26.jpg,/uploads/images/turebi/narikala.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(163, '5-дней-4-ночи-1-2-3', '5-дней-4-ночи-1-2-3', '5-дней-4-ночи-1-2-3', '14', NULL, '5 дней / 4 ночи', '5 дней / 4 ночи', '5 дней / 4 ночи', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси &ndash; Мцхета - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси &ndash; Боржоми &ndash; Вардзия &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси &ndash; Алаверди &ndash; Икалто &ndash; Греми &ndash; Кварели - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Отъезд &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1. Прибытие &ndash; Тбилиси&nbsp;&nbsp;&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Встреча гостей в аэропорту и трансфер в отель выбранной категории.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Размещение в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.Тбилиси / Мцхета &ndash; Тбилиси&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i>&laquo; По двум столицам &raquo;</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь,</span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3. Тбилиси &ndash; Боржоми &ndash; Вардзия &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Поездка в Самцхе &ndash; Джавахети.<span style=\"color:#002060\">&laquo; <b><i>НАСЛЕДИЕ ЗОЛОТОГО ВЕКА</i></b> &raquo;</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 8 &ndash; 10 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">С древних времен <b>Джавахети</b> был одним из эриставств ( княжеств ) Картлинского царства. Географическое расположение Месхети создавало выгодные условия для культурного и экономического развития страны. Оно способствовало установить связь между старыми культурами известных на территории Грузии и с центрами цивилизаций Ближнего Востока.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Период царствования Царицы Тамар ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. ) считался Золотым веком Грузии. В этот период начались большие строительства. Среди них самым удивительным считается Вардзия &ndash; наш последний пункт <img alt=\"\" src=\"/uploads/images/turebi/Borjomi987.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />по этому маршруту.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">По дороге к высеченному городу в скале, Вы посетите курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжая путешествие, Вы посетите высеченный в скале комплекс<b> Вардзия</b> &ndash; один из важнейших и впечатляющих исторических памятников Грузии. Вардзия &ndash; памятник&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, предназначенный для военной крепости и грандиозный укрепленный монастырь.Комплекс Вардзия простирается на 500 м и состоит из 13 этажей, который связывают друг с другом сложная система тоннелей.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наш гид повествует Вам историю строительства этого великолепного памятника, который сохранил следы многих трагических событий. Вы окунетесь во времена царствования&nbsp; великой царицы Тамары, узнаете много исторических фактов и легенд, связанных с ней и на мгновенье представите мир ее глазами...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси &ndash; Алаверди &ndash; Икалто &ndash; Греми &ndash; Кварели &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия в Кахетию </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 8 &ndash; 9 ч</span></span></span></i></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Кахетия &ndash; один из самых больших регионов в Восточной Грузии. Некогда самостоятельное царство. Царство бесконечных виноградников и живописных гор, старинных традиций и загадочных мертвых городов. Царство самых вкусных грузинских вин и спокойной печали древних монастырей.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наше путешествие начинается с <b>Гомборского перевала </b>( 2000 м над уровнем моря ) &ndash; царство лесных великанов. Высокие, прямоствольные деревья достигают толщины в несколько обхватов. Густые кроны, почти не пропуская лучей солнца, создают сплошной шатер и только светлые буковые стволы не дают этому &laquo; темному царству &raquo; погрузиться во мрак. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В центре Алазанской долины Вы посетите величайший храм средневековой Грузии &ndash; <b>Алавердский Кафедральный собор</b> ( Собор Святого Георгия, </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ) и монастырский комплекс. Храм, поражающий своим видом и великолепием, является одним из крупнейших соборов в Грузии, связанный с именем одного из тринадцати сирийских первооснователей грузинского монашества &ndash; Иосифом Алавердели.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Собор Алаверди представлен на включение в список Всемирного наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%98%D0%BA%D0%B0%D0%BB%D1%82%D0%BE.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Вы посетите <b>монастырь Икалто</b> &ndash; один из самых старых в Грузии и самых древних в Кахетии, основанный одним из тринадцати&nbsp; сирийских отцов Зеноном Икалтойским в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке. На территории монастыря три храма &ndash; церковь Святой Троицы ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), главный храм Божества ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), храм Успения Божьей Матери ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), здание Академии ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), вошедшая в историю тем, что здесь учился Шота Руставели ( поэт </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, автор эпической поэмы &laquo; Витязь в тигровой шкуре &raquo;). В древние времена Икалто был крупной духовной школой и образовательным центром.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Монастырь Икалто также находится на рассмотрении о включении его в список ЮНЕСКО. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетим город <b>Греми</b>, престольный город богатого и успешного Кахетинского царства ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ). В комплекс входят : церковь Архангелов, царская палата с колокольней, часовня св. Георгия с башней, на горе Самеба &ndash; круглая часовня Святой Троицы. В Греми Вам повествуют историю жизни кахетинских царей и цариц, о роли каждого из них в жизни города и царства в целом. Увидите надземные и подземные элементы городских строений или урбанской инфраструктуры.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В заключение посетим компанию <b>&laquo; Виноделие Хареба &raquo; </b>- один из лидирующих заводов в винодельческом ремесле Грузии. Два винных завода компании занимают площадь в 2,5 Га. Склад вина построен в виде тоннеля и является национальной достопримечательностью, называемой <b>&laquo; Кварельский туннель &raquo;</b>. В нем под землей на территории 8 км хранится около 25 тысяч бутылок вина.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Дегустация вин ( 2 бокала ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Возвращение в Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Отъезд &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер в аэропорт</span></span></span></span></p>\r\n', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:22.0pt\"><span style=\"line-height:115%\"><span style=\"color:#0070c0\">ПРОГРАММА&nbsp; ТУРА</span></span></span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Прибытие &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси &ndash; Мцхета - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси &ndash; Боржоми &ndash; Вардзия &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси &ndash; Алаверди &ndash; Икалто &ndash; Греми &ndash; Кварели - Тбилиси</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Отъезд &ndash; Тбилиси</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 1. Прибытие &ndash; Тбилиси&nbsp;&nbsp;&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Встреча гостей в аэропорту и трансфер в отель выбранной категории.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Размещение в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночевка в Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 2.Тбилиси / Мцхета &ndash; Тбилиси&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i>&laquo; По двум столицам &raquo;</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь,</span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b></span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира</b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Древняя столица Грузии ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">III</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. до н.э. &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">V</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">&nbsp;В конце </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; начале </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XV</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">II</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 3. Тбилиси &ndash; Боржоми &ndash; Вардзия &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Поездка в Самцхе &ndash; Джавахети.<span style=\"color:#002060\">&laquo; <b><i>НАСЛЕДИЕ ЗОЛОТОГО ВЕКА</i></b> &raquo;</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 8 &ndash; 10 ч.</span></span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">С древних времен <b>Джавахети</b> был одним из эриставств ( княжеств ) Картлинского царства. Географическое расположение Месхети создавало выгодные условия для культурного и экономического развития страны. Оно способствовало установить связь между старыми культурами известных на территории Грузии и с центрами цивилизаций Ближнего Востока.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Период царствования Царицы Тамар ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> &ndash; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XIII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> вв. ) считался Золотым веком Грузии. В этот период начались большие строительства. Среди них самым удивительным считается Вардзия &ndash; наш последний пункт <img alt=\"\" src=\"/uploads/images/turebi/Borjomi987.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />по этому маршруту.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">По дороге к высеченному городу в скале, Вы посетите курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжая путешествие, Вы посетите высеченный в скале комплекс<b> Вардзия</b> &ndash; один из важнейших и впечатляющих исторических памятников Грузии. Вардзия &ndash; памятник&nbsp; </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, предназначенный для военной крепости и грандиозный укрепленный монастырь.Комплекс Вардзия простирается на 500 м и состоит из 13 этажей, который связывают друг с другом сложная система тоннелей.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наш гид повествует Вам историю строительства этого великолепного памятника, который сохранил следы многих трагических событий. Вы окунетесь во времена царствования&nbsp; великой царицы Тамары, узнаете много исторических фактов и легенд, связанных с ней и на мгновенье представите мир ее глазами...</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 4.</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> Тбилиси &ndash; Алаверди &ndash; Икалто &ndash; Греми &ndash; Кварели &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Экскурсия в Кахетию </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Продолжительность тура : 8 &ndash; 9 ч</span></span></span></i></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Кахетия &ndash; один из самых больших регионов в Восточной Грузии. Некогда самостоятельное царство. Царство бесконечных виноградников и живописных гор, старинных традиций и загадочных мертвых городов. Царство самых вкусных грузинских вин и спокойной печали древних монастырей.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Наше путешествие начинается с <b>Гомборского перевала </b>( 2000 м над уровнем моря ) &ndash; царство лесных великанов. Высокие, прямоствольные деревья достигают толщины в несколько обхватов. Густые кроны, почти не пропуская лучей солнца, создают сплошной шатер и только светлые буковые стволы не дают этому &laquo; темному царству &raquo; погрузиться во мрак. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В центре Алазанской долины Вы посетите величайший храм средневековой Грузии &ndash; <b>Алавердский Кафедральный собор</b> ( Собор Святого Георгия, </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ) и монастырский комплекс. Храм, поражающий своим видом и великолепием, является одним из крупнейших соборов в Грузии, связанный с именем одного из тринадцати сирийских первооснователей грузинского монашества &ndash; Иосифом Алавердели.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Собор Алаверди представлен на включение в список Всемирного наследия ЮНЕСКО.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%98%D0%BA%D0%B0%D0%BB%D1%82%D0%BE.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Вы посетите <b>монастырь Икалто</b> &ndash; один из самых старых в Грузии и самых древних в Кахетии, основанный одним из тринадцати&nbsp; сирийских отцов Зеноном Икалтойским в </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> веке. На территории монастыря три храма &ndash; церковь Святой Троицы ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">VI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), главный храм Божества ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), храм Успения Божьей Матери ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">IX</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), здание Академии ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ), вошедшая в историю тем, что здесь учился Шота Руставели ( поэт </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XII</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> века, автор эпической поэмы &laquo; Витязь в тигровой шкуре &raquo;). В древние времена Икалто был крупной духовной школой и образовательным центром.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Монастырь Икалто также находится на рассмотрении о включении его в список ЮНЕСКО. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Посетим город <b>Греми</b>, престольный город богатого и успешного Кахетинского царства ( </span></span></span><span style=\"line-height:115%\"><span arial=\"\">XVI</span></span><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\"> в. ). В комплекс входят : церковь Архангелов, царская палата с колокольней, часовня св. Георгия с башней, на горе Самеба &ndash; круглая часовня Святой Троицы. В Греми Вам повествуют историю жизни кахетинских царей и цариц, о роли каждого из них в жизни города и царства в целом. Увидите надземные и подземные элементы городских строений или урбанской инфраструктуры.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">В заключение посетим компанию <b>&laquo; Виноделие Хареба &raquo; </b>- один из лидирующих заводов в винодельческом ремесле Грузии. Два винных завода компании занимают площадь в 2,5 Га. Склад вина построен в виде тоннеля и является национальной достопримечательностью, называемой <b>&laquo; Кварельский туннель &raquo;</b>. В нем под землей на территории 8 км хранится около 25 тысяч бутылок вина.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Дегустация вин ( 2 бокала ).</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Возвращение в Тбилиси.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Ночь в отеле Тбилиси.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">День 5</span></span></span></b><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">. Отъезд &ndash; Тбилиси&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Завтрак в отеле.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Освобождение номеров до 12:00.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\"><span arial=\"\">Трансфер в аэропорт</span></span></span></span></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B8%20pat.jpg', '/uploads/images/turebi/%D0%90%D0%BB%D0%B0%D0%B2%D0%B5%D1%80%D0%B4%D0%B8.jpg,/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B83.jpg,/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B834.jpeg,/uploads/images/turebi/mcxeta4534.jpg,/uploads/images/turebi/%D0%9A%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D0%B86.jpg,/uploads/images/turebi/vardzia23.jpg,/uploads/images/turebi/Borjomi1.jpg,/uploads/images/turebi/varZ.jpg,/uploads/images/turebi/vardzia2.jpg,/uploads/images/turebi/%D0%98%D0%BA%D0%B0%D0%BB%D1%82%D0%BE.jpg,/uploads/images/turebi/Borjomi987.jpg', '', 0, 1, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(164, '07-дней-06-ночей', '07-дней-06-ночей', '07-дней-06-ночей', '17', NULL, '07 дней / 06 ночей', '07 дней / 06 ночей', '07 дней / 06 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1. &nbsp;</b>Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2. </b>Тбилиси</p>\r\n\r\n<p><b>День 3. </b>Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4. </b>Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Гудаури</p>\r\n\r\n<p><b>День 5. </b>Гудаури &ndash; Гори &ndash; Уплисцихе - Боржоми &ndash; Бакуриани</p>\r\n\r\n<p><b>День 6. </b>Бакуриани &ndash; Ахалцихе &ndash; Вардзия &ndash; Тбилиси</p>\r\n\r\n<p><b>День 7.&nbsp; </b>Отъезд.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. &nbsp;Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси.</p>\r\n\r\n<p><b>Продолжительность тура:&nbsp; 3 &ndash; 5 ч.</b></p>\r\n\r\n<p>Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: right;\" />которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия во Внешнюю Кахетию.</p>\r\n\r\n<p><b>Продолжительность тура : 5 &ndash; 7 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ<b><i>. ( посещение музея за дополнительную плату )</i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Гудаури</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в бывшую столицу Грузии &ndash; Мцхета и путешествие по Военно &ndash; Грузинской дороге.</p>\r\n\r\n<p><b>Продолжительность тура:&nbsp; 7 &ndash; 8 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте <img alt=\"\" src=\"/uploads/images/turebi/sveticxoveli7.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Путешествие продолжим по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске. . <b><i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i></b>.</p>\r\n\r\n<p>Возвращение в Гудаури.</p>\r\n\r\n<p>Ночевка в отеле Гудаури.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Гудаури &ndash; Гори &ndash; Уплисцихе - Боржоми &ndash; Бакуриани</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 8 ч.</b></p>\r\n\r\n<p>По программе город<b> Гори</b> расположенный на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</p>\r\n\r\n<p>Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</p>\r\n\r\n<p>Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.</p>\r\n\r\n<p>В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>Далее Вы направитесь в курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</p>\r\n\r\n<p>Поездка в <b>Бакуриани</b>&nbsp; ( грузинский горно &ndash; лыжный курорт, который находится&nbsp; на высоте 1800 метров над уровнем моря ).</p>\r\n\r\n<p>Ночевка в отеле Бакуриани.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6. Бакуриани &ndash; Ахалцихе &ndash; Вардзия &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Из Боржоми Вы направитесь в город <b>Ахалцихе</b> ( старое название &ndash; Ломсия ), где еще в IX веке находился город &ndash; дворец османских завоевателей, так называемый<b> Рабат</b> ( с арабского &laquo; укрепленное место &raquo; ). Рабат &ndash; это один большой город в городе. В комплексе кроме исторических крепостей, башен, церквей, келий и мечети существуют и современные учреждения разного типа.</p>\r\n\r\n<p>Редко где Вы сможете встретить столько красоты, столько достоинства и величия. Редко какая крепость может похвастаться своей необычной историей и своими царскими корнями. Вся атмосфера крепости дышит великими событиями, которые происходили здесь столько веков назад.</p>\r\n\r\n<p>Продолжая путешествие, Вы посетите высеченный в скале комплекс<b> Вардзия</b> &ndash; один из важнейших и впечатляющих исторических памятников Грузии. Вардзия &ndash; памятник&nbsp; XII века, предназначенный для военной крепости и грандиозный укрепленный монастырь.Комплекс Вардзия простирается на 500 м и состоит из 13 этажей, который связывают друг с другом сложная система тоннелей.</p>\r\n\r\n<p>Наш гид повествует Вам историю строительства этого великолепного памятника, который сохранил следы многих трагических событий. Вы окунетесь во времена царствования&nbsp; великой царицы Тамары, узнаете много исторических фактов и легенд, связанных с ней и на мгновенье представите мир ее глазами...</p>\r\n\r\n<p>Возвращение в Тбилиси.</p>\r\n\r\n<p>Ночевка в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 7.&nbsp; Отъезд.</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт.</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/Borjomi%20pat.jpg', '/uploads/images/turebi/tbilisi5464.jpg,/uploads/images/turebi/tbilisi543.jpg,/uploads/images/turebi/bodbis%20monasteri.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%2012.jpg,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0223.jpg,/uploads/images/turebi/%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B834.jpg,/uploads/images/turebi/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B513.jpg,/uploads/images/turebi/Uplistsikhe1.jpg,/uploads/images/turebi/upliscixe.jpg,/uploads/images/turebi/bakuriani2.jpg,/uploads/images/turebi/bakuriani4.jpg,/uploads/images/turebi/Borjomi1.jpg,/uploads/images/turebi/borjomi34.jpg', '', 0, 0, 0, 1),
(165, '09-дней-08-ночей', '09-дней-08-ночей', '09-дней-08-ночей', '12', NULL, '09 дней / 08 ночей', '09 дней / 08 ночей', '09 дней / 08 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси</p>\r\n\r\n<p><b>День 3</b>. Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash;Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Гудаури</p>\r\n\r\n<p>&nbsp;<b>День 5</b>. Гудаури &ndash; Гори &ndash; Уплисцихе &ndash; Кутаиси</p>\r\n\r\n<p><b>День 6.</b> Кутаиси &ndash; Гелатский монастырский комплекс &ndash; Моцамета - Пещера Прометея &ndash; Кутаиси</p>\r\n\r\n<p><b>День 7.</b> Кутаиси &ndash; Мартвильский монастырь &ndash; Мартвильские каньоны &ndash; Батуми</p>\r\n\r\n<p><b>День 8</b>. Батуми</p>\r\n\r\n<p><b>День 9</b>. Отъезд &ndash; Батуми</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. &nbsp;Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси.</p>\r\n\r\n<p><b>Продолжительность тура:&nbsp; 3 &ndash; 5 ч.</b></p>\r\n\r\n<p>Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B83.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия во Внешнюю Кахетию.</p>\r\n\r\n<p><b>Продолжительность тура : 5 &ndash; 7 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ<b><i>. ( посещение музея за дополнительную плату )</i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Гудаури</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в бывшую столицу Грузии &ndash; Мцхета и путешествие по Военно &ndash; Грузинской дороге.</p>\r\n\r\n<p><b>Продолжительность тура:&nbsp; 7 &ndash; 8 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/sveticxoveli7.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Путешествие продолжим по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%92%D0%BE%D0%B5%D0%BD%D0%BD%D0%BE%20pat.jpg\" style=\"margin: 12px; float: left; width: 290px; height: 295px;\" />Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске. . <b><i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i></b>.</p>\r\n\r\n<p>Возвращение в Гудаури.</p>\r\n\r\n<p>Ночевка в отеле Гудаури.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Гудаури &ndash; Гори &ndash; Уплисцихе - Кутаиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 8 ч.</b></p>\r\n\r\n<p>По программе город<b> Гори</b> расположенный на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</p>\r\n\r\n<p>Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</p>\r\n\r\n<p>Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.</p>\r\n\r\n<p>В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>Далее Вы направитесь в город Кутаиси.</p>\r\n\r\n<p>Ночевка в отеле Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6.</b> <b>Кутаиси &ndash; Гелатский монастырский комплекс &ndash; Моцамета - Пещера Прометея &ndash; Кутаиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 6 &ndash; 8 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>С нашим гидом Вы сможете посетить <b>храм Баграта</b> &ndash; величественный собор, который возвышается над городом Кутаиси, построенный в X &ndash; XI вв, во времена правления грузинского царя Баграта III &ndash; основоположника великого рода князей Багратиони. Храм Баграта &ndash; символ объединенного грузинского народа. Именно здесь был коронован Давид Строитель.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%9C%D0%BE%D1%86%D0%B0%D0%BC%D0%B5%D1%82%D0%B033.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Затем Вы посетите выдающийся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p>Путешествие продолжится в<b> монастырь Моцамета</b> &ndash; красочное место Грузии, который буквально утопает в зелени деревьев и только черепица выдает присутствие храма в буйной растительности. В этом уютном месте наш гид повествует Вам историю и легенды, связанные с этим монастырем. Моцамета &ndash; это не просто место, затерянное в горах. Это красивый храм, где можно&nbsp; насладится пением птиц, вдохнуть свежий высокогорный воздух и понять, что даже время не властно разрушить всю эту вечную красоту природы.</p>\r\n\r\n<p>У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: right;\" />прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке.</p>\r\n\r\n<p>Возвращение в Кутаиси.</p>\r\n\r\n<p>Ночевка в отеле Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 7.</b> <b>Кутаиси &ndash; Мартвильский монастырь &ndash; Мартвильские каньоны &ndash; Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 8 ч</b>.</p>\r\n\r\n<p>Мартвильский каньон, или же Каньон Гачедили &ndash; не очень большой &laquo; мокрый &raquo; каньон около города Мартвили, гланая достопримечательность мартвильского района и топовая достопримечательность Самегрело.</p>\r\n\r\n<p>Длина этой природной достопримечательности &ndash; 2400 м, глубина надреза &ndash; от 20 до 40 метров. В нескольких местах каньон опускается террасами и в середине образовывает известный ступенчато &ndash; каскадный мартвильский водопад, высотой в 12 метров.</p>\r\n\r\n<p>&nbsp;На лодке в каньон можно заплыть достаточно глубоко и своими глазами увидеть уникальную красоту здешней природы. Каньон Мартвили состоит в списке 10 приоритетрых местностей Грузии, которым присвоен статус памятника природы.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Мартвильский монастырь, или Чкондиди ( рубеж VI - VII столетий ) &ndash; старинный мегрельский монастырь около города Мартвили, главный монастырь в Самегрело &ndash; был построен при абхазских царях как усыпальница, и позже пользовался для тех же целей князьями Самегрело.</p>\r\n\r\n<p>Основной собор принадлежит к типу раннехристианских храмов, именнуемых &laquo; тетраконх &raquo; ( в переводе с греческого &laquo; четыре раковины &raquo; ), и в плане представляет собой равносторонний крест.</p>\r\n\r\n<p>Собор Успения Богородицы первый в череде самых крупных грузинских храмов. Арки внутри церкви буквально взлетают к небу, расширяя и без того немалое пространство.</p>\r\n\r\n<p>Наш гид повествует Вам историю и легенды,связанные с этим местом. И у вас будет возможность насладиться окружающей живописной природой этого местечка.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Переезд в город Батуми.</p>\r\n\r\n<p>Ночевка в отеле Батуми.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 8</b>. <b>Батуми </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Обзорная экскурсия по Батуми. Посещение Ботанического сада.</p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p><b>Батуми</b> &ndash; один из древнейших городов Грузии, который согласно историческим источникам, был основан еще до нашей эры. Богатое историческое прошлое в значительной степени обусловило архитектурное разнообразие города.</p>\r\n\r\n<p>В сегодняшнем Батуми, помимо старых, исторических строений, есть и много новых, сделанных в современном стиле: <b>Алфавитная башня</b>, <b>Дом Юстиции</b>, рестораны и гостиницы необычных форм и др.</p>\r\n\r\n<p>В самом&nbsp; сердце старого Батуми находятся <b>площадь Европы </b>и &laquo; <b>Пьяцца</b> &raquo;, окруженные центральными улицами и диковинными зданиями.</p>\r\n\r\n<p><b>Батумский приморский парк</b> занимает территорию длиной в 7 км.&nbsp; Бульвар украшают бунгало, кафе &ndash; лаунджи, рестораны, детские аттракционы, скамейки современного дизайна, скульптуры и новые фонтаны. Аллеи приморского парка отличаются необычным сочетанием вечнозеленых растений, магнолий и пальм. Бульвар всегда был источником многих творческих начинаний. И сегодня он является своеобразной гаванью для оригинальных творческих идей и современных скульптурных композиций: &laquo; <b>Я, ты и Батуми</b> &raquo;, &laquo; <b>Нино и Али</b> &raquo;, &laquo; <b>Женщина с велосипедом</b> &raquo;, &laquo; <b>Статуи влюбленных</b> &raquo;, или признанные символом города , скульптуры &laquo; <b>Дельфины</b> &raquo; - делают Батумский бульвар более притягательным и эффектным.</p>\r\n\r\n<p>Близ Батуми находится один из богатейших и уникальнейших <b>ботанических садов</b>, где сосуществуют виды растений совершенно разных климатических и ландшафтных зон. У Вас будет возмодность посетить Ботанический сад Аджарии и насладиться живописными и красочными пейзажами.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 9.&nbsp; Отъезд.</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт.</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20pat3.jpg', '/uploads/images/turebi/%D0%9C%D0%BE%D1%86%D0%B0%D0%BC%D0%B5%D1%82%D0%B04.jpg,/uploads/images/turebi/batumi344.png,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F2.jpg,/uploads/images/turebi/Batumi1.jpg,/uploads/images/turebi/batumi65.jpg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8212.jpg,/uploads/images/turebi/bodbis%20monasteri.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/upliscixe.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B82.jpg,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F.jpg,/uploads/images/turebi/%D0%9C%D0%BE%D1%86%D0%B0%D0%BC%D0%B5%D1%82%D0%B02.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(166, '10-дней-09-ночей', '10-дней-09-ночей', '10-дней-09-ночей', '16', NULL, '10 дней / 09 ночей', '10 дней / 09 ночей', '10 дней / 09 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси</p>\r\n\r\n<p><b>День 3</b>. Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash;Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Гудаури</p>\r\n\r\n<p>&nbsp;<b>День 5</b>. Гудаури &ndash; Гори &ndash; Уплисцихе &ndash; Кутаиси</p>\r\n\r\n<p><b>День 6.</b> Кутаиси &ndash; Гелатский монастырский комплекс &ndash; Моцамета - Пещера Прометея &ndash; Кутаиси</p>\r\n\r\n<p><b>День 7.</b> Кутаиси &ndash; Сванетия ( Местия )</p>\r\n\r\n<p><b>День 8</b>. Местия &ndash; Ушгули - Местия</p>\r\n\r\n<p><b>День 9</b>. Местия &ndash; Тбилиси</p>\r\n\r\n<p><b>День 10</b>. Отъезд - Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси.</p>\r\n\r\n<p><b>Продолжительность тура:&nbsp; 3 &ndash; 5 ч.</b></p>\r\n\r\n<p>Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/narikala.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash;Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия во Внешнюю Кахетию.</p>\r\n\r\n<p><b>Продолжительность тура : 5 &ndash; 7 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ<b><i>. ( посещение музея за дополнительную плату )</i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Мцхета &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Гудаури</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в бывшую столицу Грузии &ndash; Мцхета и путешествие по Военно &ndash; Грузинской дороге.</p>\r\n\r\n<p><b>Продолжительность тура:&nbsp; 7 &ndash; 8 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Путешествие продолжим по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%92%D0%BE%D0%B5%D0%BD%D0%BD%D0%BE%20%E2%80%93%20%D0%B3%D1%80%D1%83%D0%B7%D0%B8%D0%BD%D1%81%D0%BA%D0%B0%D1%8F%20%D0%B4%D0%BE%D1%80%D0%BE%D0%B3%D0%B0.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 200px;\" />Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске. . <b><i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i></b>.</p>\r\n\r\n<p>Возвращение в Гудаури.</p>\r\n\r\n<p>Ночевка в отеле Гудаури.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>&nbsp;День 5. Гудаури &ndash; Гори &ndash; Уплисцихе &ndash; Кутаиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 8 ч.</b></p>\r\n\r\n<p>По программе город<b> Гори</b> расположенный на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</p>\r\n\r\n<p>Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</p>\r\n\r\n<p>Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.</p>\r\n\r\n<p>В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>Далее Вы направитесь в город Кутаиси.</p>\r\n\r\n<p>Ночевка в отеле Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6. Кутаиси &ndash; Гелатский монастырский комплекс &ndash; Моцамета - Пещера Прометея &ndash; Кутаиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 6 &ndash; 8 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>С нашим гидом Вы сможете посетить <b>храм Баграта</b> &ndash; величественный собор, который возвышается над городом Кутаиси, построенный в X &ndash; XI вв, во времена правления грузинского царя Баграта III &ndash; основоположника великого рода князей Багратиони. Храм Баграта &ndash; символ объединенного грузинского народа. Именно здесь был коронован Давид Строитель.</p>\r\n\r\n<p>Затем Вы посетите выдающийся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C%20%D0%9C%D0%BE%D1%86%D0%B0%D0%BC%D0%B5%D1%82%D0%B0.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Путешествие продолжится в<b> монастырь Моцамета</b> &ndash; красочное место Грузии, который буквально утопает в зелени деревьев и только черепица выдает присутствие храма в буйной растительности. В этом уютном месте наш гид повествует Вам историю и легенды, связанные с этим монастырем. Моцамета &ndash; это не просто место, затерянное в горах. Это красивый храм, где можно&nbsp; насладится пением птиц, вдохнуть свежий высокогорный воздух и понять, что даже время не властно разрушить всю эту вечную красоту природы.</p>\r\n\r\n<p>У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке.</p>\r\n\r\n<p>Возвращение в Кутаиси.</p>\r\n\r\n<p>Ночевка в отеле Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 7. Кутаиси &ndash; Сванетия ( Местия ) </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Переезд в Верхнюю Сванетию. Обзорная экскурсия по Местия.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p><b>Верхняя Сванетия</b> &ndash; горный район на северо &ndash; западе Грузии. В котловине, ограниченной юго &ndash; западным склоном Большого Кавказа и Сванетским хребтом, находится множество небольших поселений, которые органично вписаны в окружающий ландшафт и отличаются самобытной архитектурой. История Сванетии уходит корнями в далекое прошлое.</p>\r\n\r\n<p>Исторически Верхняя Сванетия делится на Восточную и Западную, в других вариантах &ndash; на Вольную и Княжескую. Восточная ( Вольная ) Сванетия дошла до нашего времени в более архаичном состоянии, с более традиционным укладом жизни и с большим количеством каменных сооружений. Здесь множество интересных объектов и Вы сможете сконцентрировать свое внимание именно на этой части Сванетии.</p>\r\n\r\n<p>История гордого, мужественного и свободолюбивого народа сванов, который по сей день сохранил свой язык, насчитывает несколько тысячелетий. Сваны никогда не вели захватнических войн и никогда не были порабащены врагами. У сванов никогда не было крепостного права, да и дворянство носило условный характер. Ведь каждый сван &ndash; это личность, он не терпит над собой господства.</p>\r\n\r\n<p>Особенность Верхней Сванетии &ndash; <b>сванские башни</b>, расположенные в каждом поселке в больших количествах. Чрезвычайно прочные лавиноустойчивые сооружения, назначение которых полностью до сих пор не выяснено. Также в Верхней Сванетии находится множество древних церквей, построенных в IX &ndash; XII веках.</p>\r\n\r\n<p>Место, откуда мы начнем обзорную экскурсию, является районным центром современной Сванетии, поселок городского типа &ndash; <b>Местия</b>. Несмотря на свои довольно скромные размеры, Местия на протяжении столетий была и остается культурным и религиозным центром Верхней Сванетии. Многочисленные средневековые церкви и укрепления Местийского района включены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Местия &ndash; один из достопримечательных памятников грузинского зодчества и этнографии. У Вас будет возможность посетить <b>музей истории и этнографии</b>. Войдя в это здание, перед глазами появятся интерьеры сванских жилищ. Кажется, что все эти предметы так и ждут, когда появятся люди и снова возьмут их в руки. В музее хранятся материалы, найденные в ходе раскопок : образцы гравюр и уникальных икон, древнейшие рукописи, шоловные уборы раннего и позднего Средневековья, украшения из железа, серебра и меди, ювелирные изделия, керамика, образцы тканей и многое другое.</p>\r\n\r\n<p><b><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9C%D1%83%D0%B7%D0%B5%D0%B9%20%D0%9C%D0%B8%D1%85%D0%B0%D0%B8%D0%BB%D0%B0%20%D0%A5%D0%B5%D1%80%D0%B3%D0%B8%D0%B0%D0%BD%D0%B8.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 176px;\" />Музей Михаила Хергиани</b> &ndash; одна из главных достопримечательностей столицы Сванетии. Быть в Местия и не побывать здесь &ndash; все равно что не зайти внутрь сванской башни, или не попробовать местной еды. Музей располагается в доме, где жил альпинист, и состоит из четырех залов: этнографического, зала истории советского альпинизма, зала восхождений Михаила Хергиани, личной комнаты альпиниста и зала памяти о спортсмене.</p>\r\n\r\n<p>Ночевка в отеле Местия.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 8. Местия &ndash; Ушгули &ndash; Местия</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 6 &ndash; 8 ч.</b></p>\r\n\r\n<p>Путешествие продолжим в село <b>Ушгули</b> &ndash; самое знатное место во всей Верхней Сванетии, которое находится у подножья горы Шхара ( 5193 м над уровнем моря ) на высоте от 2060 до 2200 метров над уровнем моря, что позволяет утверждать, что Ушгули является самым высоким поселением в Европе. Центр коммуны, деревня Чажаши, была внесена в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>По дороге осмотрим так называемую <b>Башню Любви</b>, построенную на огромнейшей глыбе на территории общины Ипари. Здесь до недавнего времени хранилась самая старая рукопись грузинского народа, написанная на грузинском языке, созданная в IX веке. Сейчас перенесена в музей Местии.</p>\r\n\r\n<p>Ушгули &ndash; это не просто община. Когда попадаешь сюда, то кажется, что ты на какой &ndash; то невидимой машине времени просто переместился совершенно в другой век. Здесь целый лес сванских башен стоит в молчаливом окружении таких же древних домов, которые очень похожи на крепости.</p>\r\n\r\n<p>На окраине Ушгули на одиноком холме стоит древняя церковь <b>Ламария </b>( Успения Святой Богородицы, X в. ) &ndash; одна из святынь Ушгульского общества. К расположенному на высоком холме храму примыкают башня и высокая крепостная ограда. Местные жители верят, что именно под этой церковью захоронена царица Тамара, наделенная сванами всеми христианскими благодетелями.</p>\r\n\r\n<p>Возвращение в Местия.</p>\r\n\r\n<p>Ночевка в отеле Местия.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 9. Местия &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Переезд в Тбилиси.</p>\r\n\r\n<p>Ночевка в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 10. Отъезд </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт.</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/svaneti%20pat.png', '/uploads/images/turebi/%D0%9C%D0%BE%D1%86%D0%B0%D0%BC%D0%B5%D1%82%D0%B04.jpg,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F2.jpg,/uploads/images/turebi/mestia65.jpg,/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/Uplistsikhe1.jpg,/uploads/images/turebi/gori21.png,/uploads/images/turebi/Kutaisi.JPG,/uploads/images/turebi/ushguli.JPG,/uploads/images/turebi/ushguli32.jpg,/uploads/images/turebi/mestia765.jpg,/uploads/images/turebi/gudauri4.jpg,/uploads/images/turebi/%D0%9C%D0%BE%D1%86%D0%B0%D0%BC%D0%B5%D1%82%D0%B02.jpg,/uploads/images/turebi/yazbegi5.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(167, '10-дней-09-ночей-1', '10-дней-09-ночей-1', '10-дней-09-ночей-1', '16', NULL, '10 дней / 09 ночей', '10 дней / 09 ночей', '10 дней / 09 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси</p>\r\n\r\n<p><b>День 3</b>. Тбилиси &ndash; Телави &ndash; Цинандали - Бодбийский монастырь &ndash; Сигнахи &ndash;Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси&nbsp; &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</p>\r\n\r\n<p>&nbsp;<b>День 5</b>. Тбилиси &ndash; Мцхета - Тбилиси</p>\r\n\r\n<p><b>День 6.</b> Тбилиси &ndash; Гори &ndash; Уплисцихе - Кутаиси</p>\r\n\r\n<p><b>День 7.</b> Кутаиси &ndash; Гелатский монастырский комплекс &ndash; Моцамета &ndash; Пещера Прометея - Кутаиси</p>\r\n\r\n<p><b>День 8</b>. Кутаиси &ndash; Боржоми &ndash; Ахалцихе - Бакуриани</p>\r\n\r\n<p><b>День 9</b>. Бакуриани &ndash; Шиомгвимский монастырь - Тбилиси</p>\r\n\r\n<p><b>День 10</b>. Отъезд - Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси.</p>\r\n\r\n<p><b>Продолжительность тура:&nbsp; 3 &ndash; 5 ч.</b></p>\r\n\r\n<p>Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B823334.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Телави &ndash; Цинандали - Бодбийский монастырь &ndash; Сигнахи &ndash;Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></i></b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</p>\r\n\r\n<p>Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpg\" style=\"margin: 12px; float: left; width: 300px; height: 146px;\" />От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ. <b><i>( посещение музея за дополнительную плату )</i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>&nbsp;Дегустация вин и чачи</b> ( обед за дополнительную плату ).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Возвращение в Тбилиси.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси&nbsp; &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 8 ч.</span></span></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную <img alt=\"\" src=\"/uploads/images/turebi/Jinvali.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске. <b><i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i></b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Возвращение в Тбилиси.</p>\r\n\r\n<p>Ночевка в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>&nbsp;День 5. Тбилиси &ndash; Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия&nbsp; в город Мцхета.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 4 &ndash; 5 ч.</span></span></i></b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%20pat2.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Возвращение в Тбилиси.</p>\r\n\r\n<p>Ночевка в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6. Тбилиси &ndash; Гори &ndash; Уплисцихе &ndash; Кутаиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в город Гори и Уплисцихе. Переезд в город Кутаиси.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 8 ч.</span></span></b></p>\r\n\r\n<p><b>Город Гори</b> расположен в 89 км от Тбилиси на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</p>\r\n\r\n<p>Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</p>\r\n\r\n<p>Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.</p>\r\n\r\n<p>В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Переезд в город Кутаиси.</p>\r\n\r\n<p>Ночевка в отеле Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 7. Кутаиси &ndash; Гелатский монастырский комплекс &ndash; Моцамета &ndash; Пещера Прометея &ndash; Кутаиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><b>Продолжительность тура : 6 &ndash; 8 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>С нашим гидом Вы сможете посетить <b>храм Баграта</b> &ndash; величественный собор, который возвышается над городом Кутаиси, построенный в X &ndash; XI вв, во времена правления грузинского царя Баграта III &ndash; основоположника великого рода князей Багратиони. Храм Баграта &ndash; символ объединенного грузинского народа. Именно здесь был коронован Давид Строитель.</p>\r\n\r\n<p>Затем Вы посетите выдающийся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p>Путешествие продолжится в<b> монастырь Моцамета</b> &ndash; красочное место Грузии, который буквально утопает в зелени деревьев и только черепица выдает присутствие храма в буйной растительности. В этом уютном месте наш гид повествует Вам историю и легенды, связанные с этим монастырем. Моцамета &ndash; это не просто место, затерянное в горах. Это красивый храм, где можно&nbsp; насладится пением птиц, вдохнуть свежий высокогорный воздух и понять, что даже время не властно разрушить всю эту вечную красоту природы.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке.</p>\r\n\r\n<p>Возвращение в Кутаиси.</p>\r\n\r\n<p>Ночевка в отеле Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 8. Кутаиси &ndash; Боржоми &ndash; Ахалцихе &ndash; Бакуриани</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Самцхе &ndash; Джавахети .</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 7 &ndash; 9 ч.</span></span></b></p>\r\n\r\n<p>С древних времен <b>Джавахети</b> был одним из эриставств ( княжеств ) Картлинского царства. Географическое расположение Месхети создавало выгодные условия для культурного и экономического развития страны. Оно способствовало установить связь между старыми культурами известных на территории Грузии и с центрами цивилизаций Ближнего Востока.</p>\r\n\r\n<p>Период царствования Царицы Тамар ( XII &ndash; XIII вв. ) считался Золотым веком Грузии. В этот период начались большие строительства. Среди них самым удивительным считается Вардзия &ndash; наш последний пункт по этому маршруту.</p>\r\n\r\n<p>По дороге к высеченному городу в скале, Вы посетите курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</p>\r\n\r\n<p>Из Боржоми Вы направитесь в город <b>Ахалцихе</b> ( старое название &ndash; Ломсия ), где еще в IX веке находился город &ndash; дворец османских завоевателей, так называемый<b> Рабат</b> ( с арабского &laquo; укрепленное место &raquo; ). Рабат &ndash; это один большой город в городе. В комплексе кроме исторических крепостей, башен, церквей, келий и мечети существуют и современные учреждения разного типа.</p>\r\n\r\n<p>Редко где Вы сможете встретить столько красоты, столько достоинства и величия. Редко какая крепость может похвастаться своей необычной историей и своими царскими корнями. Вся атмосфера крепости дышит великими событиями, которые происходили здесь столько веков назад.</p>\r\n\r\n<p>Поездка в <b>Бакуриани</b>&nbsp; ( грузинский горно &ndash; лыжный курорт, который находится&nbsp; на высоте 1800 метров над уровнем моря ).</p>\r\n\r\n<p>Ночевка в отеле Бакуриани.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 9. Бакуриани &ndash; Шиомгвимский монастырь &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Шиомгвимский монастырь. Возвращение в Тбилиси.</p>\r\n\r\n<p><b>Продолжительность тура : 5 &ndash; 7 ч.</b><img alt=\"\" src=\"/uploads/images/turebi/%D0%A8%D0%B8%D0%BE%D0%BC%D0%B3%D0%B2%D0%B8%D0%BC%D1%81%D0%BA%D0%B8%D0%B9.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" /></p>\r\n\r\n<p><b>Шиомгвимский монастырь</b> &ndash; старинный монастырь в 9 км от города Мцхеты, основанный где-то после 560 года святым Шио, одним из ассирийских отцов, известный как Шио Мгвимский. Монастырский комплекс включает в себя храм Святого Иоанна Крестителя ( 560-580 гг. ), вырытые монахами пещеры, колодец &ndash; склеп&nbsp; Шио,&nbsp; верхняя церковь Божьей Матери ( Земо Эклесия, XII в. ), трапезная ( XII &ndash; XVII вв. ), часовня ( XII в. ).</p>\r\n\r\n<p>Наш гид расскажет Вам о жизни Святого Шио, о его деяниях, творивших чудеса.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночевка в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 10. Отъезд </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20pat.jpg', '/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F1.jpg,/uploads/images/turebi/%D0%A8%D0%B8%D0%BE%D0%BC%D0%B3%D0%B2%D0%B8%D0%BC%D1%81%D0%BA%D0%B8%D0%B9.jpg,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpeg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C.jpg,/uploads/images/turebi/ananuri232.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8.jpg,/uploads/images/turebi/bakuriani2.jpg,/uploads/images/turebi/Borjomi12.jpg,/uploads/images/turebi/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B513.jpg,/uploads/images/turebi/%D0%A8%D0%B8%D0%BE%D0%BC%D0%B3%D0%B2%D0%B8%D0%BC%D1%81%D0%BA%D0%B83.jpg,/uploads/images/turebi/%D0%9F%D0%B5%D1%89%D0%B5%D1%80%D0%B0%20%D0%9F%D1%80%D0%BE%D0%BC%D0%B5%D1%82%D0%B5%D1%8F.jpg,/uploads/images/turebi/tbilisi654.jpg,/uploads/images/turebi/Kutaisi.JPG,/uploads/images/turebi/yazbegi3.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(168, '20-дней-19-ночей', '20-дней-19-ночей', '20-дней-19-ночей', '19', NULL, '20 дней / 19 ночей', '20 дней / 19 ночей', '20 дней / 19 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><span style=\"font-size:18px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 1</b>. Прибытие &ndash; Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 2.</b> Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 3</b>. Тбилиси &ndash; Мцхета &ndash;Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 4.</b> Тбилиси&nbsp; &ndash; Болниси &ndash; Дманиси &ndash; Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;<b>День 5 &ndash; 6 </b>. Тбилиси &ndash; Уджарма &ndash; Ахали Шуамта &ndash; Алаверди &ndash; Телави &ndash;Цинандали &ndash; Греми &ndash; Бодбийский монастырь -&nbsp; Сигнахи - Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 7.</b> Тбилиси ( свободный день )</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 8.</b> Тбилиси&nbsp; &ndash; Ананури &ndash; Гудаури &ndash; Казбеги - Гудаури</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 9</b>. Гудаури &ndash; Гори &ndash; Уплисцихе - Боржоми &ndash; Бакуриани</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 10</b>. Бакуриани &ndash; Ахалцихе &ndash; Вардзия &ndash; Бакуриани</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 11</b>. Бакуриани &ndash; Кутаиси &ndash; Гелатский монастырь &ndash; Моцамета - Пещера Прометея &ndash; Кутаиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 12.</b> Кутаиси &ndash; Мартвильский монастырь &ndash; Мартвильские каньоны &ndash; Сванетия ( Местия )</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 13</b>. Местия</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 14</b>. Местия &ndash; Ушгули &ndash; Местия</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 15</b>. Местия &ndash; Батуми</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 16.</b> Батуми &ndash; Гонио - Махунцети ( Горная Аджария ) - Батуми</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 17.</b> Батуми ( свободный день )</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 18.</b> Батуми &ndash; Ботанический сад &ndash; Батуми</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 19.</b> Батуми &ndash; Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 20.</b> Отъезд</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 1</b>. <b>Прибытие </b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Прибытие в Тбилиси.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Встреча гостей в аэропорту и трансфер в отель выбранной категории.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Размещение в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночевка в Тбилиси</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 2.</b> <b>Тбилиси</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Пешая экскурсия по Тбилиси.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура:&nbsp; 3 &ndash; 5 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;.<b><i> ( подъем на канатке с парка Рике к Нарикале )</i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B824.jpeg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\" style=\"color:#002060\">( Мастер &ndash; класс по хинкали / дегустация чачи за дополнительную плату )</span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в отеле Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 3</b>. <b>Тбилиси &ndash; Мцхета &ndash;Тбилиси</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Экскурсия&nbsp; в город Мцхета&nbsp;&nbsp; <b><i><span lang=\"RU\"><span style=\"line-height:115%\">&laquo; </span></span></i></b><b><i><span lang=\"RU\"><span style=\"line-height:115%\">КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ </span></span></i></b><b><i><span lang=\"RU\"><span style=\"line-height:115%\">&raquo;</span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\">Продолжительность тура : 4 &ndash; 5 ч.</span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\" style=\"color:#002060\">( Дегустация вин за дополнительную плату )</span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в отеле Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 4.</b> <b>Тбилиси&nbsp; &ndash; Болниси &ndash; Дманиси &ndash; Тбилиси</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Экскурсия в Квемо ( Нижняя ) Картли&nbsp;&nbsp; &laquo; <b><i><span lang=\"RU\"><span style=\"line-height:115%\">НА ГЛУБИНЕ ИСТОРИИ</span></span></i></b> &raquo;.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\">Продолжительность тура : 6 - 8 ч.</span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Невозможно представить&nbsp; Средневековую Грузию без города &ndash; крепости<b> Дманиси</b>. Здесь в те далекие времена проходила бойкая торговля <img alt=\"\" src=\"/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B823.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />самым разнообразным товаром. Здесь проживало много ремесленников, которые на заказ могли изготовить красивые кувшины или сшить прочные сапоги из кожи. Здесь можно было легко обменять один товар на другой. Дманиси был перевалочным пунктом на Великом Шелковом Пути. Но не только торговлей занимался Дманиси. Эта была еще и очень хорошо укрепленная крепость, которая помогала защищать остальную территорию Грузии. Общая территория крепости когда &ndash; то составляла 25 Га. Во время раскопок на территории города &ndash; крепости Дманиси нашли очень интересные артефакты и человеческие кости, которые теперь ставят под вопрос саму теорию эволюции человочества. Именно здесь 1,85 млн. лет назад человек прямоходящий ( Homo erectus, Homo georgicus ) уже умел создавать характерные для него инструменты. &laquo; Первых европейцев &raquo; назвали <b>Зезва и Мзия</b>. Их восстановленные образы и археологические находки Вы сможете увидеть в музее, который находится в археологической зоне дманисского городища.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">На территории городища есть два интересных религиозно &ndash; исторических памятника: остатки крепостных сооружений и храм <b>Дманисский Сион </b>( VII в. ).</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наше путешествие продолжается в живописное местечко Болниси, где на высоте 620 метров над уровнем моря находится памятник грузинской архитектуры Золотого Века &ndash; <b>монастырь Цугругашени</b> ( XIII в. ). Цугругашени далек от мирской жизни, место - тихое и уединенное и человек остается наедине с собой и своими мыслями.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">С нашим гидом Вы посетите древнюю базилику <b>Болнисский Сион</b> ( 478 &ndash; 493 гг. ), которая до наших дней дошла практически без изменений. Болнисский Сион особенно интересен надписями V века на стенах храма. Эти&nbsp; надписи являются древнейшими образцами грузинской письменности.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Стены базилики хранят тысячи молитв, и храм до сих пор продолжает служить людям. Посещение таких мест обогащает людей не только новыми знаниями и впечатлениями, но и дарует духовную пищу.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\" style=\"color:#002060\">( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в отеле Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp;<b>День 5 &ndash; 6 </b>. <b>Тбилиси &ndash; Уджарма &ndash; Ахали Шуамта &ndash; Алаверди &ndash; Телави &ndash;Цинандали &ndash; Греми &ndash; &laquo; Корпорация Киндзмараули &raquo; - Бодбийский монастырь -&nbsp; Сигнахи &ndash; Тбилиси</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Экскурсия в Кахетию</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\">Продолжительность тура : &nbsp;1 &ndash; ый день - 6 &ndash; 8 ч</span></span></i></b><span lang=\"RU\"><span style=\"line-height:115%\">.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><span lang=\"RU\"><span style=\"line-height:115%\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b><i>2 &ndash; ой день &ndash; 6 &ndash; 8 ч.</i></b></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\">Первый день</span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Кахетия &ndash; один из самых больших регионов в Восточной Грузии. Некогда самостоятельное царство. Царство бесконечных виноградников и живописных гор, старинных традиций и загадочных мертвых городов. Царство самых вкусных грузинских вин и спокойной печали древних монастырей.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наше путешествие начинается с <b>Гомборского перевала </b>( 2000 м над уровнем моря ) &ndash; царство лесных великанов. Высокие, прямоствольные деревья достигают толщины в несколько обхватов. Густые кроны, почти не пропуская лучей солнца, создают сплошной шатер и только светлые буковые стволы не дают этому &laquo; темному царству &raquo; погрузиться во мрак.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">На заросшем склоне горы Цивгомбори находится Хахульский женский монастырь Богородицы -&nbsp; <b>Ахали Шуамта</b> ( Новое Межгорье, XVI в. ) &ndash; центрально-купольная церковь и колокольня. Самым главным объектом молитв монастыря была икона Хахульской Богоматери, украшенная золотом и драгоценными камнями, на обратной стороне которой находились 44 святыни. Сегодня эта икона находится в сокровищнице Тбилисского государственного музея искусств. Монастырь являлся важным духовным очагом Кахетии. Сыграл также и&nbsp; политическую роль, так как именно это место было избрано для встречи царя Кахетии Александра II и Бориса Годунова, чтобы решить вопрос о помощи государства Российского христианским странам в борьбе с захватчиками.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">В центре Алазанской долины Вы посетите величайший храм средневековой Грузии &ndash; <b>Алавердский Кафедральный собор</b> ( Собор Святого Георгия, XI в. ) и монастырский комплекс. Храм, поражающий своим видом и великолепием, является одним из крупнейших соборов в Грузии, связанный с именем одного из тринадцати сирийских первооснователей грузинского монашества &ndash; Иосифом Алавердели.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Собор Алаверди представлен на включение в список Всемирного наследия ЮНЕСКО.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Возвращение в Телави.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в Телави.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><i><span lang=\"RU\"><span style=\"line-height:115%\">Второй день</span></span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%93%D1%80%D0%B5%D0%BC%D0%B82.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Посетим город <b>Греми</b>, престольный город богатого и успешного Кахетинского царства ( XVI в. ). В комплекс входят : церковь Архангелов, царская палата с колокольней, часовня св. Георгия с башней, на горе Самеба &ndash; круглая часовня Святой Троицы. В Греми Вам повествуют историю жизни кахетинских царей и цариц, о роли каждого из них в жизни города и царства в целом. Увидите надземные и подземные элементы городских строений или урбанской инфраструктуры</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>&laquo; Корпорация Киндзмараули &raquo;</b> - один из крупнейших винных заводов на территории Кахетии, который действовал еще во времена СССР, но в 1993 году был выкуплен в частную собственность и модернизирован. Самые же старые постройки относятся к 1533 году и многие из них можно увидеть и потрогать руками. Винзавод производит много разных вин, причем ввиду своего давнего существования он имеет в запасах даже коньяки 23 &ndash; летней выдержки.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">В городе находится музей Нико Пиросмани, где выставлены 16 его работ.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;Возвращение в Тбилиси.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 7.</b> <b>Тбилиси &nbsp;<i>( свободный день )</i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><i>Свободный день.</i></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 8.</b> <b>Тбилиси&nbsp; &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Гудаури</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Поездка в бывшую столицу Грузии &ndash; Мцхета и путешествие по Военно &ndash; Грузинской дороге.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура:&nbsp; 7 &ndash; 8 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную<img alt=\"\" src=\"/uploads/images/turebi/Jinvali.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске. <span style=\"color:#002060\">. <b><i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i></b></span>.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Возвращение в Гудаури.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночевка в отеле Гудаури.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 9</b>. <b>Гудаури &ndash; Гори &ndash; Уплисцихе - Боржоми &ndash; Бакуриани</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура : 7 &ndash; 8 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">По программе город<b> Гори</b> расположенный на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><img alt=\"\" src=\"/uploads/images/turebi/upliscixe%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: right;\" />Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Далее Вы направитесь в Боржоми.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Курортный город <b>Боржоми</b> окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Поездка в <b>Бакуриани</b>&nbsp; ( грузинский горно &ndash; лыжный курорт, который находится&nbsp; на высоте 1800 метров над уровнем моря ).</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночевка в отеле Бакуриани.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 10</b>. <b>Бакуриани &ndash; Ахалцихе &ndash; Вардзия &ndash; Бакуриани</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура : 7 &ndash; 9 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Из Бакуриани Вы направитесь в город <b>Ахалцихе</b> ( старое название &ndash; Ломсия ), где еще в IX веке находился город &ndash; дворец османских завоевателей, так называемый<b> Рабат</b> ( с арабского &laquo; укрепленное место &raquo; ). Рабат &ndash; это один большой город в городе. В комплексе кроме исторических крепостей, башен, церквей, келий и мечети существуют и современные учреждения разного типа.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Редко где Вы сможете встретить столько красоты, столько достоинства и величия. Редко какая крепость может похвастаться своей необычной историей и своими царскими корнями. Вся атмосфера крепости дышит великими событиями, которые происходили здесь столько веков назад.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/vardzia%20pat1.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />Продолжая путешествие, Вы посетите высеченный в скале комплекс<b> Вардзия</b> &ndash; один из важнейших и впечатляющих исторических памятников Грузии. Вардзия &ndash; памятник&nbsp; XII века, предназначенный для военной крепости и грандиозный укрепленный монастырь.Комплекс Вардзия простирается на 500 м и состоит из 13 этажей, который связывают друг с другом сложная система тоннелей.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наш гид повествует Вам историю строительства этого великолепного памятника, который сохранил следы многих трагических событий. Вы окунетесь во времена царствования&nbsp; великой царицы Тамары, узнаете много исторических фактов и легенд, связанных с ней и на мгновенье представите мир ее глазами...</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Возвращение в Бакуриани.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночевка в отеле Бакуриани.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 11</b>. <b>Бакуриани &ndash; Кутаиси &ndash; Гелатский монастырь &ndash; Моцамета - Пещера Прометея &ndash; Кутаиси</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Переезд в город Кутаиси.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура : 7 &ndash; 9 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Затем Вы посетите выдающийся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Путешествие продолжится в<b> монастырь Моцамета</b> &ndash; красочное место Грузии, который буквально утопает в зелени деревьев и <img alt=\"\" src=\"/uploads/images/turebi/%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C%20%D0%9C%D0%BE%D1%86%D0%B0%D0%BC%D0%B5%D1%82%D0%B0.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />только черепица выдает присутствие храма в буйной растительности. В этом уютном месте наш гид повествует Вам историю и легенды, связанные с этим монастырем. Моцамета &ndash; это не просто место, затерянное в горах. Это красивый храм, где можно&nbsp; насладится пением птиц, вдохнуть свежий высокогорный воздух и понять, что даже время не властно разрушить всю эту вечную красоту природы.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Возвращение в Кутаиси.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночевка в отеле Кутаиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 12.</b> <b>Кутаиси &ndash; Мартвильский монастырь &ndash; Мартвильские каньоны &ndash; Сванетия ( Местия )</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 8 ч.</span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><img alt=\"\" src=\"/uploads/images/turebi/martvili4.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Мартвильский каньон, или же Каньон Гачедили &ndash; не очень большой &laquo; мокрый &raquo; каньон около города Мартвили, гланая достопримечательность мартвильского района и топовая достопримечательность Самегрело.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Длина этой природной достопримечательности &ndash; 2400 м, глубина надреза &ndash; от 20 до 40 метров. В нескольких местах каньон опускается террасами и в середине образовывает известный ступенчато &ndash; каскадный мартвильский водопад, высотой в 12 метров.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;На лодке в каньон можно заплыть достаточно глубоко и своими глазами увидеть уникальную красоту здешней природы. Каньон Мартвили состоит в списке 10 приоритетрых местностей Грузии, которым присвоен статус памятника природы.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\">Мартвильский монастырь, или Чкондиди ( рубеж VI - VII столетий ) &ndash; старинный мегрельский монастырь около города Мартвили, главный монастырь в Самегрело &ndash; был построен при абхазских царях как усыпальница, и позже пользовался для тех же целей князьями Самегрело.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Основной собор принадлежит к типу раннехристианских храмов, именнуемых &laquo; тетраконх &raquo; ( в переводе с греческого &laquo; четыре раковины &raquo; ), и в плане представляет собой равносторонний крест.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Собор Успения Богородицы первый в череде самых крупных грузинских храмов. Арки внутри церкви буквально взлетают к небу, расширяя и без того немалое пространство.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наш гид повествует Вам историю и легенды,связанные с этим местом. И у вас будет возможность насладиться окружающей живописной природой этого местечка.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;Переезд в Сванетию ( Местия ).</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в отеле Местия.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 13</b>. <b>Местия </b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Обзорная экскурсия по Местия.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\">Продолжительность тура : 4 &ndash; 5 ч.</span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Верхняя Сванетия</b> &ndash; горный район на северо &ndash; западе Грузии. В котловине, ограниченной юго &ndash; западным склоном Большого Кавказа и Сванетским хребтом, находится множество небольших поселений, которые органично вписаны в окружающий ландшафт и отличаются самобытной архитектурой. История Сванетии уходит корнями в далекое прошлое.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Исторически Верхняя Сванетия делится на Восточную и Западную, в других вариантах &ndash; на Вольную и Княжескую. Восточная ( Вольная ) Сванетия дошла до нашего времени в более архаичном состоянии, с более традиционным укладом жизни и с большим количеством каменных сооружений. Здесь множество интересных объектов и Вы сможете сконцентрировать свое внимание именно на этой части Сванетии.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">История гордого, мужественного и свободолюбивого народа сванов, который по сей день сохранил свой язык, насчитывает несколько тысячелетий. Сваны никогда не вели захватнических войн и никогда не были порабащены врагами. У сванов никогда не было крепостного права, да и дворянство носило условный характер. Ведь каждый сван &ndash; это личность, он не терпит над собой господства.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Особенность Верхней Сванетии &ndash; <b>сванские башни</b>, расположенные в каждом поселке в больших количествах. Чрезвычайно прочные лавиноустойчивые сооружения, назначение которых полностью до сих пор не выяснено. Также в Верхней Сванетии находится множество древних церквей, построенных в IX &ndash; XII веках.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Место, откуда мы начнем обзорную экскурсию, является районным центром современной Сванетии, поселок городского типа &ndash; <b>Местия</b>. Несмотря на свои довольно скромные размеры, Местия на протяжении столетий была и остается культурным и религиозным центром Верхней Сванетии. Многочисленные средневековые церкви и укрепления Местийского района включены в список Всемирного Наследия ЮНЕСКО.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Местия &ndash; один из достопримечательных памятников грузинского зодчества и этнографии. У Вас будет возможность посетить <b>музей истории и этнографии</b>. Войдя в это здание, перед глазами появятся интерьеры сванских жилищ. Кажется, что все эти предметы так и ждут, когда появятся люди и снова возьмут их в руки. В музее хранятся материалы, найденные в ходе раскопок : образцы гравюр и уникальных икон, древнейшие рукописи, шоловные уборы раннего и позднего Средневековья, украшения из железа, серебра и меди, ювелирные изделия, керамика, образцы тканей и многое другое.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Музей Михаила Хергиани</b> &ndash; одна из главных достопримечательностей столицы Сванетии. Быть в Местия и не побывать здесь &ndash; все равно что не зайти внутрь сванской башни, или не попробовать местной еды. Музей располагается в доме, где жил альпинист, и состоит из четырех залов: этнографического, зала истории советского альпинизма, зала восхождений Михаила Хергиани, личной комнаты альпиниста и зала памяти о спортсмене.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночевка в Местия.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 14</b>. <b>Местия &ndash; Ушгули &ndash; Местия</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Поездка в село Ушгули.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 7 ч.</span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Путешествие продолжим в село <b>Ушгули</b> &ndash; самое знатное место во всей Верхней Сванетии, которое находится у подножья горы <img alt=\"\" src=\"/uploads/images/turebi/ushguli.JPG\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />Шхара ( 5193 м над уровнем моря ) на высоте от 2060 до 2200 метров над уровнем моря, что позволяет утверждать, что Ушгули является самым высоким поселением в Европе. Центр коммуны, деревня Чажаши, была внесена в список Всемирного Наследия ЮНЕСКО.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">По дороге осмотрим так называемую <b>Башню Любви</b>, построенную на огромнейшей глыбе на территории общины Ипари. Здесь до недавнего времени хранилась самая старая рукопись грузинского народа, написанная на грузинском языке, созданная в IX веке. Сейчас перенесена в музей Местии.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ушгули &ndash; это не просто община. Когда попадаешь сюда, то кажется, что ты на какой &ndash; то невидимой машине времени просто переместился совершенно в другой век. Здесь целый лес с', '/uploads/images/turebi/6%20days%205%20night/martvili%20pat.jpg', '/uploads/images/turebi/martvili.jpg,/uploads/images/turebi/batumi344.png,/uploads/images/turebi/batumi312.jpg,/uploads/images/turebi/martvili4.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B822.png,/uploads/images/turebi/batumi4321.jpg,/uploads/images/turebi/MARTVILI-compressor.jpg,/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B823.jpg,/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B8.jpg,/uploads/images/turebi/%D0%A3%D0%B4%D0%B6%D0%B0%D1%80%D0%BC%D0%B0.jpg,/uploads/images/turebi/%D0%A3%D0%B4%D0%B6%D0%B0%D1%80%D0%BC%D0%B03.jpg,/uploads/images/turebi/%D0%90%D0%BB%D0%B0%D0%B2%D0%B5%D1%80%D0%B4%D0%B8.jpg,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpeg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C1.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8212.jpg,/uploads/images/turebi/bakuriani.jpg,/uploads/images/turebi/bakuriani2.jpg,/uploads/images/turebi/batumi543.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(169, '05-дней-04-ночей-1-2-3', '05-дней-04-ночей-1-2-3', '05-дней-04-ночей-1-2-3', '14', NULL, '05 дней / 04 ночей', '05 дней / 04 ночей', '05 дней / 04 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Сигнахи &ndash; Бодбийский монастырь &ndash; Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Отъезд &ndash; Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B823334.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в бывшую столицу Грузии &ndash; Мцхета. <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8F%20%D0%94%D0%B6%D0%B2%D0%B0%D1%80%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Сигнахи &ndash; Бодбийский монастырь &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ОБИТЕЛЬ ПРЕКРАСНОГО</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 5 &ndash; 7 ч.</b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Маршрут</span></span></b> <b>:</b> <b><i>Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</i></b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь<img alt=\"\" src=\"/uploads/images/turebi/%D0%91%D0%BE%D0%BB%D0%BD%D0%B8%D1%81%D0%B8%20%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B8%2021.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" /> покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ.</p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Отъезд &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B8%20pat6.jpg', '/uploads/images/turebi/tbilisi5464.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C(1).jpg,/uploads/images/turebi/tbilisi86.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B81.jpg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B824.jpeg,/uploads/images/turebi/mcxeta4534.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C.jpg,/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%2016.jpg,/uploads/images/turebi/%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B834.jpg,/uploads/images/turebi/%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg', '', 0, 0, 0, 1),
(170, '05-дней-04-ночей-1-2-3-4', '05-дней-04-ночей-1-2-3-4', '05-дней-04-ночей-1-2-3-4', '14', NULL, '05 дней / 04 ночей', '05 дней / 04 ночей', '05 дней / 04 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1.</b> Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси / Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</p>\r\n\r\n<p><b>День 5.</b> Отъезд &ndash; Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1.</b> <b>Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2.</b> <b>Тбилиси / Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">&laquo; По двум столицам &raquo;</span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"color:#002060\">&nbsp;( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3.</b> <b>Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию &laquo; <b><i>&nbsp;</i></b>&raquo;</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</p>\r\n\r\n<p>Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ. <b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"color:#002060\">( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4.</b> <b>Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ПУТИ К ВЕЛИКАНУ</span></span></i></b> &raquo;.</p>\r\n\r\n<p><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b><img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%92%D0%BE%D0%B5%D0%BD%D0%BD%D0%BE%20%E2%80%93%20%D0%B3%D1%80%D1%83%D0%B7%D0%B8%D0%BD%D1%81%D0%BA%D0%B0%D1%8F%20%D0%B4%D0%BE%D1%80%D0%BE%D0%B3%D0%B0.jpg\" style=\"margin: 12px; width: 300px; height: 200px; float: left;\" />Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i>.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5.</b> <b>Отъезд &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%92%D0%BE%D0%B5%D0%BD%D0%BD%D0%BE%20pat.jpg', '/uploads/images/turebi/yazbegi12.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B845.jpg,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpg,/uploads/images/turebi/bodbis%20monasteri.jpg,/uploads/images/turebi/mcxeta6556.jpg,/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0%201.jpg,/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg,/uploads/images/turebi/tbilisi654433.jpg,/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20324.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B8.jpg', '', 0, 0, 0, 1),
(171, '05-дней-04-ночей-1-2-3-4-5', '05-дней-04-ночей-1-2-3-4-5', '05-дней-04-ночей-1-2-3-4-5', '14', NULL, '05 дней / 04 ночей', '05 дней / 04 ночей', '05 дней / 04 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси / Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Ананури &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Боржоми &ndash; Ахалцихе &ndash; Вардзия &ndash; Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Отъезд - Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси / Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Тбилиси ( пешая ) и в город Мцхета &nbsp;&nbsp;<b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">&laquo; По двум столицам &raquo;</span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете сам<img alt=\"\" src=\"/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B83.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />ую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8F%20%D0%94%D0%B6%D0%B2%D0%B0%D1%80%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"color:#002060\">&nbsp;( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Ананури &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; Грузинской дороге и пещерный город Уплисцихе.</p>\r\n\r\n<p><b>Продолжительность тура : 6 &ndash; 8 ч.</b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%96%D0%B8%D0%BD%D0%B2%D0%B0%D0%BB%D0%B8%20pat.jpg\" style=\"margin: 12px; width: 300px; height: 201px; float: left;\" />В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p><b>Город Гори</b> расположен в 89 км от Тбилиси на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</p>\r\n\r\n<p>Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</p>\r\n\r\n<p>Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон. <b><i><span style=\"color:#002060\">( посещение музея по желанию )</span></i></b></p>\r\n\r\n<p>В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Боржоми &ndash; Ахалцихе &ndash; Вардзия &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в Самцхе &ndash; Джавахети. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>НАСЛЕДИЕ ЗОЛОТОГО ВЕКА</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 8 &ndash; 10 ч.</b></p>\r\n\r\n<p>С древних времен <b>Джавахети</b> был одним из эриставств ( княжеств ) Картлинского царства. Географическое расположение Месхети создавало выгодные условия для культурного и экономического развития страны. Оно способствовало установить связь между старыми культурами известных на территории Грузии и с центрами цивилизаций Ближнего Востока.</p>\r\n\r\n<p>Период царствования Царицы Тамар ( XII &ndash; XIII вв. ) считался Золотым веком Грузии. В этот период начались большие строительства. Среди них самым удивительным считается Вардзия &ndash; наш последний пункт по этому маршруту.</p>\r\n\r\n<p>По дороге к высеченному городу в скале, Вы посетите курортный город <b>Боржоми</b>, который окружен горными массивами, покрытыми хвойными и широколиственными лесами. Боржоми известен своей лечебной минеральной водой. Минеральные источники были обнаружены более тысячи лет назад, хотя вторую жизнь им даровал в 1829 году Херсонский гренадерский полк. В парке Боржоми наш гид расскажет Вам историю минеральной воды; при этом у Вас будет возможность попробовать воду прямо из источника и ознакомиться с достопримечательностями этого курорта.</p>\r\n\r\n<p>Из Боржоми Вы направитесь в город <b>Ахалцихе</b> ( старое название &ndash; Ломсия ), где еще в IX веке находился город &ndash; дворец османских <img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B51.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />завоевателей, так называемый<b> Рабат</b> ( с арабского &laquo; укрепленное место &raquo; ). Рабат &ndash; это один большой город в городе. В комплексе кроме исторических крепостей, башен, церквей, келий и мечети существуют и современные учреждения разного типа.</p>\r\n\r\n<p>Редко где Вы сможете встретить столько красоты, столько достоинства и величия. Редко какая крепость может похвастаться своей необычной историей и своими царскими корнями. Вся атмосфера крепости дышит великими событиями, которые происходили здесь столько веков назад.</p>\r\n\r\n<p>Продолжая путешествие, Вы посетите высеченный в скале комплекс<b> Вардзия</b> &ndash; один из важнейших и впечатляющих исторических памятников Грузии. Вардзия &ndash; памятник&nbsp; XII века, предназначенный для военной крепости и грандиозный укрепленный монастырь.Комплекс Вардзия простирается на 500 м и состоит из 13 этажей, который связывают друг с другом сложная система тоннелей.</p>\r\n\r\n<p>Наш гид повествует Вам историю строительства этого великолепного памятника, который сохранил следы многих трагических событий. Вы окунетесь во времена царствования&nbsp; великой царицы Тамары, узнаете много исторических фактов и легенд, связанных с ней и на мгновенье представите мир ее глазами...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Отъезд - Тбилиси </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/vardzia%20pat1.jpg', '/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/Borjomi987.jpg,/uploads/images/turebi/vardzia23.jpg,/uploads/images/turebi/mcxeta4534.jpg,/uploads/images/turebi/mcxeta34.jpg,/uploads/images/turebi/borjomi34.jpg,/uploads/images/turebi/uflistsikhe5422.jpg,/uploads/images/turebi/vardzia2.jpg,/uploads/images/turebi/%D0%90%D1%85%D0%B0%D0%BB%D1%86%D0%B8%D1%85%D0%B513.jpg,/uploads/images/turebi/%D0%9B%D0%B5%D0%B3%D0%B2%D1%82%D0%B0%D1%85%D0%B5%D0%B2%D0%B82.jpg,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpeg,/uploads/images/turebi/abanotubani12.jpg,/uploads/images/turebi/%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B822.png', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(172, '06-дней-05-ночей', '06-дней-05-ночей', '06-дней-05-ночей', '11', NULL, '06 дней / 05 ночей', '06 дней / 05 ночей', '06 дней / 05 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси&nbsp;</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи&nbsp; &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Мцхета - Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги - Тбилиси</p>\r\n\r\n<p><b>День 6</b>. Отъезд - Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси&nbsp; </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи&nbsp; &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию &laquo; <b><i>&nbsp;</i></b>&raquo;</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). <img alt=\"\" src=\"/uploads/images/turebi/%D0%93%D0%BE%D0%BC%D0%B1%D0%BE%D1%80%D1%81%D0%BA%D0%B8%D0%B8%CC%86-%D0%BF%D0%B5%D1%80%D0%B5%D0%B2%D0%B0%D0%BB.jpg\" style=\"margin: 12px; width: 300px; height: 200px; float: right;\" />Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</p>\r\n\r\n<p>Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ. <b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"color:#002060\">( Дегустация вин / чачи / обед за дополнительную плату )</span></i></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в бывшую столицу Грузии &ndash; Мцхета. <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0223.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ПУТИ К ВЕЛИКАНУ</span></span></i></b> &raquo;.</p>\r\n\r\n<p><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i>.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6. Отъезд </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/tbilisi543pat.jpg', '/uploads/images/turebi/yazbegi3.jpg,/uploads/images/turebi/abanotubani12.jpg,/uploads/images/turebi/abanot.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%2032.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20324.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C1.jpg,/uploads/images/turebi/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B8123.jpg,/uploads/images/turebi/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B81.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%20%203.jpg,/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B845.jpg,/uploads/images/turebi/yazbegi4.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(173, '14-дней-13-ночей', '14-дней-13-ночей', '14-дней-13-ночей', '15', NULL, '14 дней / 13 ночей', '14 дней / 13 ночей', '14 дней / 13 ночей', '', '', '', '', '', '<p><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси&nbsp;</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash;Мцхета &ndash;Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги - Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Тбилиси &ndash;Кахетия</p>\r\n\r\n<p><b>День 6</b>. Кахетия &ndash; Кутаиси</p>\r\n\r\n<p><b>День 7.</b> Кутаиси &ndash; Сванетия</p>\r\n\r\n<p><b>День 8.</b> Сванетия - Местия</p>\r\n\r\n<p><b>День 9.</b> Местия &ndash; Ушгули -Батуми</p>\r\n\r\n<p><b>День 10.</b> Батуми &ndash; Гонио &ndash; Махунцети &ndash; Батуми</p>\r\n\r\n<p><b>День 11.</b> Батуми&nbsp; ( самостоятельный отдых )</p>\r\n\r\n<p><b>День 12</b>. Батуми&nbsp; ( самостоятельный отдых )</p>\r\n\r\n<p><b>День 13</b>. Батуми&nbsp; ( самостоятельный отдых )</p>\r\n\r\n<p><b>День 14.</b> Отъезд<b> - </b>&nbsp;Батуми&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси&nbsp; </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i><span style=\"color:#7030a0\">( подъем на канатке с парка Рике к Нарикале )</span></i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B824.jpeg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3</b>. <b>Тбилиси &ndash; Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в бывшую столицу Грузии &ndash; Мцхета. <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%9C%D1%86%D1%85%D0%B5%D1%82%D0%B0223.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ПУТИ К ВЕЛИКАНУ</span></span></i></b> &raquo;.</p>\r\n\r\n<p><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорош<img alt=\"\" src=\"/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />о сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горн</b><b>о &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице &nbsp;)</i>.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5 &ndash; 6 </b>. <b>Тбилиси &ndash; Уджарма &ndash; Ахали Шуамта &ndash; Алаверди &ndash; Телави &ndash;Цинандали &ndash; Греми &ndash; &laquo; Корпорация Киндзмараули &raquo; - Бодбийский монастырь -&nbsp; Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : &nbsp;1 &ndash; ый день - 6 &ndash; 8 ч</span></span></i></b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">.</span></span></p>\r\n\r\n<p><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <b><i>2 &ndash; ой день &ndash; 6 &ndash; 8 ч.</i></b></span></span></p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Первый день</span></span></i></b></p>\r\n\r\n<p>Кахетия &ndash; один из самых больших регионов в Восточной Грузии. Некогда самостоятельное царство. Царство бесконечных виноградников и живописных гор, старинных традиций и загадочных мертвых городов. Царство самых вкусных грузинских вин и спокойной печали древних монастырей.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала </b>( 2000 м над уровнем моря ) &ndash; царство лесных великанов. Высокие, прямоствольные деревья достигают толщины в несколько обхватов. Густые кроны, почти не пропуская лучей солнца, создают сплошной шатер и только светлые буковые стволы не дают этому &laquo; темному царству &raquo; погрузиться во мрак.</p>\r\n\r\n<p>На заросшем склоне горы Цивгомбори находится Хахульский женский монастырь Богородицы -&nbsp; <b>Ахали Шуамта</b> ( Новое Межгорье, XVI в. ) &ndash; центрально-купольная церковь и колокольня. Самым главным объектом молитв монастыря была икона Хахульской Богоматери, украшенная золотом и драгоценными камнями, на обратной стороне которой находились 44 святыни. Сегодня эта икона находится в сокровищнице Тбилисского государственного музея искусств. Монастырь являлся важным духовным очагом Кахетии. Сыграл также и&nbsp; политическую роль, так как именно это место было избрано для встречи царя Кахетии Александра II и Бориса Годунова, чтобы решить вопрос о помощи государства Российского христианским странам в борьбе с захватчиками.</p>\r\n\r\n<p>В центре Алазанской долины Вы посетите величайший храм средневековой Грузии &ndash; <b>Алавердский Кафедральный собор</b> ( Собор Святого Георгия, XI в. ) и монастырский комплекс. Храм, поражающий своим видом и великолепием, является одним из крупнейших соборов в Грузии, связанный с именем одного из тринадцати сирийских первооснователей грузинского монашества &ndash; Иосифом Алавердели.</p>\r\n\r\n<p>Собор Алаверди представлен на включение в список Всемирного наследия ЮНЕСКО.</p>\r\n\r\n<p>Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>Возвращение в Телави.</p>\r\n\r\n<p>Ночь в Телави.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Второй день</span></span></i></b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Посетим город <b>Греми</b>, престольный город богатого и успешного Кахетинского царства ( XVI в. ). В комплекс входят : церковь Архангелов, царская палата с колокольней, часовня св. Георгия с башней, на горе Самеба &ndash; круглая часовня Святой Троицы. В Греми Вам повествуют историю жизни кахетинских царей и цариц, о роли каждого из них в жизни города и царства в целом. Увидите надземные и подземные элементы городских строений или урбанской инфраструктуры</p>\r\n\r\n<p><b>&laquo; Корпорация Киндзмараули &raquo;</b> - один из крупнейших винных заводов на территории Кахетии, который действовал еще во времена СССР, но в 1993 году был выкуплен в частную собственность и модернизирован. Самые же старые постройки относятся к 1533 году и многие из них можно увидеть и потрогать руками. Винзавод производит много разных вин, причем ввиду своего давнего существования он имеет в запасах даже коньяки 23 &ndash; летней выдержки.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ.</p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;Переезд в Кутаиси.</p>\r\n\r\n<p>Ночь в Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;<b>День 7</b>.<b> Кутаиси &ndash; Гелатский монастырь &ndash; Моцамета - Пещера Прометея &ndash; Сванетия ( Местия )</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по окрестностям Кутаиси.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>Затем Вы посетите выдающийся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p>Путешествие продолжится в<b> монастырь Моцамета</b> &ndash; красочное место Грузии, который буквально утопает в зелени деревьев и только черепица выдает присутствие храма в буйной растительности. В этом уютном месте наш гид повествует Вам историю и легенды, связанные с этим монастырем. Моцамета &ndash; это не просто место, затерянное в горах. Это красивый храм, где можно&nbsp; насладится пением птиц, вдохнуть свежий высокогорный воздух и понять, что даже время не властно разрушить всю эту вечную красоту природы.</p>\r\n\r\n<p>У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке.</p>\r\n\r\n<p>Переезд в Сванетию ( Местия ).</p>\r\n\r\n<p>Ночевка в отеле Местия.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 8</b>. <b>Местия<img alt=\"\" src=\"/uploads/images/turebi/mestia%20pp.jpg\" style=\"margin: 12px; width: 300px; height: 180px; float: right;\" /> </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Обзорная экскурсия по Местия.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 4 &ndash; 5 ч.</span></span></b></p>\r\n\r\n<p><b>Верхняя Сванетия</b> &ndash; горный район на северо &ndash; западе Грузии. В котловине, ограниченной юго &ndash; западным склоном Большого Кавказа и Сванетским хребтом, находится множество небольших поселений, которые органично вписаны в окружающий ландшафт и отличаются самобытной архитектурой. История Сванетии уходит корнями в далекое прошлое.</p>\r\n\r\n<p>Исторически Верхняя Сванетия делится на Восточную и Западную, в других вариантах &ndash; на Вольную и Княжескую. Восточная ( Вольная ) Сванетия дошла до нашего времени в более архаичном состоянии, с более традиционным укладом жизни и с большим количеством каменных сооружений. Здесь множество интересных объектов и Вы сможете сконцентрировать свое внимание именно на этой части Сванетии.</p>\r\n\r\n<p>История гордого, мужественного и свободолюбивого народа сванов, который по сей день сохранил свой язык, насчитывает несколько тысячелетий. Сваны никогда не вели захватнических войн и никогда не были порабащены врагами. У сванов никогда не было крепостного права, да и дворянство носило условный характер. Ведь каждый сван &ndash; это личность, он не терпит над собой господства.</p>\r\n\r\n<p>Особенность Верхней Сванетии &ndash; <b>сванские башни</b>, расположенные в каждом поселке в больших количествах. Чрезвычайно прочные лавиноустойчивые сооружения, назначение которых полностью до сих пор не выяснено. Также в Верхней Сванетии находится множество древних церквей, построенных в IX &ndash; XII веках.</p>\r\n\r\n<p>Место, откуда мы начнем обзорную экскурсию, является районным центром современной Сванетии, поселок городского типа &ndash; <b>Местия</b>. Несмотря на свои довольно скромные размеры, Местия на протяжении столетий была и остается культурным и религиозным центром Верхней Сванетии. Многочисленные средневековые церкви и укрепления Местийского района включены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Местия &ndash; один из достопримечательных памятников грузинского зодчества и этнографии. У Вас будет возможность посетить <b>музей истории и этнографии</b>. Войдя в это здание, перед глазами появятся интерьеры сванских жилищ. Кажется, что все эти предметы так и ждут, когда появятся люди и снова возьмут их в руки. В музее хранятся материалы, найденные в ходе раскопок : образцы гравюр и уникальных икон, древнейшие рукописи, шоловные уборы раннего и позднего Средневековья, украшения из железа, серебра и меди, ювелирные изделия, керамика, образцы тканей и многое другое.</p>\r\n\r\n<p><b>Музей Михаила Хергиани</b> &ndash; одна из главных достопримечательностей столицы Сванетии. Быть в Местия и не побывать здесь &ndash; все равно что не зайти внутрь сванской башни, или не попробовать местной еды. Музей располагается в доме, где жил альпинист, и состоит из четырех залов: этнографического, зала истории советского альпинизма, зала восхождений Михаила Хергиани, личной комнаты альпиниста и зала памяти о спортсмене.</p>\r\n\r\n<p>Ночевка в Местия.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 9.</b> <b>Местия &ndash; Ушгули &ndash; Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в село Ушгули. <b><span style=\"color:#002060\">. <i>( поездка на внедорожнике )</i>.</span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 7 ч.</span></span></b></p>\r\n\r\n<p>Путешествие продолжим в село <b>Ушгули</b> &ndash; самое знатное место во всей Верхней Сванетии, которое находится у подножья горы Шхара ( 5193 м над уровнем моря ) на высоте от 2060 до 2200 метров над уровнем моря, что позволяет утверждать, что Ушгули является самым высоким поселением в Европе. Центр коммуны, деревня Чажаши, была внесена в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>По дороге осмотрим так называемую <b>Башню Любви</b>, построенную на огромнейшей глыбе на территории общины Ипари. Здесь до недавнего времени хранилась самая старая рукопись грузинского народа, написанная на грузинском языке, созданная в IX веке. Сейчас перенесена в музей Местии.</p>\r\n\r\n<p>Ушгули &ndash; это не просто община. Когда попадаешь сюда, то кажется, что ты на какой &ndash; то невидимой машине времени просто переместился совершенно в другой век. Здесь целый лес сванских башен стоит в молчаливом окружении таких же древних домов, которые очень похожи на крепости.</p>\r\n\r\n<p>На окраине Ушгули на одиноком холме стоит древняя церковь <b>Ламария </b>( Успения Святой Богородицы, X в. ) &ndash; одна из святынь Ушгульского общества. К расположенному на высоком холме храму примыкают башня и высокая крепостная ограда. Местные жители верят, что именно под этой церковью захоронена царица Тамара, наделенная сванами всеми христианскими благодетелями.</p>\r\n\r\n<p>Переезд в город Батуми.</p>\r\n\r\n<p>Ночь в отеле Батуми.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 10.</b> <b>Батуми &ndash; Гонио - Махунцети ( Горная Аджария ) &ndash; Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в горную Аджарию.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 4 - 6 ч.</span></span></b></p>\r\n\r\n<p>В 12 км к югу от Батуми находится <b>крепость Гонио &ndash; Апсарос</b>, а в ее окрестностях &ndash; одно из древнейших поселений Грузии. В прошлом крепость Гонио &ndash; Апсарос имела уникальное стратегическое значение: она защищала подходы к ущельям рек Чорохи и Ачарисцкали, которые связывали внутренние регионы Юго &ndash; Западной Грузии с Черноморским побережьем. Крепость Гонио была опорной цитаделью на Черноморском побережье сначала Византийской, а затем и Османской империй. Соответственно, крепость включает и несколько культурных пластов : римский&nbsp; (III - IV в. до н. э. ), византийский ( VI &ndash; VII в. ) и османский ( XVI в. )</p>\r\n\r\n<p>Стратегическое расположение Аджарии на перекресте торговых путей ( Северный Кавказ- Малая Азия, Турция &ndash; Самцхе &ndash; Артануджи и так далее ) обусловило необходимость поддержания в надлежащем состоянии существующих дорог и мостов. Сохранившиеся в Аджарии древние арочные каменные мосты являются важнейшими памятниками грузинской материальной культуры. В результате последних исследований выявлено около 25 сохранившихся в Аджарии арочных мостов, строительство которых, согласно устным сказаниям, связано с именем царицы Тамар.</p>\r\n\r\n<p>В селе Махунцети расположен <b>мост</b> <b>&ndash;</b> <b>один из самых крупных арочных мостов Аджарии.</b> Он построен из скальных камней вулканического происхождения, соединенных между собой известковым раствором.</p>\r\n\r\n<p>По дороге Вы увидете самую длинную реку Аджарии &ndash; Аджарисцкали и&nbsp; ее слияние с Чорохи, а также <b>горный водопад Махунцети</b> с кристально чистой водой.</p>\r\n\r\n<p>Ночевка в отеле Батуми.</p>\r\n\r\n<p><b>День 11. Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><i>Самостоятельный отдых.</i></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 12. Батуми&nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/uploads/images/turebi/batumi%20pat2.jpg\" style=\"margin: 12px; float: right; width: 290px; height: 295px;\" /></b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><i>Самостоятельный отдых.</i></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 13. Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p><i>Самостоятельный отдых.</i></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 14. Отъезд</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт Батуми.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><b><span lang=\"RU\" style=\"font-size:18.0pt\"><span style=\"line-height:115%\"><span style=\"color:#c00000\">Краткая информация</span></span></span></b></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Наземное обслуживание</span></span></b> &ndash;&nbsp; для индивидуальных путешественников мы предлагаем легковую машину.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Гиды</span></span></b> &ndash; Мы обеспечиваем услуги профессиональных опытных гидов. Во время пребывания в Грузии, гид - Ваш основной контактный персонал. В случае неотложной необходимости, Вы также можете связаться с представителем нашей компании.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Проживание</span></span></b> &ndash; на протяжении туров мы обеспечиваем проживание в отелях, выбранной Вами категории. Ранний заезд, так же как и поздний выезд, согласовывается заранее и оплачивается дополнительно.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span style=\"color:#7030a0\">В цену входит :</span></span></span></b></p>\r\n\r\n<p><b>Встреча / проводы в аэропорту Тбилиси, трансферы</b></p>\r\n\r\n<p><b>Проживание в 2 &ndash; местном номере ( DBL</b><b> TWIN</b><b> STANDART</b><b> ) в Тбилиси, Кахетии, Кутаиси в отеле 3*</b></p>\r\n\r\n<p><b>Завтраки</b></p>\r\n\r\n<p><b>Транспортное обслуживание по программе</b></p>\r\n\r\n<p><b>Услуги русскоговорящего гида</b></p>\r\n\r\n<p><b>Входные билеты на объекты</b></p>\r\n\r\n<p><b>Дегустации по программе</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#c00000\">Общая стоимость подобранного для Вас экскурсионного пакета на 2 персоны&nbsp; составляет &ndash;&nbsp; 118 000 РУБ. ( &nbsp;сто восемнадцать тысяч рублей ).</span></span></span></b></p>\r\n\r\n<p><b>&nbsp;</b></p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span calibri=\"\" style=\"font-family:\">Любая из нижепредставленных программ может пересмотреться согласно Вашему предпочтению и пожеланиям.</span></span></span></i></b></p>\r\n', '/uploads/images/turebi/batumi%20pat2.jpg', '/uploads/images/turebi/batumi32.jpg,/uploads/images/turebi/batumi4321.jpg,/uploads/images/turebi/batumi98.jpg,/uploads/images/turebi/Kutaisi.JPG,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B8.jpg,/uploads/images/turebi/ushguli32.jpg,/uploads/images/turebi/ushguli54.jpg,/uploads/images/turebi/tbilisi5464.jpg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B824.jpeg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20324.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B82.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(174, '9-дней-8-ночей', '9-дней-8-ночей', '9-дней-8-ночей', '12', NULL, '9 дней / 8 ночей', '9 дней / 8 ночей', '9 дней / 8 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси&nbsp;</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash;Мцхета &ndash;Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги - Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Тбилиси &ndash;Кахетия</p>\r\n\r\n<p><b>День 6</b>. Кахетия &ndash; Кутаиси</p>\r\n\r\n<p><b>День 7.</b> Кутаиси &ndash; Сванетия</p>\r\n\r\n<p><b>День 8.</b> Сванетия - Местия</p>\r\n\r\n<p><b>День 9.</b> Местия &ndash; Ушгули -Батуми</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси&nbsp;</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i><span style=\"color:#7030a0\">( подъем на канатке с парка Рике к Нарикале )</span></i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3</b>. <b>Тбилиси &ndash; Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в бывшую столицу Грузии &ndash; Мцхета. <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/mcxeta34.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ПУТИ К ВЕЛИКАНУ</span></span></i></b> &raquo;.</p>\r\n\r\n<p><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расска<img alt=\"\" src=\"/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />жет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице &nbsp;)</i>.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5 &ndash; 6 </b>. <b>Тбилиси &ndash; Уджарма &ndash; Ахали Шуамта &ndash; Алаверди &ndash; Телави &ndash;Цинандали &ndash; Греми &ndash; &laquo; Корпорация Киндзмараули &raquo; - Бодбийский монастырь -&nbsp; Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : &nbsp;1 &ndash; ый день - 6 &ndash; 8 ч</span></span></i></b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">.</span></span></p>\r\n\r\n<p><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b><i>2 &ndash; ой день &ndash; 6 &ndash; 8 ч.</i></b></span></span></p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Первый день</span></span></i></b></p>\r\n\r\n<p>Кахетия &ndash; один из самых больших регионов в Восточной Грузии. Некогда самостоятельное царство. Царство бесконечных виноградников и живописных гор, старинных традиций и загадочных мертвых городов. Царство самых вкусных грузинских вин и спокойной печали древних монастырей.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала </b>( 2000 м над уровнем моря ) &ndash; царство лесных великанов. Высокие, прямоствольные деревья достигают толщины в несколько обхватов. Густые кроны, почти не пропуская лучей солнца, создают сплошной шатер и только светлые буковые стволы не дают этому &laquo; темному царству &raquo; погрузиться во мрак.</p>\r\n\r\n<p>На заросшем склоне горы Цивгомбори находится Хахульский женский монастырь Богородицы -&nbsp; <b>Ахали Шуамта</b> ( Новое Межгорье, XVI в. ) &ndash; центрально-купольная церковь и колокольня. Самым главным объектом молитв монастыря была икона Хахульской Богоматери, украшенная золотом и драгоценными камнями, на обратной стороне которой находились 44 святыни. Сегодня эта икона находится в сокровищнице Тбилисского государственного музея искусств. Монастырь являлся важным духовным очагом Кахетии. Сыграл также и&nbsp; политическую роль, так как именно это место было избрано для встречи царя Кахетии Александра II и Бориса Годунова, чтобы решить вопрос о помощи государства Российского христианским странам в борьбе с захватчиками.</p>\r\n\r\n<p>В центре Алазанской долины Вы посетите величайший храм средневековой Грузии &ndash; <b>Алавердский Кафедральный собор</b> ( Собор Святого Георгия, XI в. ) и монастырский комплекс. Храм, поражающий своим видом и великолепием, является одним из крупнейших соборов в Грузии, связанный с именем одного из тринадцати сирийских первооснователей грузинского монашества &ndash; Иосифом Алавердели.</p>\r\n\r\n<p>Собор Алаверди представлен на включение в список Всемирного наследия ЮНЕСКО.</p>\r\n\r\n<p>Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>Возвращение в Телави.</p>\r\n\r\n<p>Ночь в Телави.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Второй день</span></span></i></b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Посетим город <b>Греми</b>, престольный город богатого и успешного Кахетинского царства ( XVI в. ). В комплекс входят : церковь Архангелов, царская палата с колокольней, часовня св. Георгия с башней, на горе Самеба &ndash; круглая часовня Святой Троицы. В Греми Вам повествуют историю жизни кахетинских царей и цариц, о роли каждого из них в жизни города и царства в целом. Увидите надземные и подземные элементы городских строений или урбанской инфраструктуры</p>\r\n\r\n<p><b>&laquo; Корпорация Киндзмараули &raquo;</b> - один из крупнейших винных заводов на территории Кахетии, который действовал еще во времена СССР, но в 1993 году был выкуплен в частную собственность и модернизирован. Самые же старые постройки относятся к 1533 году и многие из них можно увидеть и потрогать руками. Винзавод производит много разных вин, причем ввиду своего давнего существования он имеет в запасах даже коньяки 23 &ndash; летней выдержки.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ.</p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;Переезд в Кутаиси.</p>\r\n\r\n<p>Ночь в Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;<b>День 7</b>.<b> Кутаиси &ndash; Гелатский монастырь &ndash; Моцамета - Пещера Прометея &ndash; Сванетия ( Местия )</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по окрестностям Кутаиси.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>Затем Вы посетите выдающийся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p>Путешествие продолжится в<b> монастырь Моцамета</b> &ndash; красочное место Грузии, который буквально утопает в зелени деревьев и только черепица выдает присутствие храма в буйной растительности. В этом уютном месте наш гид повествует Вам историю и легенды, связанные с этим монастырем. Моцамета &ndash; это не просто место, затерянное в горах. Это красивый храм, где можно&nbsp; насладится пением птиц, вдохнуть свежий высокогорный воздух и понять, что даже время не властно разрушить всю эту вечную красоту природы.</p>\r\n\r\n<p>У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке.</p>\r\n\r\n<p>Переезд в Сванетию ( Местия ).</p>\r\n\r\n<p>Ночевка в отеле Местия.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 8</b>. <b>Местия </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Обзорная экскурсия по Местия.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 4 &ndash; 5 ч.</span></span></b></p>\r\n\r\n<p><b><img alt=\"\" src=\"/uploads/images/turebi/mestia%20pp.jpg\" style=\"margin: 12px; width: 300px; height: 180px; float: left;\" />Верхняя Сванетия</b> &ndash; горный район на северо &ndash; западе Грузии. В котловине, ограниченной юго &ndash; западным склоном Большого Кавказа и Сванетским хребтом, находится множество небольших поселений, которые органично вписаны в окружающий ландшафт и отличаются самобытной архитектурой. История Сванетии уходит корнями в далекое прошлое.</p>\r\n\r\n<p>Исторически Верхняя Сванетия делится на Восточную и Западную, в других вариантах &ndash; на Вольную и Княжескую. Восточная ( Вольная ) Сванетия дошла до нашего времени в более архаичном состоянии, с более традиционным укладом жизни и с большим количеством каменных сооружений. Здесь множество интересных объектов и Вы сможете сконцентрировать свое внимание именно на этой части Сванетии.</p>\r\n\r\n<p>История гордого, мужественного и свободолюбивого народа сванов, который по сей день сохранил свой язык, насчитывает несколько тысячелетий. Сваны никогда не вели захватнических войн и никогда не были порабащены врагами. У сванов никогда не было крепостного права, да и дворянство носило условный характер. Ведь каждый сван &ndash; это личность, он не терпит над собой господства.</p>\r\n\r\n<p>Особенность Верхней Сванетии &ndash; <b>сванские башни</b>, расположенные в каждом поселке в больших количествах. Чрезвычайно прочные лавиноустойчивые сооружения, назначение которых полностью до сих пор не выяснено. Также в Верхней Сванетии находится множество древних церквей, построенных в IX &ndash; XII веках.</p>\r\n\r\n<p>Место, откуда мы начнем обзорную экскурсию, является районным центром современной Сванетии, поселок городского типа &ndash; <b>Местия</b>. Несмотря на свои довольно скромные размеры, Местия на протяжении столетий была и остается культурным и религиозным центром Верхней Сванетии. Многочисленные средневековые церкви и укрепления Местийского района включены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Местия &ndash; один из достопримечательных памятников грузинского зодчества и этнографии. У Вас будет возможность посетить <b>музей истории и этнографии</b>. Войдя в это здание, перед глазами появятся интерьеры сванских жилищ. Кажется, что все эти предметы так и ждут, когда появятся люди и снова возьмут их в руки. В музее хранятся материалы, найденные в ходе раскопок : образцы гравюр и уникальных икон, древнейшие рукописи, шоловные уборы раннего и позднего Средневековья, украшения из железа, серебра и меди, ювелирные изделия, керамика, образцы тканей и многое другое.</p>\r\n\r\n<p><b>Музей Михаила Хергиани</b> &ndash; одна из главных достопримечательностей столицы Сванетии. Быть в Местия и не побывать здесь &ndash; все равно что не зайти внутрь сванской башни, или не попробовать местной еды. Музей располагается в доме, где жил альпинист, и состоит из четырех залов: этнографического, зала истории советского альпинизма, зала восхождений Михаила Хергиани, личной комнаты альпиниста и зала памяти о спортсмене.</p>\r\n\r\n<p>Ночевка в Местия.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 9.</b> <b>Местия &ndash; Ушгули &ndash; Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в село Ушгули. <b><span style=\"color:#002060\">. <i>( поездка на внедорожнике )</i>.</span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 7 ч.</span></span></b></p>\r\n\r\n<p>Путешествие продолжим в село <b>Ушгули</b> &ndash; самое знатное место во всей Верхней Сванетии, которое находится у подножья горы Шхара ( 5193 м над уровнем моря ) на высоте от 2060 до 2200 метров над уровнем моря, что позволяет утверждать, что Ушгули является самым высоким поселением в Европе. Центр коммуны, деревня Чажаши, была внесена в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>По дороге осмотрим так называемую <b>Башню Любви</b>, построенную на огромнейшей глыбе на территории общины Ипари. Здесь до недавнего времени хранилась самая старая рукопись грузинского народа, написанная на грузинском языке, созданная в IX веке. Сейчас перенесена в музей Местии.</p>\r\n\r\n<p>Ушгули &ndash; это не просто община. Когда попадаешь сюда, то кажется, что ты на какой &ndash; то невидимой машине времени просто переместился совершенно в другой век. Здесь целый лес сванских башен стоит в молчаливом окружении таких же древних домов, которые очень похожи на крепости.</p>\r\n\r\n<p>На окраине Ушгули на одиноком холме стоит древняя церковь <b>Ламария </b>( Успения Святой Богородицы, X в. ) &ndash; одна из святынь Ушгульского общества. К расположенному на высоком холме храму примыкают башня и высокая крепостная ограда. Местные жители верят, что именно под этой церковью захоронена царица Тамара, наделенная сванами всеми христианскими благодетелями.</p>\r\n\r\n<p>Переезд в город Батуми.</p>\r\n\r\n<p>Ночь в отеле Батуми.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:18.0pt\"><span style=\"line-height:115%\"><span style=\"color:#c00000\">Краткая информация</span></span></span></b></p>\r\n\r\n<p align=\"center\" style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Наземное обслуживание</span></span></b> &ndash;&nbsp; для индивидуальных путешественников мы предлагаем легковую машину.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Гиды</span></span></b> &ndash; Мы обеспечиваем услуги профессиональных опытных гидов. Во время пребывания в Грузии, гид - Ваш основной контактный персонал. В случае неотложной необходимости, Вы также можете связаться с представителем нашей компании.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Проживание</span></span></b> &ndash; на протяжении туров мы обеспечиваем проживание в отелях, выбранной Вами категории. Ранний заезд, так же как и поздний выезд, согласовывается заранее и оплачивается дополнительно.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span style=\"color:#7030a0\">В цену входит :</span></span></span></b></p>\r\n\r\n<p><b>Встреча / проводы в аэропорту Тбилиси, трансферы</b></p>\r\n\r\n<p><b>Проживание в 2 &ndash; местном номере ( DBL</b><b> TWIN</b><b> STANDART</b><b> ) в Тбилиси, Кахетии, Кутаиси в отеле 3*</b></p>\r\n\r\n<p><b>Завтраки</b></p>\r\n\r\n<p><b>Транспортное обслуживание по программе</b></p>\r\n\r\n<p><b>Услуги русскоговорящего гида</b></p>\r\n\r\n<p><b>Входные билеты на объекты</b></p>\r\n\r\n<p><b>Дегустации по программе</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#c00000\">Общая стоимость подобранного для Вас экскурсионного пакета на 2 персоны&nbsp; составляет &ndash;&nbsp;&nbsp; 105 000 РУБ. ( сто пять тысяч рублей ).</span></span></span></b></p>\r\n\r\n<p><b>&nbsp;</b></p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">Любая из нижепредставленных программ может пересмотреться согласно Вашему предпочтению и пожеланиям.</span></span></i></b></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/batumi%20pat.jpg', '/uploads/images/turebi/Kutaisi.JPG,/uploads/images/turebi/batumi65.jpg,/uploads/images/turebi/batumi4321.jpg,/uploads/images/turebi/mestia.jpg,/uploads/images/turebi/svaneti.jpg,/uploads/images/turebi/ushguli.JPG,/uploads/images/turebi/ananuri232.jpg,/uploads/images/turebi/mestia765.jpg,/uploads/images/turebi/tbilisi5464.jpg,/uploads/images/turebi/Bagrati_Kutaisi.jpg,/uploads/images/turebi/mestia65.jpg,/uploads/images/turebi/svaneti23.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(175, '9-дней-8-ночей-1', '9-дней-8-ночей-1', '9-дней-8-ночей-1', '12', NULL, '9 дней / 8 ночей', '9 дней / 8 ночей', '9 дней / 8 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси &ndash; Мцхета &ndash; Тбилиси</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4</b>. Тбилиси &ndash; Болниси &ndash; Дманиси &ndash; Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Тбилиси &ndash; Кахетия</p>\r\n\r\n<p><b>День 6.</b> Кахетия &ndash; Кутаиси</p>\r\n\r\n<p><b>День 7.</b> Кутаиси &ndash; Сванетия ( Местия )</p>\r\n\r\n<p><b>День 8.</b> Местия</p>\r\n\r\n<p><b>День 9</b>. Местия &ndash; Ушгули &ndash; Батуми</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 1. Прибытие </b></span></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\">День 2</span></span>. Тбилиси и Мцхета</b></span></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">&laquo; По двум столицам &raquo;</span></span></i></b></p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 8 ч.</span></span></i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i>( подъем на канатке с парка Рике к Нарикале )</i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса <img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%D0%90%D0%BD%D1%87%D0%B8%D1%81%D1%85%D0%B0%D1%82%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 240px; float: right;\" />Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Гори &ndash; Уплисцихе &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в город Гори и Уплисцихе.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 8 ч.</span></span></b></p>\r\n\r\n<p><b>Город Гори</b> расположен в 89 км от Тбилиси на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</p>\r\n\r\n<p>Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/stalin.jpg\" style=\"margin: 12px; width: 300px; height: 195px; float: left;\" />Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.</p>\r\n\r\n<p>В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</p>\r\n\r\n<p>С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</p>\r\n\r\n<p>Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночевка в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">День 4</span></span></b><b>. Тбилиси &ndash; Дманиси &ndash; Болниси&nbsp; &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Квемо ( Нижняя ) Картли&nbsp;&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ГЛУБИНЕ ИСТОРИИ</span></span></i></b> &raquo;.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 - 8 ч.</span></span></b></p>\r\n\r\n<p>Невозможно представить&nbsp; Средневековую Грузию без города &ndash; крепости<b> Дманиси</b>. Здесь в те далекие времена проходила бойкая торговля самым разнообразным товаром. Здесь проживало много ремесленников, которые на заказ могли изготовить красивые кувшины или сшить прочные сапоги из кожи. Здесь можно было легко обменять один товар на другой. Дманиси был перевалочным пунктом на Великом Шелковом Пути. Но не только торговлей занимался Дманиси. Эта была еще и очень хорошо укрепленная крепость, которая помогала защищать остальную территорию Грузии. Общая территория крепости когда &ndash; то составляла 25 Га. Во время раскопок на территории города &ndash; крепости Дманиси нашли очень интересные артефакты и человеческие кости, которые теперь ставят под вопрос саму теорию эволюции человочества. Именно здесь 1,85 млн. лет назад человек прямоходящий ( Homo erectus, Homo georgicus ) уже умел создавать характерные для него инструменты. &laquo; Первых европейцев &raquo; назвали <b>Зезва и Мзия</b>. Их восстановленные образы и археологические находки Вы сможете увидеть в музее, который находится в археологической зоне дманисского городища.</p>\r\n\r\n<p>На территории городища есть два интересных религиозно &ndash; исторических памятника: остатки крепостных сооружений и храм <b>Дманисский Сион </b>( VII в. ).</p>\r\n\r\n<p>Наше путешествие продолжается в живописное местечко Болниси, где на высоте 620 метров над уровнем моря находится памятник грузинской архитектуры Золотого Века &ndash; <b>монастырь Цугругашени</b> ( XIII в. ). Цугругашени далек от мирской жизни, место - тихое и уединенное и человек остается наедине с собой и своими мыслями.</p>\r\n\r\n<p>С нашим гидом Вы посетите древнюю базилику <b>Болнисский Сион</b> ( 478 &ndash; 493 гг. ), которая до наших дней дошла практически без изменений. Болнисский Сион особенно интересен надписями V века на стенах храма. Эти&nbsp; надписи являются древнейшими образцами грузинской письменности.</p>\r\n\r\n<p>Стены базилики хранят тысячи молитв, и храм до сих пор продолжает служить людям. Посещение таких мест обогащает людей не только новыми знаниями и впечатлениями, но и дарует духовную пищу.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5 &ndash; 6 </b>. <b>Тбилиси &ndash; Уджарма &ndash; Ахали Шуамта &ndash; Алаверди &ndash; Телави &ndash;Цинандали &ndash; Греми &ndash; &laquo; Корпорация Киндзмараули &raquo; - Бодбийский монастырь -&nbsp; Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : &nbsp;1 &ndash; ый день - 6 &ndash; 8 ч</span></span></i></b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">.</span></span></p>\r\n\r\n<p><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <b><i>2 &ndash; ой день &ndash; 6 &ndash; 8 ч.</i></b></span></span></p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Первый день</span></span></i></b></p>\r\n\r\n<p>Кахетия &ndash; один из самых больших регионов в Восточной Грузии. Некогда самостоятельное царство. Царство бесконечных виноградников и живописных гор, старинных традиций и загадочных мертвых городов. Царство самых вкусных грузинских вин и спокойной печали древних монастырей.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала </b>( 2000 м над уровнем моря ) &ndash; царство лесных великанов. Высокие, прямоствольные деревья достигают толщины в несколько обхватов. Густые кроны, почти не пропуская лучей солнца, создают сплошной шатер и только светлые буковые стволы не дают этому &laquo; темному царству &raquo; погрузиться во мрак.</p>\r\n\r\n<p>На заросшем склоне горы Цивгомбори находится Хахульский женский монастырь Богородицы -&nbsp; <b>Ахали Шуамта</b> ( Новое Межгорье, XVI в. ) &ndash; центрально-купольная церковь и колокольня. Самым главным объектом молитв монастыря была икона Хахульской Богоматери, украшенная золотом и драгоценными камнями, на обратной стороне которой находились 44 святыни. Сегодня эта икона находится в сокровищнице Тбилисского государственного музея искусств. Монастырь являлся важным духовным очагом Кахетии. Сыграл также и&nbsp; политическую роль, так как именно это место было избрано для встречи царя Кахетии Александра II и Бориса Годунова, чтобы решить вопрос о помощи государства Российского христианским странам в борьбе с захватчиками.</p>\r\n\r\n<p>В центре Алазанской долины Вы посетите величайший храм средневековой Грузии &ndash; <b>Алавердский Кафедральный собор</b> ( Собор Святого Георгия, XI в. ) и монастырский комплекс. Храм, поражающий своим видом и великолепием, является одним из крупнейших соборов в Грузии, связанный с именем одного из тринадцати сирийских первооснователей грузинского монашества &ndash; Иосифом Алавердели.</p>\r\n\r\n<p>Собор Алаверди представлен на включение в список Всемирного наследия ЮНЕСКО.</p>\r\n\r\n<p>Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>Возвращение в Телави.</p>\r\n\r\n<p>Ночь в Телави.</p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Второй день</span></span></i></b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Посетим город <b>Греми</b>, престольный город богатого и успешного Кахетинского царства ( XVI в. ). В комплекс входят : церковь Архангелов, царская палата с колокольней, часовня св. Георгия с башней, на горе Самеба &ndash; круглая часовня Святой Троицы. В Греми Вам повествуют историю жизни кахетинских царей и цариц, о роли каждого из них в жизни города и царства в целом. Увидите надземные и подземные элементы городских строений или урбанской инфраструктуры</p>\r\n\r\n<p><b>&laquo; Корпорация Киндзмараули &raquo;</b> - один из крупнейших винных заводов на территории Кахетии, который действовал еще во времена СССР, но в 1993 году был выкуплен в частную собственность и модернизирован. Самые же старые постройки относятся к 1533 году и многие из них можно увидеть и потрогать руками. Винзавод производит много разных вин, причем ввиду своего давнего существования он имеет в запасах даже коньяки 23 &ndash; летней выдержки.</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится музей Нико Пиросмани, где выставлены 16 его работ.</p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;Переезд в Кутаиси.</p>\r\n\r\n<p>Ночь в Кутаиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;<b>День 7</b>.<b> Кутаиси &ndash; Гелатский монастырь &ndash; Моцамета - Пещера Прометея &ndash; Сванетия ( Местия )</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по окрестностям Кутаиси.</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Мы предлагаем Вам обзорную экскурсию по одному из древнейших городов Грузии &ndash; <b>Кутаиси</b> &ndash; древнюю столицу Колхидского и Имеретинского царств, расположенную в долине реки Риони, на Колхидской низменности. Город упоминается еще в VI &ndash; III вв. до нашей эры. Именно здесь правил легендарный царь Ээт, и в этом месте жила Медея, где развернулись события, описанные в древнегреческих мифах про золотое руно.</p>\r\n\r\n<p>Затем Вы посетите выдающийся памятник грузинского зодчества, центр духовной и научной жизни Средневековья, усыпальницу грузинских царей, культовое место для паломников &ndash; <b>монастырский комплекс Гелати</b> ( XI &ndash; XII вв. ), который находится под охраной ЮНЕСКО как объект культурного и исторического мирового наследия. В ансамбль Гелатского монастыря входят : главный храм Успения Пресвятой Богородицы, малый храм Георгия Победоносца, храм святого Николая Чудотворца, колокольня &ndash; старая тюрьма, могила царя Давида, железные ворота Гянджи, здание академии, каменная ограда.</p>\r\n\r\n<p>Путешествие продолжится в<b> монастырь Моцамета</b> &ndash; красочное место Грузии, который буквально утопает в зелени деревьев и только черепица выдает присутствие храма в буйной растительности. В этом уютном месте наш гид повествует Вам историю и легенды, связанные с этим монастырем. Моцамета &ndash; это не просто место, затерянное в горах. Это красивый храм, где можно&nbsp; насладится пением птиц, вдохнуть свежий высокогорный воздух и понять, что даже время не властно разрушить всю эту вечную красоту природы.</p>\r\n\r\n<p>У Вас будет возможность посетить пещеры, обнаруженные в этом регионе. <b>Пещера Прометея</b> ( Кумистави ) &ndash; одна из ярких достопримечательностей Имеретии, да и Грузии в целом. Это целый комплекс пещер, обнаруженных в 1984 году. Мы предлагаем Вам прогулку среди свисающих сталактитов, торчащих сталагмитов, подземных рек, озер и даже водопадов. Кроме прогулки, в пещере можно прокатиться на лодке по подземной реке.</p>\r\n\r\n<p>Переезд в Сванетию ( Местия ).</p>\r\n\r\n<p>Ночевка в отеле Местия.</p>\r\n\r\n<p><b><img alt=\"\" src=\"/uploads/images/turebi/mestia%20pp.jpg\" style=\"margin: 12px; width: 300px; height: 180px; float: right;\" />День 8</b>. <b>Местия </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Обзорная экскурсия по Местия.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 4 &ndash; 5 ч.</span></span></b></p>\r\n\r\n<p><b>Верхняя Сванетия</b> &ndash; горный район на северо &ndash; западе Грузии. В котловине, ограниченной юго &ndash; западным склоном Большого Кавказа и Сванетским хребтом, находится множество небольших поселений, которые органично вписаны в окружающий ландшафт и отличаются самобытной архитектурой. История Сванетии уходит корнями в далекое прошлое.</p>\r\n\r\n<p>Исторически Верхняя Сванетия делится на Восточную и Западную, в других вариантах &ndash; на Вольную и Княжескую. Восточная ( Вольная ) Сванетия дошла до нашего времени в более архаичном состоянии, с более традиционным укладом жизни и с большим количеством каменных сооружений. Здесь множество интересных объектов и Вы сможете сконцентрировать свое внимание именно на этой части Сванетии.</p>\r\n\r\n<p>История гордого, мужественного и свободолюбивого народа сванов, который по сей день сохранил свой язык, насчитывает несколько тысячелетий. Сваны никогда не вели захватнических войн и никогда не были порабащены врагами. У сванов никогда не было крепостного права, да и дворянство носило условный характер. Ведь каждый сван &ndash; это личность, он не терпит над собой господства.</p>\r\n\r\n<p>Особенность Верхней Сванетии &ndash; <b>сванские башни</b>, расположенные в каждом поселке в больших количествах. Чрезвычайно прочные лавиноустойчивые сооружения, назначение которых полностью до сих пор не выяснено. Также в Верхней Сванетии находится множество древних церквей, построенных в IX &ndash; XII веках.</p>\r\n\r\n<p>Место, откуда мы начнем обзорную экскурсию, является районным центром современной Сванетии, поселок городского типа &ndash; <b>Местия</b>. Несмотря на свои довольно скромные размеры, Местия на протяжении столетий была и остается культурным и религиозным центром Верхней Сванетии. Многочисленные средневековые церкви и укрепления Местийского района включены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Ночь в отеле Местия.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 9.</b> <b>Местия &ndash; Ушгули &ndash; Батуми</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Поездка в село Ушгули. <b><span style=\"color:#002060\">&nbsp;<i>( поездка на внедорожнике )</i>.</span></b></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 &ndash; 7 ч.</span></span></b></p>\r\n\r\n<p>Путешествие продолжим в село <b>Ушгули</b> &ndash; самое знатное место во всей Верхней Сванетии, которое находится у подножья горы Шхара ( 5193 м над уровнем моря ) на высоте от 2060 до 2200 метров над уровнем моря, что позволяет утверждать, что Ушгули является самым высоким поселением в Европе. Центр коммуны, деревня Чажаши, была внесена в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>По дороге осмотрим так называемую <b>Башню Любви</b>, построенную на огромнейшей глыбе на территории общины Ипари. Здесь до недавнего времени хранилась самая старая рукопись грузинского народа, написанная на грузинском языке, созданная в IX веке. Сейчас перенесена в музей Местии.</p>\r\n\r\n<p>Ушгули &ndash; это не просто община. Когда попадаешь сюда, то кажется, что ты на какой &ndash; то невидимой машине времени просто переместился совершенно в другой век. Здесь целый лес сванских башен стоит в молчаливом окружении таких же древних домов, которые очень похожи на крепости.</p>\r\n\r\n<p>На окраине Ушгули на одиноком холме стоит древняя церковь <b>Ламария </b>( Успения Святой Богородицы, X в. ) &ndash; одна из святынь Ушгульского общества. К расположенному на высоком холме храму примыкают башня и высокая крепостная ограда. Местные жители верят, что именно под этой церковью захоронена царица Тамара, наделенная сванами всеми христианскими благодетелями.</p>\r\n\r\n<p>Переезд в город Батуми.</p>\r\n\r\n<p>Ночь в отеле Батуми.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:18.0pt\"><span style=\"line-height:115%\"><span style=\"color:#c00000\">Краткая информация</span></span></span></b></p>\r\n\r\n<p align=\"center\" style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Наземное обслуживание</span></span></b> &ndash;&nbsp; для индивидуальных путешественников мы предлагаем легковую машину.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Гиды</span></span></b> &ndash; Мы обеспечиваем услуги профессиональных опытных гидов. Во время пребывания в Грузии, гид - Ваш основной контактный персонал. В случае неотложной необходимости, Вы также можете связаться с представителем нашей компании.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Проживание</span></span></b> &ndash; на протяжении туров мы обеспечиваем проживание в отелях, выбранной Вами категории. Ранний заезд, так же как и поздний выезд, согласовывается заранее и оплачивается дополнительно.В стоимость программы не входят услуги носильщиков.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span style=\"color:#7030a0\">В цену входит :</span></span></span></b></p>\r\n\r\n<p><b>Встреча / проводы в аэропорту Тбилиси, трансферы</b></p>\r\n\r\n<p><b>Проживание в 2 &ndash; местном номере ( Dbl</b><b> Standart</b><b> ) в Тбилиси в отеле 3*</b></p>\r\n\r\n<p><b>Завтраки</b></p>\r\n\r\n<p><b>Транспортное обслуживание по программе</b></p>\r\n\r\n<p><b>Услуги русскоговорящего гида</b></p>\r\n\r\n<p><b>Входные билеты на объекты</b></p>\r\n\r\n<p><b>Дегустации по программе</b></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#c00000\">Общая стоимость каждого из подобранных для Вас экскурсионных пакетов на 2 персоны &nbsp;составляет &ndash; 55000 РУБ. ( пятьдесят пять тысяч рублей ).</span></span></span></b></p>\r\n\r\n<p><b>&nbsp;</b></p>\r\n\r\n<p><b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\"><span calibri=\"\" style=\"font-family:\">Любая из нижепредставленных программ может пересмотреться согласно Вашему предпочтению и пожеланиям.</span></span></span></i></b></p>\r\n', '/uploads/images/turebi/6%20days%205%20night/mestia%20pat.jpg', '/uploads/images/turebi/batumi344.png,/uploads/images/turebi/batumi4321.jpg,/uploads/images/turebi/batumi65.jpg,/uploads/images/turebi/ushguli54.jpg,/uploads/images/turebi/mestia%20pp.jpg,/uploads/images/turebi/mestia765.jpg,/uploads/images/turebi/svaneti23.jpg,/uploads/images/turebi/mestia.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%BB%D0%BD%D0%B8%D1%81%D0%B8.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%BB%D0%BD%D0%B8%D1%81%D0%B8%20%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B8%2021.jpg,/uploads/images/turebi/Kutaisi.JPG,/uploads/images/turebi/Bagrati_Kutaisi.jpg', '', 0, 0, 0, 1),
(176, '06-дней-05-ночей-1', '06-дней-05-ночей-1', '06-дней-05-ночей-1', '11', NULL, '06 дней / 05 ночей', '06 дней / 05 ночей', '06 дней / 05 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><b>День 1</b>. Прибытие &ndash; Тбилиси</p>\r\n\r\n<p><b>День 2.</b> Тбилиси&nbsp;</p>\r\n\r\n<p><b>День 3.</b> Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи&nbsp; &ndash; Тбилиси</p>\r\n\r\n<p><b>День 4.</b> Тбилиси &ndash; Мцхета - Тбилиси</p>\r\n\r\n<p><b>День 5</b>. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги - Тбилиси</p>\r\n\r\n<p><b>День 6</b>. Отъезд - Тбилиси</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1. Прибытие </b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2. Тбилиси&nbsp; </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Пешая экскурсия по Тбилиси. <span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; <b><i>ВОЛШЕБНЫЙ БЕНЕФИС</i></b> &raquo;</span></span></span></p>\r\n\r\n<p><b>Продолжительность тура : 3 &ndash; 5 ч<i>.</i></b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p>Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i><span style=\"color:#7030a0\">( подъем на канатке с парка Рике к Нарикале )</span></i></b></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B823334.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3. Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи&nbsp; &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию &laquo; <b><i>&nbsp;</i></b>&raquo;</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают <img alt=\"\" src=\"/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</p>\r\n\r\n<p>Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ. <b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"color:#7030a0\">Дегустация вин на винном заводе &laquo; Шуми &raquo;.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4. Тбилиси &ndash; Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в бывшую столицу Грузии &ndash; Мцхета. <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span style=\"color:#002060\">&laquo; КОЛЫБЕЛЬ ГРУЗИНСКОЙ ГОСУДАРСТВЕННОСТИ &raquo;</span></span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 4 &ndash; 5 ч.</b></p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/sveticxoveli7.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ПУТИ К ВЕЛИКАНУ</span></span></i></b> &raquo;.</p>\r\n\r\n<p><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и <img alt=\"\" src=\"/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B843.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i>.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6. Отъезд </b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8%20pat.jpg', '/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8%20324.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B845.jpg,/uploads/images/turebi/mcxeta6556.jpg,/uploads/images/turebi/mcxeta4534.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/bodbis%20monasteri2.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B821.jpg,/uploads/images/turebi/%D0%90%D0%B1%D0%B0%D0%BD%D0%BE%D1%82%D1%83%D0%B1%D0%B0%D0%BD%D0%B8.jpg,/uploads/images/turebi/ananuri232.jpg,/uploads/images/turebi/gudauri3.jpg,/uploads/images/turebi/yazbegi5.jpg', '', 0, 0, 0, 1);
INSERT INTO `tours` (`id`, `slug_ge`, `slug_en`, `slug_ru`, `category`, `lector`, `title_ge`, `title_en`, `title_ru`, `descr_ge`, `descr_en`, `descr_ru`, `text_ge`, `text_en`, `text_ru`, `image`, `image_paths`, `sale`, `favorite`, `active_ge`, `active_en`, `active_ru`) VALUES
(177, '06-дней-05-ночей-1-2', '06-дней-05-ночей-1-2', '06-дней-05-ночей-1-2', '11', NULL, '06 дней / 05 ночей', '06 дней / 05 ночей', '06 дней / 05 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\"><b><span lang=\"RU\" style=\"font-size:16.0pt\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></p>\r\n\r\n<p><strong>День 1. Прибытие &ndash; Тбилиси</strong></p>\r\n\r\n<p><strong>День 2. Тбилиси / Мцхета &ndash; Тбилиси</strong></p>\r\n\r\n<p><strong>День 3. Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси</strong></p>\r\n\r\n<p><strong>День 4. Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</strong></p>\r\n\r\n<p><strong>День 5. Тбилиси &ndash; Дманиси &ndash; Болниси&nbsp; &ndash; Тбилиси</strong></p>\r\n\r\n<p><strong>День 6. Отъезд &ndash; Тбилиси</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 1.</b> <b>Прибытие &ndash; Тбилиси</b></p>\r\n\r\n<p>Встреча гостей в аэропорту и трансфер в отель выбранной категории.</p>\r\n\r\n<p>Размещение в отеле.</p>\r\n\r\n<p>Ночевка в Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 2.</b> <b>Тбилиси / Мцхета &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Тбилиси ( пешая ) и в город Мцхета&nbsp;&nbsp; <b><i><span lang=\"RU\" style=\"font-size:14.0pt\"><span style=\"line-height:115%\">&laquo; По двум столицам &raquo;</span></span></i></b></p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>&nbsp;Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</p>\r\n\r\n<p>Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" />Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" style=\"font-family:\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</p>\r\n\r\n<p>Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i><span style=\"color:#7030a0\">( подъем на канатке с парка Рике к Нарикале )</span></i></b></p>\r\n\r\n<p>Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</p>\r\n\r\n<p>Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</p>\r\n\r\n<p>Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</p>\r\n\r\n<p>Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</p>\r\n\r\n<p>&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/mcxeta4534.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</p>\r\n\r\n<p>Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</p>\r\n\r\n<p>Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</p>\r\n\r\n<p>Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</p>\r\n\r\n<p>Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</p>\r\n\r\n<p>Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</p>\r\n\r\n<p>После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</p>\r\n\r\n<p>Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 3.</b> <b>Тбилиси &ndash; Телави &ndash; Цинандали &ndash; Бодбийский монастырь - Сигнахи &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Кахетию &laquo; <b><i>&nbsp;</i></b>&raquo;</p>\r\n\r\n<p><b>Продолжительность тура : 7 &ndash; 9 ч.</b></p>\r\n\r\n<p>Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</p>\r\n\r\n<p><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</p>\r\n\r\n<p>Наше путешествие начинается с <b>Гомборского перевала</b> &ndash; 35 км умопомрачительного пейзажа и гора Циви ( 2000 м над уровнем моря ). Природа меняется по мере подъема и последующего спуска &ndash; вокруг и лиственные деревья, и лес, и долины, и альпийские зоны, ясное солнечное небо сменяет облачность, а облака, как будто нарисованные.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/turebi/%D0%A2%D0%B5%D0%BB%D0%B0%D0%B2%D0%B81.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: left;\" />Город<b> Телави</b> &ndash; сердце Кахетии, центр всего грузинского виноделия, которое возведено здесь в культ. Телави служил важным торговым центром на караванном пути из Ближнего Востока в Европу. История города тесно связана с историей самой Кахетии. С нашим гидом Вы сможете прогуляться по центру города, увидеть Батонис Цихе ( Крепость Господина ), где расположен дворец царя Ираклия II, насладиться зеленью речных долин и склонами гор Большого Кавказа.</p>\r\n\r\n<p>От Телави в 10 км расположена деревня <b>Цинандали</b>, в которой находится родовое поместье поэта, общественного деятеля и князя Кахетии XIX в. Александра Чавчавадзе ( 1786 &ndash; 1846 ) &ndash; сына первого посла Грузии в России Гарсевана Чавчавадзе, крестника Екатерины Великой и тестя А. С. Грибоедова. У Вас будет возможность посетить дом &ndash; музей князя и&nbsp; прогуляться по саду грузинской аристократии XIX в., которая имела утонченный европейский вкус.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</p>\r\n\r\n<p>С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</p>\r\n\r\n<p>Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</p>\r\n\r\n<p>Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</p>\r\n\r\n<p>В городе находится <b>музей Нико Пиросмани</b>, где выставлены 16 его работ. <b><i><span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></p>\r\n\r\n<p>С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</p>\r\n\r\n<p>Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b><span lang=\"RU\" style=\"color:#7030a0\">Дегустация вин на винном заводе &laquo; Шуми &raquo;.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 4.</b> <b>Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия по Военно &ndash; грузинской дороге&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ПУТИ К ВЕЛИКАНУ</span></span></i></b> &raquo;.</p>\r\n\r\n<p><b><i>Продолжительность тура : 6 &ndash; 8 ч.</i></b></p>\r\n\r\n<p>Путешествие начнем по знаменитой Военно &ndash; Грузинской дороге ( 175 км ). Здесь проходит главный Кавказский хребет. Впереди Вас ждет живописная природа и ландшафт.</p>\r\n\r\n<p><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</p>\r\n\r\n<p>В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</p>\r\n\r\n<p>На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</p>\r\n\r\n<p>Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</p>\r\n\r\n<p>Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</p>\r\n\r\n<p>Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске<b><span style=\"color:#002060\">. <i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i>.</span></b></p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 5. Тбилиси &ndash; Дманиси &ndash; Болниси&nbsp; &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Экскурсия в Квемо ( Нижняя ) Картли&nbsp;&nbsp; &laquo; <b><i><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">НА ГЛУБИНЕ ИСТОРИИ</span></span></i></b> &raquo;.<img alt=\"\" src=\"/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B823.jpg\" style=\"margin: 12px; width: 300px; height: 146px; float: right;\" /></p>\r\n\r\n<p><b><span lang=\"RU\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\">Продолжительность тура : 6 - 8 ч.</span></span></b></p>\r\n\r\n<p>Невозможно представить&nbsp; Средневековую Грузию без города &ndash; крепости<b> Дманиси</b>. Здесь в те далекие времена проходила бойкая торговля самым разнообразным товаром. Здесь проживало много ремесленников, которые на заказ могли изготовить красивые кувшины или сшить прочные сапоги из кожи. Здесь можно было легко обменять один товар на другой. Дманиси был перевалочным пунктом на Великом Шелковом Пути. Но не только торговлей занимался Дманиси. Эта была еще и очень хорошо укрепленная крепость, которая помогала защищать остальную территорию Грузии. Общая территория крепости когда &ndash; то составляла 25 Га. Во время раскопок на территории города &ndash; крепости Дманиси нашли очень интересные артефакты и человеческие кости, которые теперь ставят под вопрос саму теорию эволюции человочества. Именно здесь 1,85 млн. лет назад человек прямоходящий ( Homo erectus, Homo georgicus ) уже умел создавать характерные для него инструменты. &laquo; Первых европейцев &raquo; назвали <b>Зезва и Мзия</b>. Их восстановленные образы и археологические находки Вы сможете увидеть в музее, который находится в археологической зоне дманисского городища.</p>\r\n\r\n<p>На территории городища есть два интересных религиозно &ndash; исторических памятника: остатки крепостных сооружений и храм <b>Дманисский Сион </b>( VII в. ).</p>\r\n\r\n<p>Наше путешествие продолжается в живописное местечко Болниси, где на высоте 620 метров над уровнем моря находится памятник грузинской архитектуры Золотого Века &ndash; <b>монастырь Цугругашени</b> ( XIII в. ). Цугругашени далек от мирской жизни, место - тихое и уединенное и человек остается наедине с собой и своими мыслями.</p>\r\n\r\n<p>С нашим гидом Вы посетите древнюю базилику <b>Болнисский Сион</b> ( 478 &ndash; 493 гг. ), которая до наших дней дошла практически без изменений. Болнисский Сион особенно интересен надписями V века на стенах храма. Эти&nbsp; надписи являются древнейшими образцами грузинской письменности.</p>\r\n\r\n<p>Стены базилики хранят тысячи молитв, и храм до сих пор продолжает служить людям. Посещение таких мест обогащает людей не только новыми знаниями и впечатлениями, но и дарует духовную пищу.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ночь в отеле Тбилиси.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><b>День 6.</b> <b>Отъезд &ndash; Тбилиси</b></p>\r\n\r\n<p>Завтрак в отеле.</p>\r\n\r\n<p>Освобождение номеров до 12:00.</p>\r\n\r\n<p>Трансфер в аэропорт</p>\r\n', '/uploads/images/turebi/6%20days%205%20night/%D1%85%D1%80%D0%B0%D0%BC%20%D0%9C%D0%B5%D1%82%D0%B5%D1%85%D0%B8%20pat.jpg', '/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B845.jpg,/uploads/images/turebi/yazbegi12.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D1%8B%D1%80%D1%8C1.jpg,/uploads/images/turebi/tbilisi5464.jpg,/uploads/images/turebi/tbilisi654433.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B82.jpg,/uploads/images/turebi/%D0%A6%D0%B8%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0%D0%BB%D0%B8.jpeg,/uploads/images/turebi/mcxeta4534.jpg,/uploads/images/turebi/signagi45r3.jpg,/uploads/images/turebi/%D0%91%D0%BE%D0%BB%D0%BD%D0%B8%D1%81%D0%B8%20%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B8%2021.jpg,/uploads/images/turebi/%D0%94%D0%BC%D0%B0%D0%BD%D0%B8%D1%81%D0%B8.jpg,/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B843.jpg', '', 0, 0, 0, 1),
(178, '06-дней-05-ночей-1-2-3', '06-дней-05-ночей-1-2-3', '06-дней-05-ночей-1-2-3', '11', NULL, '06 дней / 05 ночей', '06 дней / 05 ночей', '06 дней / 05 ночей', '', '', '', '', '', '<p align=\"center\" style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:18px;\"><b><span lang=\"RU\"><span style=\"line-height:115%\">Короткое описание программы :</span></span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 1. &nbsp;</b>Прибытие &ndash; Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 2. </b>Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 3. </b>Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 4. </b>Тбилиси &ndash; <a href=\"http://georgianfiesta.compressman.com/page/index/mcxeta?lang=ru\" target=\"_blank\">Мцхета </a>&ndash; Гори &ndash; Уплисцихе - Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 5. </b>Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги - Тбилиси</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>День 6.&nbsp; </b>Отъезд.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 1. &nbsp;Прибытие </b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Встреча гостей в аэропорту и трансфер в отель выбранной категории.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Размещение в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночевка в Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 2. Тбилиси&nbsp;&nbsp;<img alt=\"\" src=\"/uploads/images/turebi/6%20days%205%20night/%E2%80%93%20%D0%A2%D0%B1%D0%B8%D0%BB%D0%B8%D1%81%D0%B81.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 200px;\" /></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Пешая экскурсия по Тбилиси.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура:&nbsp; 3 &ndash; 5 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наши гиды ознакомят Вас с историей столицы Грузии <b>- Тбилиси</b>. История этого удивительного города тянется со времен седой древности. А именно с V века, когда грузинский царь Вахтанг Горгасали приказал основать город в центре плодородной долины, уютно лежащей меж двух кавказских хребтов.&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Сегодня облик города гармонично соединяет древность и современные черты: рядом с храмами VI &ndash; VII вв. выросли современные высотные здания, появились широкие улицы, парки, сады, скверы, через полноводную Куру перекинулись&nbsp; величественные мосты. Но душа настоящего Тбилиси по- прежнему обитает в <b>Старом городе</b>. Посетив этот чарующий уголок,&nbsp; Вы навсегда увезете кусочек Тбилиси в своем сердце...</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Вы посетите<b>&nbsp; храм Метехи</b> ( Успенская церковь<span lang=\"RU\" sylfaen=\"\">,</span> XIII век ), живописно поставленный на гребень скалы. Метехская скала &ndash; это целый комплекс памятников и истории Грузии. Трудно найти в городе другое место, вокруг которого происходило бы столько значимых событий.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Историю Грузии и самого Тбилиси просто невозможно представить себе без <b>крепости Нарикала</b> ( IV век ). Это городская крепость Тбилиси, вроде кремля или цитадели, в которой никогда не было главных храмов или дворцов, чем и отличалась от других &laquo; кремлей &raquo;. <b><i><span style=\"color:#7030a0\">( подъем на канатке с парка Рике к Нарикале )</span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Прогулка по району серных бань <b>&ndash; Абанотубани</b>, где предположительно начинался город Тбилиси. Увидете самую красивую Орбелианскую баню или, как многие ее называют <b>-&nbsp; Голубая баня</b>. О серных банях с восторгом отзывались многие выдающиеся писатели и поэты. А. С. Пушкин писал : &laquo; От роду не встречал ни в России,ни в Турции ничего роскошнее тифлисских бань&raquo; ( &laquo; Путешествие в Арзрум &raquo;, 1829 г. ). Через квартал протекает река, ведущая до симпатичного водопада по ущелью <b>Легвтахеви</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Инжировое ущелье ).</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ознакомитесь со старым городом и знаменитыми улицами Шардена и К. Абхази, на которых Вам встретятся караван &ndash; сараи (<b> Карвасла</b> XVII вв.), колоритные, так называемые, &laquo; Итальянские дворики &raquo; с резными деревянными верандами, рынок&nbsp; ковров ручной работы, башню с часами, построенную Резо Габриадзе<b>, мост Мира </b>- удивительное современное строение, соединяющее старый город с парком Рике<b>, храм Сиони</b> ( Успения Божьей Матери, бывший кафедральный собор, XII в. ), в котором хранятся такие святые реликвии, как крест святой Нино и череп Фомы Неверующего.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Посетите древнейшее церковное строение в Тбилиси &ndash; <b>Анчисхати</b>, базилику VI века, название которой связано с иконой Спаса Нерукотворного ( полотно с отпечатком лица Спасителя ) &ndash; одно из лучших творений грузинских ювелиров.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Посетите район Авлабари &ndash; старый район в самом сердце столицы Грузии, где возвышается гора Элиа или гора Ильи. А на самой горе возвышается величественное и красивое здание &ndash; <b>Цминда Самеба</b> или <b>&nbsp;кафедральный собор Святой Троицы</b> ( высота 101 метр, 1995-2002 гг.), строительство которого было признано строительством тысячелетия, посвященное 2000 - летию рождества Христова и 1500 -летию автокефалии грузинской церкви. Комплекс включает в себя не только сам собор, но и колокольню, резиденцию Католикоса, монастырь, семинарию, академию. А еще 9 часовен, 5 из которых расположены под землей на глубине 13 метров.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в отеле Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 3. Тбилиси &ndash; Бодбийский монастырь &ndash; Сигнахи &ndash; Тбилиси</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Экскурсия во Внешнюю Кахетию.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура : 5 &ndash; 7 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Кахетинский регион &ndash; самый большой регион в Восточной Грузии. Он богат историческими памятниками. Здесь находится более 5000 архитектурных монументов.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Кахетия</b> известна как родина винограда и вина. Здесь выращивают десятки сортов красного и белого винограда, из которых изготавливают прославленные вина. Наш гид откроет Вам секреты технологии приготовления традиционного грузинского вина, которые входят в нематериальное наследие ЮНЕСКО.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><img alt=\"\" src=\"/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%20pat.jpg\" style=\"margin: 12px; width: 290px; height: 295px; float: left;\" />Посетите монастырский и епископальный комплекс Святого Георгия в местечке <b>Бодбе</b> &ndash; это общенациональная грузинская святыня. Здесь покоятся останки равноапостольской Нино Каппадокийской &ndash; великой крестительницы Грузии &ndash; предвестнице христианской эпохи. Здесь находится Чудотворная икона Иверийской Божьей Матери.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">С монастыря открывается чарующий вид на Алазанскую долину и заснеженные величественные вершины Кавказских гор.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Из монастыря направляетесь в <b>город Сигнахи</b>&nbsp; - город любви, как прозвали его посетители, так как там функционирует 24 часа Дворец Бракосочетания.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Сигнахи находится на выгодном стратегическом месте, на вершине горы. Город опоясывает крепостная стена длиной в 7.5 км со встроенными 28 башнями и 7 воротами. Отсюда и название города, что в переводе с тюркского значит &laquo; убежище &raquo;. У Вас будет возможность подняться на одну из башен и пройтись по исторической стене.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">В городе находится музей Нико Пиросмани, где выставлены 16 его работ<b><i>. <span style=\"color:#002060\">( посещение музея за дополнительную плату )</span></i></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">С города также открывается необычайно красивый вид на Алазанскую долину и Кавказские горы.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Прогулка по улочкам Сигнахи не оставит Вас равнодушными.&nbsp; Резные деревянные балкончики, репродукции с картин Пиросмани в виде скульптур, оригинальные скверы и мини &ndash; площади влюбят Вас в себя и в очередной раз утвердят за собой название города &ndash; <b>Города Любви</b>.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b><span lang=\"RU\" style=\"color:#7030a0\">Дегустация вин на винном заводе &laquo; Шуми &raquo;.</span></b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в отеле Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 4.</b> <b>Тбилиси &ndash; Мцхета &ndash; Гори &ndash; Уплисцихе - Тбилиси </b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Поездка в бывшую столицу Грузии &ndash; Мцхета и путешествие в пещерный город Уплисцихе.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура:&nbsp; 7 &ndash; 8 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Древняя столица Грузии ( III в. до н.э. &ndash; V в.н.э. ) &ndash;<b> Мцхета</b> &ndash; святое место для каждого грузина. Такого количества святых и культовых мест, как в Мцхете, расположенной в 20 км от Тбилиси, нет больше во всей Грузии. За это древний город называют &laquo; Вторым Иерусалимом &raquo;. Именно сюда принесла благую весть святая Нино Каппадокийская, здесь хранится одна из величайших святынь христианского мира &ndash; Хитон Господен. Этим же объясняется статус Мцхеты, как святого города, куда стекаются христианские паломники и туристы со всего мира.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Мцхета расположена на месте слияния двух горных рек &ndash; Арагви и Куры, в окружении величественных горных вершин. Панорама с холма, на котором стоит монастырь Джвари, восхитительна. Эта красота навеяла музу и появилась знаменитая поэма&nbsp; М. Ю. Лермонтова &laquo; Мцыри &raquo;.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">&nbsp;В конце XIX &ndash; начале XX вв. древний город Мцхета и окресности были объявлены архитектурно &ndash; историческим музеем &ndash; заповедником и внесены в список Всемирного Наследия ЮНЕСКО.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наше путешествие начинается с монастыря <b>Джвари </b>( Креста ), расположенный на горе у слияния рек Арагвы и Куры. Именно на этом месте Нино Каппадокийская поставила Святой крест,&nbsp; ознаменовавший принятие Грузией христианства. Позднее, а именно в VI веке,&nbsp; над крестом был сооружен храм, который назвали Храмом Святого Креста.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Здесь Вы ознакомитесь с историей языческой Грузии и ее главным божеством того времени - Армази.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Мцхетский Джвари &ndash; гениальное творение грузинского культового зодчества, исполненное простоты, спокойствия, строгости и гармонии.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Вы посетите один из главных православных грузинских храмов &ndash; <b>кафедральный собор Светицховели</b> ( XI в. ), выстроенный на месте первой в Грузии христианской церкви Двенадцати Апостолов, сооруженной в&nbsp; IV веке. Здесь Вы узнаете историю перенесения сюда Хитона Господня. Наш гид расскажет Вам о самых ярких моментах жития святой Нино и о главном событии &ndash; построении собора Светицховели &ndash; Столпа Животворящего.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Собор Светицховели отличается величественной и гармоничной архитектурой. Внутренние стены расписаны фресками XV &ndash; XVII вв. Храм является усыпальницей царского рода Багратиони и Мухранских. Перед алтарем находятся захоронения грузинских царей, в том числе Вахтанга Горгасали &ndash; основателя Тбилиси, Ираклия II &ndash; царя Кахетии, последнего царя Грузии &ndash; Георгия XII.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Еще один выдающийся памятник&nbsp; XI века, неподалеку от Светицховели, &ndash; <b>монастырь Самтавро</b>, связанный с именем равноапостольной Нино. Именно здесь, в ежевичнике, поселилась&nbsp; Святая Нино, в кустах которого она соорудила шалаш и проповедовала людям Христову истину и исцеляла больных.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">После смерти в Самтавро нашли покой&nbsp;&nbsp; царь Мириан и святая царица Нана, при которых было принято христианство в Грузии. Церковь стала усыпальницей царского рода и предстоятелей Церкви. С этим связано происхождение ее названия: &laquo; самтавро &raquo; в переводе означает &laquo; царская территория &raquo;.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Святыни храма: Чудотворная икона Святой Нино с мощами 21 святых &ndash; пожертвование внуков царя Георгия XII &ndash; го, часть камня из Бодбе с могилы святой Нино, мощи одного из тринадцати отцов сирийских и другие реликвии.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Путешествие продолжим в город<b> Гори</b> расположенный на слиянии двух рек, Куры и Лиахви. Гори называют сердцем Восточной Грузии. Он является административным центром края Шида Картли ( Внутренней Картли ) и Горийского муниципалитета.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Гори &ndash; один из старейших городов Грузии. Главной достопримечательностью и древним символом города является<b> Горисцихе</b> &ndash; средневековая оборонительная крепость. Ее развалины находятся на холме в центральной части города.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Еще одной достопримечательностью города является <b>Дом &ndash; музей Иосифа Сталина</b>, разделенный на 3 части : собственно музей, дом и вагон.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">В 15 км от города Гори находится пещерный город античной древности &ndash; <b>Уплисцихе </b>( Крепость Владыки ). Уплисцихе &ndash; <img alt=\"\" src=\"/uploads/images/turebi/%D0%A3%D0%BF%D0%BB%D0%B8%D1%81%D1%86%D0%B8%D1%85%D0%B5.jpg\" style=\"margin: 12px; float: right; width: 300px; height: 190px;\" />уникальный памятник грузинской культуры, расположенный на скальном массиве, спускающемуся к левому берегу реки Куры. Этот город один из памятников Грузии эпохи античности, который гармонично вписывается в окружающий его рельеф.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">С нашим гидом у Вас будет возможность повернуть время вспять и погрузиться в историю многовековой давности. Воспроизвести мысленно быт древних предков и ощутить дух тысячелетий. Ведь за свое немалое существование, которое составляет почти 2 тысячи лет, Уплисцихе перенес и величайшие взлеты, и очень обидные падения.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Уплисцихе &ndash; это не просто пещеры. Это эпоха. Это дом для миллионов человек, которые здесь жили. Это огромные залы, выдолбленные в скале руками неведомых нам людей. Наш гид в деталях и красках расскажет множество интересных легенд, обратит Ваше внимание на интересные объекты города, расскажет, чем жил город, как развлекались местные жители. А также насладитесь видами в округе, так как Уплисцихе находится в потрясающем природном месте на южном берегу Куры.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Возвращение в Тбилиси.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночевка в отеле Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 5.</b> <b>Тбилиси &ndash; Ананури &ndash; Гудаури &ndash; Казбеги - Тбилиси </b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Продолжительность тура : 7 &ndash; 8 ч.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\"><b>Военно &ndash; грузинская дорога</b> &ndash; это одна из самых красивых дорог на Кавказе, соединяющая Грузию с Россией. Это настоящий исторический маршрут, уже очень давно ставший популярным у путешественников и туристов. По этой дороге на Кавказ, в поисках музы, путешествовали известные писатели &ndash; Пушкин, Лермонтов, Грибоедов, А. Дюма. Немало стихов и поэм написано под впечатлением от здешних красот!</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">В 40 км от Тбилиси располагается деревня <b>Жинвали</b>, где в обязательном порядке останавливаются все, чтобы запечатлить невероятную красоту Жинвальского водохранилища : огромную чашу бирюзовой воды среди зеленых гор и голубого неба.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">На северном берегу водохранилища возвышается <b>крепость Ананури</b> ( XV в. ) &ndash; один из величайших памятников грузинского зодчества позднего средневековья. Несмотря на то, что на территории страны есть множество архитектурных памятников этой эпохи, второго настолько хорошо сохранившегося памятника той эпохи, нет.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Наш гид расскажет Вам историю этой крепости и о событиях, связанные с ней и ее жителями.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Вы сможете посетить <b>горно &ndash; лыжный курорт Гудаури</b> на высоте 2000 м над уровнем моря, проехать Крестовый перевал ( 2395 м ) и насладиться невероятными видами могучих гор.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Последняя остановка <b>в Степанцминда</b> ( <b>Казбеги</b>, 1700 м ) &ndash; административный центр региона, главной достопримечательностью которого является сама гора Казбек ( &laquo; Мкинварцвери, 5047 м &raquo; ) и храм в селе Гергети &ndash;<b> Гергетская церковь Святой Троицы</b> ( Гергетис Цминда Самеба, XIV в., 2170 м над уровнем моря ) &ndash; единственный храм такого типа в Казбегском районе. Здесь хранится чудотворная икона Богородицы, чудом уцелевшая после пожара церкви в Смоленске. <span style=\"color:#215868\">. <b><i>( подъем на внедорожнике к храму Гергетской Святой Троице за дополнительную плату )</i></b>.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Ночь в отеле Тбилиси.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><b>День 6.&nbsp; Отъезд.</b></span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Завтрак в отеле.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Освобождение номеров до 12:00.</span></p>\r\n\r\n<p><span style=\"font-size:14px;\">Трансфер в аэропорт.</span></p>\r\n', '/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%20%20pat.jpg', '/uploads/images/turebi/%D0%91%D0%BE%D0%B4%D0%B1%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B9%2032.jpg,/uploads/images/turebi/yazbegi5.jpg,/uploads/images/turebi/yazbegi3.jpg,/uploads/images/turebi/%D0%A1%D0%B8%D0%B3%D0%BD%D0%B0%D1%85%D0%B8.jpg,/uploads/images/turebi/%D0%A3%D0%BF%D0%BB%D0%B8%D1%81%D1%86%D0%B8%D1%85%D0%B5.jpg,/uploads/images/turebi/upliscixe.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%2012.jpg,/uploads/images/turebi/%D0%9A%D0%B0%D0%B7%D0%B1%D0%B5%D0%B3%D0%B845.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B82.jpg,/uploads/images/turebi/ananuri324.jpg,/uploads/images/turebi/%D0%93%D1%83%D0%B4%D0%B0%D1%83%D1%80%D0%B8.jpg,/uploads/images/turebi/tbilisi5464.jpg,/uploads/images/turebi/gudauri4.jpg,/uploads/images/turebi/%D0%90%D0%BD%D0%B0%D0%BD%D1%83%D1%80%D0%B8%20%203.jpg', 'от 200 лари', 0, 0, 0, 1);

